  `cauchuyen_tacgia` varchar(100) NOT NULL,
  `cauchuyen_noidung` text NOT NULL,
  `cauchuyen_ngay` date NOT NULL,
  `cauchuyen_trangthai` tinyint(4) NOT NULL,
  `cauchuyen_hot` tinyint(4) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `cauchuyen`
--

INSERT INTO `cauchuyen` (`cauchuyen_id`, `cauchuyen_tieude`, `cauchuyen_tacgia`, `cauchuyen_noidung`, `cauchuyen_ngay`, `cauchuyen_trangthai`, `cauchuyen_hot`) VALUES
(43, 'Phượt và những bức ảnh khơi nguồn cảm hứng', 'Nguyễn Văn A', '<p style=\"text-align: justify;\">Nhắc đến Phượt, chắc hẳn c&aacute;c bạn trẻ kh&ocirc;ng thể n&agrave;o kh&ocirc;ng biết đến những anh ch&agrave;ng phượt thủ đời đầu nổi tiếng. Miền Bắc c&oacute; anh Ng&ocirc; Huy H&ograve;a (biệt danh Hachi8), anh Nguyễn Ch&iacute; Nam (biệt danh Nam Chấy), anh Trần Việt Anh&hellip;. Miền Nam c&oacute; anh Ng&ocirc; Trần Hải An (biệt danh Quỷ Cốc Tử) hay anh Phạm Nguyễn Kh&aacute;nh Bằng&hellip;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img title=\"Phượt thủ nổi tiếng Ng&ocirc; Huy H&ograve;a\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-2.jpg\" alt=\"Phượt thủ nổi tiếng Ng&ocirc; Huy H&ograve;a\" width=\"700\" data-index=\"1\" /></p>\r\n<p style=\"text-align: justify;\"><em>Phượt thủ nổi tiếng Ng&ocirc; Huy H&ograve;a (biệt danh Hachi8) - Ảnh: Hachi8</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Họ l&agrave; những con người c&oacute; niềm đam m&ecirc; bất tận cho những chuyến đi. &nbsp;Họ l&agrave; t&aacute;c giả của những bức h&igrave;nh xinh đẹp nơi rẻo cao T&acirc;y Bắc đến tận miền đảo xa x&ocirc;i nơi Thổ Chu( Ph&uacute; Quốc), những điểm cực, những ngọn n&uacute;i cao ngất ngưỡng&hellip; những địa danh khi chưa c&oacute; nhiều người chưa đặt ch&acirc;n đến.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img title=\"Anh Ng&ocirc; Huy H&ograve;a l&agrave; t&aacute;c giả của nhiều bức ảnh tạo cảm hứng cho giới trẻ \" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-3.jpg\" alt=\"Anh Ng&ocirc; Huy H&ograve;a l&agrave; t&aacute;c giả của nhiều bức ảnh tạo cảm hứng cho giới trẻ \" width=\"700\" data-index=\"2\" /></p>\r\n<p style=\"text-align: center;\"><em>Anh Ng&ocirc; Huy H&ograve;a l&agrave; t&aacute;c giả của nhiều bức ảnh tạo cảm hứng cho giới trẻ - Ảnh: Hachi8</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Ch&iacute;nh những phong cảnh tuyệt vời bước ra từ nhiều bức h&igrave;nh đầy nghệ thuật đ&atilde; kể cho giới trẻ nghe về sự gi&agrave;u đẹp của Tổ quốc qu&ecirc; hương. Để rồi những người trẻ lại kh&aacute;t khao chinh phục, mong muốn được tận mắt ngắm nh&igrave;n cho kỳ hết vẻ đẹp của non s&ocirc;ng đất Việt. V&agrave; cứ thế, ngọn lửa ấy dần lan tỏa đi khắp con tin giới trẻ, &acirc;m ỉ v&agrave; ch&aacute;y m&atilde;i cho tới tận h&ocirc;m nay.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img title=\"Phượt thủ nổi tiếng Trần Hải An (biệt danh Quỷ Cốc Tử)\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-4.jpg\" alt=\"Phượt thủ nổi tiếng Trần Hải An (biệt danh Quỷ Cốc Tử)\" width=\"700\" data-index=\"3\" /></p>\r\n<p style=\"text-align: justify;\"><em>Phượt thủ nổi tiếng Trần Hải An (biệt danh Quỷ Cốc Tử) - Ảnh: Quỷ Cốc Tử</em></p>', '2019-06-19', 1, 0),
(44, 'Phượt và những chuyến xe chuyên chở yêu thương', 'Trần Văn B', '<p style=\"text-align: justify;\">Hiện nay, đ&atilde; v&agrave; đang c&oacute; nhiều nh&oacute;m phượt ra đời, gom g&oacute;p y&ecirc;u thương chở khắp mọi miền đất nước. Những chuyến xe ấy ng&agrave;y c&agrave;ng nh&acirc;n rộng v&agrave; được giới trẻ nhiệt t&igrave;nh ủng hộ. C&aacute;c nh&oacute;m phượt từ thiện c&oacute; mặt ở cả 3 miền đất nước, hoạt động trong c&aacute;c nh&oacute;m k&iacute;n v&agrave; kh&ocirc;ng qu&aacute; rầm rộ. Họ &acirc;m thầm, lặng lẽ cống hiến, san sẻ t&igrave;nh thương m&agrave; kh&ocirc;ng cần được tung h&ocirc;, ca ngợi, với họ c&oacute; lẽ niềm vui đ&atilde; l&agrave; một m&oacute;n qu&agrave; to lớn, đầy &yacute; nghĩa.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><em><img title=\"MC Nguy&ecirc;n Khang trong một chuyến Phượt từ thiện\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-5.jpg\" alt=\"MC Nguy&ecirc;n Khang trong một chuyến Phượt từ thiện\" width=\"700\" data-index=\"4\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>MC Nguy&ecirc;n Khang trong một chuyến Phượt từ thiện - Photo: Phạm Hưởng</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Những nh&oacute;m từ thiện được nhắc đ&ecirc;n tr&ecirc;n mặt b&aacute;o c&oacute; thể kể đến như Tim Hồng, Sống Hướng Thiện, M&ugrave;a Đ&ocirc;ng Ấm, Phượt H&agrave; Nội, Phượt S&agrave;i G&ograve;n,... b&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; rất nhiều nh&oacute;m k&iacute;n kh&aacute;c vẫn đang chung tay, g&oacute;p sức v&igrave; cộng đồng. C&aacute;c bạn trẻ đ&atilde; kh&ocirc;ng ngại kh&oacute; khăn đi những quảng đường xa x&ocirc;i, nguy hiểm đến tận rẻo cao T&acirc;y Bắc, c&aacute;c hải đảo vắng người để tiếp th&ecirc;m ngọn lửa y&ecirc;u thương nồng ấm.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img title=\"Gom y&ecirc;u thương đi khắp miền Tổ quốc \" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-6.jpg\" alt=\"Gom y&ecirc;u thương đi khắp miền Tổ quốc \" width=\"700\" data-index=\"5\" /></p>\r\n<p style=\"text-align: justify;\"><em>Gom y&ecirc;u thương đi khắp miền Tổ quốc - Ảnh: Hachi8</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Ch&iacute;nh những chuyến đi Phượt kết hợp từ thiện của c&aacute;c bạn trẻ đ&atilde; vun đắp thương y&ecirc;u v&agrave; x&oacute;a nh&ograve;a khoảng c&aacute;ch giữa c&aacute;c v&ugrave;ng miền cả nước. Những chuyến đi ấy c&agrave;ng l&agrave;m ta ngưỡng mộ bởi những h&agrave;nh động cao đẹp, &yacute; nghĩa v&agrave; đầy t&iacute;nh nh&acirc;n văn.</p>', '2019-06-19', 1, 0),
(45, 'PHƯỢT TỪ THIỆN 0 ĐỒNG - THE JAILBREAK 2014', 'Đỗ A', '<p style=\"text-align: justify;\">C&oacute; lẽ những ai quan t&acirc;m sẽ kh&ocirc;ng thể n&agrave;o qu&ecirc;n được những điều kh&ocirc;ng tưởng m&agrave; c&aacute;c bạn trẻ đ&atilde; l&agrave;m được trong cuộc thi The Jailbreak 2014. L&agrave; một dự &aacute;n phi lợi nhuận nhằm g&acirc;y quỹ từ thiện, The Jailbreak (Chinh phục giới hạn) l&agrave; cuộc thi được tổ chứa bởi một nh&oacute;m du học sinh đầy ho&agrave;i b&atilde;o. Chỉ với 0 đồng, bạn c&oacute; thể đi được bao xa trong 36 tiếng đồng hồ?. Mỗi người tham dự phải tự kiếm tiền, đi nhờ xe v&agrave; l&agrave;m mọi c&aacute;ch để đi được c&agrave;ng xa c&agrave;ng tốt.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img title=\"The Jailbreak 2014 ng&agrave;y khởi động\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-7.jpg\" alt=\"The Jailbreak 2014 ng&agrave;y khởi động\" width=\"700\" data-index=\"6\" /></p>\r\n<p style=\"text-align: justify;\"><em>The Jailbreak 2014 ng&agrave;y khởi động - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">Trong cuộc thi đ&oacute;, nhiều nh&oacute;m chơi đ&atilde; thực sự để lại ấn tượng trong l&ograve;ng c&aacute;c bạn trẻ với th&ocirc;ng điệp mạnh mẽ: kh&ocirc;ng g&igrave; l&agrave; kh&ocirc;ng thể. Điển h&igrave;nh l&agrave; nh&oacute;m Unlimited đ&atilde; đi được 2500km từ th&agrave;nh phố Hồ Ch&iacute; Minh đến tận L&agrave;o Cai với số tiền khởi điểm l&agrave; 0 đồng. Họ đ&atilde; phải đi nhờ xe, đi bộ, xin t&agrave;i trợ, b&aacute;n n&oacute;n g&acirc;y quỹ...để đi được một qu&atilde;ng đường xa như vậy.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><em><img title=\"Nepal l&agrave; nơi những bạn trẻ chọn l&agrave;m đ&iacute;ch đến chỉ với 0 đồng trong tay\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-8.jpg\" alt=\"Nepal l&agrave; nơi những bạn trẻ chọn l&agrave;m đ&iacute;ch đến chỉ với 0 đồng trong tay\" width=\"700\" data-index=\"7\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Nepal l&agrave; nơi những bạn trẻ chọn l&agrave;m đ&iacute;ch đến chỉ với 0 đồng trong tay -&nbsp;</em><em>Ảnh:&nbsp;</em><em>chandatmattoet</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Ấn tượng hơn l&agrave; nh&oacute;m Lửa, c&aacute;c th&agrave;nh vi&ecirc;n đ&atilde; đi được sang tận Nepal chỉ với 0 đồng. Mượn &yacute; tưởng của anh Đo&agrave;n Trần Anh Tuấn, c&aacute;c bạn trẻ đ&atilde; tiến h&agrave;nh &ldquo;rao th&acirc;n&rdquo; tr&ecirc;n facebook nhận l&agrave;m việc theo giờ để c&oacute; kinh ph&iacute; sang nước bạn. Nhờ v&agrave;o sở trường v&agrave; t&agrave;i năng của c&aacute;c th&agrave;nh vi&ecirc;n c&ugrave;ng sự ủng hộ nhiệt t&igrave;nh của bạn b&egrave;, nh&oacute;m Lửa sau 4 ng&agrave;y thực hiện chiến dịch đ&atilde; c&oacute; trong tay 1600 USD l&agrave;m lộ ph&iacute;.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img title=\"Nepal - đất nước mang đậm đ&agrave; bản sắc\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-9.jpg\" alt=\"Nepal - đất nước mang đậm đ&agrave; bản sắc\" width=\"700\" data-index=\"8\" /></p>\r\n<p style=\"text-align: justify;\"><em>Nepal - đất nước mang đậm đ&agrave; bản sắc - Ảnh:&nbsp;</em><em>chandatmattoet</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Nh&oacute;m đ&atilde; c&oacute; một chuyến đi rong ruổi đến v&ugrave;ng đất Nepal đậm đ&agrave; bản sắc văn h&oacute;a, quyển s&aacute;ch &ldquo;99 điều kh&ocirc;ng thể kh&ocirc;ng l&agrave;m ở Nepal&rdquo; nhằm g&acirc;y quỹ từ thiện cũng được nh&oacute;m ấp ủ xuất bản sau chuyến đi n&agrave;y.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', '2019-06-19', 1, 0),
(46, 'Phượt và cuộc gặp lại sau 40 năm', 'Triệu H', '<p style=\"text-align: justify;\"><em>Đ&oacute; l&agrave; c&acirc;y chuyện hi hữu của người phượt thủ đ&atilde; gần 70 tuổi m&agrave; người ta quen gọi &ocirc;ng với biệt danh thương mến: Bố Gi&agrave;. Sau khi vợ mất, c&aacute;c con cũng đ&atilde; c&oacute; gia đ&igrave;nh &ocirc;ng t&igrave;m vui tr&ecirc;n chiếc xe gắn m&aacute;y c&ugrave;ng những cung đường khắp mọi miền đất nước. Trong một chuyến phượt Xuy&ecirc;n Việt, &ocirc;ng gặp lại c&ocirc; &Uacute;t ng&agrave;y xưa khi c&oacute; cuộc giao lưu với c&acirc;u lạc bộ xe cổ tại Đ&agrave; Nẵng. C&ocirc; &Uacute;t ch&iacute;nh l&agrave; b&agrave; chủ nh&agrave; h&agrave;ng m&agrave; hai đo&agrave;n gh&eacute; lại ăn trưa.</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em><img title=\"Chọn những cung đường l&agrave;m niềm vui sống\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-10.jpg\" alt=\"Chọn những cung đường l&agrave;m niềm vui sống\" width=\"700\" data-index=\"9\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Chọn những cung đường l&agrave;m niềm vui sống- Ảnh: Hachi8</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em>Ng&agrave;y đ&oacute;, &ocirc;ng v&agrave; c&ocirc; &Uacute;t quen nhau tại chiến trường T&acirc;y Nguy&ecirc;n, thời kh&aacute;ng chiến chia ly, loạn lạc, mỗi dịp gặp mặt họ chỉ kịp trao nhau nụ cười v&agrave; &aacute;nh mắt nhẹ nh&agrave;ng, đầy &yacute; nhị. Sau đ&oacute;, cả hai mất li&ecirc;n lạc trong những cuộc chiến tranh kh&oacute;i lửa, chẳng ai biết tin nhau suốt những năm d&agrave;i đằng đẵng, những tưởng rằng họ sẽ chẳng bao giờ gặp lại bạn xưa. Ấy vậy m&agrave; họ đ&atilde; gặp được nhau trong một chuyến phượt sau 40 mất hẳn li&ecirc;n lạc. C&ocirc; &Uacute;t giờ đ&atilde; ngo&agrave;i 60, chồng mất sớm c&ocirc; ở vậy lo cho c&aacute;c con ăn học th&agrave;nh t&agrave;i, đến tận khi con c&oacute; gia đ&igrave;nh ấn &ecirc;m, vi&ecirc;n m&atilde;n.</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em><img title=\"Con đường đỡ ch&ocirc;ng ch&ecirc;nh khi c&oacute; người đồng h&agrave;nh ăn &yacute;\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-11.jpg\" alt=\"Con đường đỡ ch&ocirc;ng ch&ecirc;nh khi c&oacute; người đồng h&agrave;nh ăn &yacute;\" width=\"700\" data-index=\"10\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Con đường đỡ ch&ocirc;ng ch&ecirc;nh khi c&oacute; người đồng h&agrave;nh ăn &yacute; - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em>Từ dịp gặp gỡ, &ocirc;ng thường mời c&ocirc; xuống nh&agrave; chơi v&agrave; gặp mặt c&aacute;c con. Cũng từ dạo ấy những chuyến phượt d&agrave;i cũng đỡ phần ch&ocirc;ng ch&ecirc;nh, c&ocirc; quạnh, chiếc y&ecirc;n sau cũng được lấp dầy bời người bạn t&acirc;m giao nhiều năm xa c&aacute;ch. Họ trở th&agrave;nh một đ&ocirc;i bạn đường ăn &yacute;, do l&agrave; người c&ugrave;ng thời n&ecirc;n họ rất dễ sẻ chia v&agrave; đồng điệu với nhau về t&acirc;m hồn.</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em><img title=\"Bạn cũng c&oacute; thể trải nghiệm cuộc sống bằng những chuyến đi của ri&ecirc;ng m&igrave;nh\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-12.jpg\" alt=\"Bạn cũng c&oacute; thể trải nghiệm cuộc sống bằng những chuyến đi của ri&ecirc;ng m&igrave;nh\" width=\"700\" data-index=\"11\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Khi n&agrave;o đ&ocirc;i ch&acirc;n c&ograve;n kh&aacute;t khao đi v&agrave; con tim vẫn c&ograve;n đam m&ecirc; x&ecirc; dịch th&igrave; khi ấy những c&acirc;u chuyện tr&ecirc;n c&aacute;c nẻo đường vẫn c&ograve;n tiếp tục, phượt v&agrave; những c&acirc;u chuyện d&agrave;i khiến ta trầm trồ, ngưỡng mộ v&agrave; suy ngẫm. Những c&acirc;y chuyện nhặt nhạnh ở tr&ecirc;n chỉ l&agrave; những mảnh gh&eacute;p nhỏ b&eacute; trong thế giới đầy sắc m&agrave;u của Phượt. Sẽ c&ograve;n rất nhiều những c&acirc;u chuyện hay đầy th&uacute; vị kh&aacute;c m&agrave; phượt thủ chỉ giữ lại cho ri&ecirc;ng m&igrave;nh. Bạn cũng c&oacute; thể tự kh&aacute;m ph&aacute; bản th&acirc;n, trải nghiệm cuộc sống bằng những chuyến đi của ri&ecirc;ng m&igrave;nh. Tất cả đều xuất ph&aacute;t từ sự lựa chọn của bạn, ngay ch&iacute;nh gi&acirc;y ph&uacute;t n&agrave;y.</em></p>', '2019-06-19', 1, 0),
(47, 'Phượt và cuộc gặp lại sau 40 năm', 'Triệu H', '<p style=\"text-align: justify;\"><em>Đ&oacute; l&agrave; c&acirc;y chuyện hi hữu của người phượt thủ đ&atilde; gần 70 tuổi m&agrave; người ta quen gọi &ocirc;ng với biệt danh thương mến: Bố Gi&agrave;. Sau khi vợ mất, c&aacute;c con cũng đ&atilde; c&oacute; gia đ&igrave;nh &ocirc;ng t&igrave;m vui tr&ecirc;n chiếc xe gắn m&aacute;y c&ugrave;ng những cung đường khắp mọi miền đất nước. Trong một chuyến phượt Xuy&ecirc;n Việt, &ocirc;ng gặp lại c&ocirc; &Uacute;t ng&agrave;y xưa khi c&oacute; cuộc giao lưu với c&acirc;u lạc bộ xe cổ tại Đ&agrave; Nẵng. C&ocirc; &Uacute;t ch&iacute;nh l&agrave; b&agrave; chủ nh&agrave; h&agrave;ng m&agrave; hai đo&agrave;n gh&eacute; lại ăn trưa.</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em><img title=\"Chọn những cung đường l&agrave;m niềm vui sống\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-10.jpg\" alt=\"Chọn những cung đường l&agrave;m niềm vui sống\" width=\"700\" data-index=\"9\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Chọn những cung đường l&agrave;m niềm vui sống- Ảnh: Hachi8</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em>Ng&agrave;y đ&oacute;, &ocirc;ng v&agrave; c&ocirc; &Uacute;t quen nhau tại chiến trường T&acirc;y Nguy&ecirc;n, thời kh&aacute;ng chiến chia ly, loạn lạc, mỗi dịp gặp mặt họ chỉ kịp trao nhau nụ cười v&agrave; &aacute;nh mắt nhẹ nh&agrave;ng, đầy &yacute; nhị. Sau đ&oacute;, cả hai mất li&ecirc;n lạc trong những cuộc chiến tranh kh&oacute;i lửa, chẳng ai biết tin nhau suốt những năm d&agrave;i đằng đẵng, những tưởng rằng họ sẽ chẳng bao giờ gặp lại bạn xưa. Ấy vậy m&agrave; họ đ&atilde; gặp được nhau trong một chuyến phượt sau 40 mất hẳn li&ecirc;n lạc. C&ocirc; &Uacute;t giờ đ&atilde; ngo&agrave;i 60, chồng mất sớm c&ocirc; ở vậy lo cho c&aacute;c con ăn học th&agrave;nh t&agrave;i, đến tận khi con c&oacute; gia đ&igrave;nh ấn &ecirc;m, vi&ecirc;n m&atilde;n.</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em><img title=\"Con đường đỡ ch&ocirc;ng ch&ecirc;nh khi c&oacute; người đồng h&agrave;nh ăn &yacute;\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-11.jpg\" alt=\"Con đường đỡ ch&ocirc;ng ch&ecirc;nh khi c&oacute; người đồng h&agrave;nh ăn &yacute;\" width=\"700\" data-index=\"10\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Con đường đỡ ch&ocirc;ng ch&ecirc;nh khi c&oacute; người đồng h&agrave;nh ăn &yacute; - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em>Từ dịp gặp gỡ, &ocirc;ng thường mời c&ocirc; xuống nh&agrave; chơi v&agrave; gặp mặt c&aacute;c con. Cũng từ dạo ấy những chuyến phượt d&agrave;i cũng đỡ phần ch&ocirc;ng ch&ecirc;nh, c&ocirc; quạnh, chiếc y&ecirc;n sau cũng được lấp dầy bời người bạn t&acirc;m giao nhiều năm xa c&aacute;ch. Họ trở th&agrave;nh một đ&ocirc;i bạn đường ăn &yacute;, do l&agrave; người c&ugrave;ng thời n&ecirc;n họ rất dễ sẻ chia v&agrave; đồng điệu với nhau về t&acirc;m hồn.</em></p>\r\n<p style=\"text-align: justify;\"><em>&nbsp;</em></p>\r\n<p style=\"text-align: justify;\"><em><img title=\"Bạn cũng c&oacute; thể trải nghiệm cuộc sống bằng những chuyến đi của ri&ecirc;ng m&igrave;nh\" src=\"https://mytourcdn.com/upload_images/Image/Location/16_12_2015/9-du-lich-phuot-viet-nam-mytour-12.jpg\" alt=\"Bạn cũng c&oacute; thể trải nghiệm cuộc sống bằng những chuyến đi của ri&ecirc;ng m&igrave;nh\" width=\"700\" data-index=\"11\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Khi n&agrave;o đ&ocirc;i ch&acirc;n c&ograve;n kh&aacute;t khao đi v&agrave; con tim vẫn c&ograve;n đam m&ecirc; x&ecirc; dịch th&igrave; khi ấy những c&acirc;u chuyện tr&ecirc;n c&aacute;c nẻo đường vẫn c&ograve;n tiếp tục, phượt v&agrave; những c&acirc;u chuyện d&agrave;i khiến ta trầm trồ, ngưỡng mộ v&agrave; suy ngẫm. Những c&acirc;y chuyện nhặt nhạnh ở tr&ecirc;n chỉ l&agrave; những mảnh gh&eacute;p nhỏ b&eacute; trong thế giới đầy sắc m&agrave;u của Phượt. Sẽ c&ograve;n rất nhiều những c&acirc;u chuyện hay đầy th&uacute; vị kh&aacute;c m&agrave; phượt thủ chỉ giữ lại cho ri&ecirc;ng m&igrave;nh. Bạn cũng c&oacute; thể tự kh&aacute;m ph&aacute; bản th&acirc;n, trải nghiệm cuộc sống bằng những chuyến đi của ri&ecirc;ng m&igrave;nh. Tất cả đều xuất ph&aacute;t từ sự lựa chọn của bạn, ngay ch&iacute;nh gi&acirc;y ph&uacute;t n&agrave;y.</em></p>', '2019-06-19', 0, 0);

