INSERT INTO `tintuc` (`tintuc_id`, `danhmuc_id`, `tintuc_ten`, `tintuc_mota`, `tintuc_noidung`, `tintuc_anh`, `tintuc_ngaytao`, `tintuc_hot`) VALUES
(40, 30, '50 câu nói bất hủ về du lịch', '<p><strong>Những c&acirc;u n&oacute;i hay lu&ocirc;n l&agrave; niềm cảm hứng bất tận trong cuộc sống v&agrave; với những ai đam m&ecirc; du lịch bụi, m&ecirc; phượt th&igrave; cũng thủ sẵn trong m&igrave;nh v&agrave;i c&acirc;u n&oacute;i như thế.</strong></p>', '<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><a href=\"http://localhost:8080/newddp/wp-content/uploads/2016/07/1-20.jpg\"><img class=\"aligncenter size-full wp-image-698\" src=\"http://localhost:8080/newddp/wp-content/uploads/2016/07/1-20.jpg\" sizes=\"(max-width: 768px) 100vw, 768px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2016/07/1-20.jpg 768w, http://dandiphuot.com/wp-content/uploads/2016/07/1-20-300x200.jpg 300w\" alt=\"cau-noi-ve-du-lich\" width=\"768\" height=\"512\" /></a></p>\r\n<ol>\r\n<li style=\"text-align: justify;\">Đi l&agrave; kẻ th&ugrave; chết người của định kiến, m&ugrave; qu&aacute;ng v&agrave; thiển cận. &ndash; Mark Twain</li>\r\n<li style=\"text-align: justify;\">Một cuộc h&agrave;nh tr&igrave;nh thực sự được t&iacute;nh kh&ocirc;ng phải bằng dặm, m&agrave; bằng những người bạn. &ndash; Tim Cahill</li>\r\n<li style=\"text-align: justify;\">Kh&ocirc;ng c&oacute; mảnh đất n&agrave;o l&agrave; xa lạ. Chỉ c&oacute; kẻ lữ h&agrave;nh l&agrave; người lạ. &ndash;Robert Louis Stevenson</li>\r\n<li style=\"text-align: justify;\">&Iacute;ch lợi của việc đi l&agrave; để điều chỉnh tr&iacute; tưởng tượng với thực tế, v&agrave; thay v&igrave; ngồi h&igrave;nh dung ra mọi chuyện, cứ đi để xem n&oacute; thực sự thế n&agrave;o. &ndash; Samuel Johnson</li>\r\n<li style=\"text-align: justify;\">Những chiếc va li tồi t&agrave;n của ch&uacute;ng t&ocirc;i lại một lần nữa chất đống ngo&agrave;i vỉa h&egrave;; con đường ph&iacute;a trước thậm ch&iacute; c&ograve;n d&agrave;i hơn nữa. Nhưng c&oacute; hề chi, con đường đi ch&iacute;nh l&agrave; cuộc sống. &ndash; Jack Kerouac</li>\r\n<li style=\"text-align: justify;\">Ai kh&ocirc;ng đi th&igrave; sẽ kh&ocirc;ng hiểu được gi&aacute; trị con người. &ndash; Ngạn ngữ Moorish</li>\r\n<li style=\"text-align: justify;\">Con người đi đến những mảnh đất xa x&ocirc;i để nh&igrave;n ngắm một c&aacute;ch say m&ecirc; những kẻ m&agrave; họ thường bỏ qua khi ở nh&agrave;. &ndash; Dagobert D. Runes</li>\r\n<li style=\"text-align: justify;\">H&agrave;nh tr&igrave;nh giống như h&ocirc;n nh&acirc;n vậy. C&aacute;ch chắc chắn nhất để phạm sai lầm l&agrave; tin rằng m&igrave;nh điều khiển n&oacute;. &ndash; John Steinbeck</li>\r\n<li style=\"text-align: justify;\">Kh&ocirc;ng ai nhận ra rằng đi đẹp đến nhường n&agrave;o cho đến khi họ về đến nh&agrave; v&agrave; ngả đầu l&ecirc;n chiếc gối cũ kỹ, th&acirc;n quen. &ndash; Lin Yutang</li>\r\n<li style=\"text-align: justify;\">Đi lu&ocirc;n lu&ocirc;n c&oacute; &iacute;ch. Nếu bạn đến một đất nước tốt đẹp hơn, bạn sẽ học hỏi để cải thiện đất nước m&igrave;nh. Nếu chẳng may bạn đến một đất nước tệ hơn, bạn sẽ học để y&ecirc;u đất nước của ch&iacute;nh m&igrave;nh. &ndash; Samuel Johnson</li>\r\n<li style=\"text-align: justify;\">C&ograve;n t&ocirc;i, t&ocirc;i đi kh&ocirc;ng phải để đến một nơi n&agrave;o cụ thể, m&agrave; chỉ để đi th&ocirc;i. Điều tuyệt vời ch&iacute;nh l&agrave; sự dịch chuyển. &ndash; Rober Louis Stevenson</li>\r\n<li style=\"text-align: justify;\">Kẻ lữ h&agrave;nh nh&igrave;n thấy những g&igrave; họ thấy. Kh&aacute;ch du lịch nh&igrave;n thấy những g&igrave; họ đến để thấy. &ndash; G. K. Chesterton</li>\r\n<li style=\"text-align: justify;\">Đ&iacute;ch đến của ch&uacute;ng ta kh&ocirc;ng phải l&agrave; một v&ugrave;ng đất, m&agrave; l&agrave; một c&aacute;ch nh&igrave;n mới. &ndash; Henry Miller</li>\r\n<li style=\"text-align: justify;\">Kẻ lữ h&agrave;nh m&agrave; kh&ocirc;ng quan s&aacute;t th&igrave; chẳng kh&aacute;c n&agrave;o một con chim m&agrave; kh&ocirc;ng c&oacute; c&aacute;nh. &ndash; Moslih Eddin Saadi</li>\r\n<li style=\"text-align: justify;\">Tỉnh dậy ho&agrave;n to&agrave;n đơn độc trong một thị trấn xa lạ l&agrave; một trong những cảm x&uacute;c tuyệt vời nhất tr&ecirc;n thế giới. &ndash; Freya Stark</li>\r\n<li style=\"text-align: justify;\">Hai mươi năm về sau bạn sẽ hối hận về những g&igrave; bạn kh&ocirc;ng l&agrave;m hơn l&agrave; những g&igrave; bạn l&agrave;m. Vậy n&ecirc;n h&atilde;y th&aacute;o d&acirc;y, nhổ neo ra khỏi bến đỗ an to&agrave;n. H&atilde;y để c&aacute;nh buồn của bạn đ&oacute;n trọn lấy gi&oacute;. Th&aacute;m hiểm. Mơ mộng. Kh&aacute;m ph&aacute;. &ndash; Mark Twain</li>\r\n<li style=\"text-align: justify;\">Đi kh&ocirc;ng chỉ l&agrave; việc nh&igrave;n thấy bằng mắt; n&oacute; l&agrave; một sự thay đổi lu&ocirc;n tiếp diễn, s&acirc;u đậm v&agrave; vĩnh cửu, c&aacute;ch nh&igrave;n nhận cuộc sống. &ndash; Miriam Beard</li>\r\n<li style=\"text-align: justify;\">Mọi h&agrave;nh tr&igrave;nh đều c&oacute; những điểm đến b&iacute; mật m&agrave; ngay cả kẻ lữ h&agrave;nh cũng kh&ocirc;ng thể ngờ tới. &ndash; Martin Buber</li>\r\n<li style=\"text-align: justify;\">Ch&uacute;ng ta sống trong một thế giới tuyệt vời đầy rẫy những vẻ đẹp, quyến rũ v&agrave; phi&ecirc;u lưu. Những chuyến phi&ecirc;u lưu sẽ l&agrave; bất tận, chỉ cần ch&uacute;ng ta t&igrave;m n&oacute; với đ&ocirc;i mắt lu&ocirc;n rộng mở. &ndash; Jawaharial Nehru</li>\r\n<li style=\"text-align: justify;\">Kh&aacute;ch du lịch kh&ocirc;ng biết nơi n&agrave;o họ từng đến, kẻ lữ h&agrave;nh kh&ocirc;ng biết nơi n&agrave;o họ sắp đến. &ndash; Paul Theroux</li>\r\n<li style=\"text-align: justify;\">Trong t&acirc;m tr&iacute; t&ocirc;i, phần thưởng v&agrave; hạnh ph&uacute;c lớn nhất của việc đi l&agrave; ng&agrave;y n&agrave;o ta cũng c&oacute; thể trải nghiệm những thứ như thể l&agrave; lần đầu, để kh&ocirc;ng c&oacute; g&igrave; l&agrave; th&acirc;n thuộc đến mức ta nh&igrave;n nhận n&oacute; như điều hiển nhi&ecirc;n. &ndash; Bill Bryson</li>\r\n<li style=\"text-align: justify;\">Đừng đi theo nơi m&agrave; đường m&ograve;n c&oacute; thể dẫn đến. H&atilde;y đi v&agrave;o nơi kh&ocirc;ng c&oacute; lối m&ograve;n v&agrave; để lại dấu vết. &ndash; Ralph Waldo Emerson</li>\r\n<li style=\"text-align: justify;\">Hai con đường t&aacute;ch nhau đi v&agrave;o trong rừng v&agrave; t&ocirc;i &ndash; t&ocirc;i chọn con đường &iacute;t người đ&atilde; đi. &ndash; Robert Frost</li>\r\n<li style=\"text-align: justify;\">Cuộc h&agrave;nh tr&igrave;nh ng&agrave;n dặm bắt đầu từ một bước ch&acirc;n nhỏ b&eacute;. &ndash; L&atilde;o Tử</li>\r\n<li style=\"text-align: justify;\">Nếu bạn từ chối đồ ăn, bỏ qua phong tục, sợ h&atilde;i t&ocirc;n gi&aacute;o v&agrave; lảng tr&aacute;nh người lạ, tốt hơn l&agrave; bạn n&ecirc;n ở nh&agrave;. &ndash; James Michener</li>\r\n<li style=\"text-align: justify;\">Điều quan trọng kh&ocirc;ng phải l&agrave; sự đến, m&agrave; l&agrave; sự đi. &ndash; T. S. Eilot</li>\r\n<li style=\"text-align: justify;\">Thế giới l&agrave; một cuốn s&aacute;ch, v&agrave; ai kh&ocirc;ng đi chỉ đọc được một trang. &ndash; St. Augustine</li>\r\n<li style=\"text-align: justify;\">Kẻ lữ h&agrave;nh giỏi kh&ocirc;ng c&oacute; lịch tr&igrave;nh cố định, v&agrave; cũng chẳng c&oacute; &yacute; định cập bến. &ndash; L&atilde;o Tử</li>\r\n<li style=\"text-align: justify;\">T&ocirc;i nhận ra rằng để biết rằng m&igrave;nh y&ecirc;u hay gh&eacute;t một người, kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o tốt hơn l&agrave; đi với người đ&oacute;. &ndash; Mark Twain</li>\r\n<li style=\"text-align: justify;\">Một khi bạn đ&atilde; đi, cuộc h&agrave;nh tr&igrave;nh sẽ kh&ocirc;ng bao giờ kết th&uacute;c. N&oacute; sẽ t&aacute;i hiện li&ecirc;n tục trong những g&oacute;c y&ecirc;n tĩnh nhất của t&acirc;m tr&iacute; bạn. T&acirc;m hồn bạn sẽ kh&ocirc;ng bao giờ dứt ra khỏi cuộc h&agrave;nh tr&igrave;nh. &ndash; Pat Conroy</li>\r\n<li style=\"text-align: justify;\">Not all those who wander are lost &ndash; Kh&ocirc;ng phải ai lang thang cũng l&agrave; đi lạc. &ndash; J. R. R. Tolkien</li>\r\n<li style=\"text-align: justify;\">Như tất cả những kẻ lữ h&agrave;nh vĩ đại, t&ocirc;i đ&atilde; thấy nhiều hơn t&ocirc;i c&oacute; thể nhớ, v&agrave; nhớ nhiều hơn t&ocirc;i c&oacute; thể thấy. &ndash; Benjamin Disraeli</li>\r\n<li style=\"text-align: justify;\">&ldquo;Đi&rdquo; t&aacute;i tạo lại sự h&agrave;i ho&agrave; nguy&ecirc;n thuỷ đ&atilde; từng tồn tại giữa con người v&agrave; vũ trụ. &ndash; Anatole France</li>\r\n<li style=\"text-align: justify;\">&ldquo;Đi&rdquo; l&agrave; để ph&aacute;t hiện ra rằng tất cả mọi người đều hiểu sai về những đất nước kh&aacute;c. &ndash; Aldous Huxley</li>\r\n<li style=\"text-align: justify;\">To&agrave;n bộ mục đ&iacute;ch của việc đi kh&ocirc;ng phải l&agrave; để đặt ch&acirc;n l&ecirc;n những mảnh đất xa lạ, m&agrave; l&agrave; để cuối c&ugrave;ng đặt ch&acirc;n l&ecirc;n đất nước của chinh m&igrave;nh như thể một mảnh đất xa lạ. &ndash; G. K. Chesteron</li>\r\n<li style=\"text-align: justify;\">Khi bạn đi, h&atilde;y nhờ rằng một đất nước xa lạ kh&ocirc;ng được thiết kế để cho bạn cảm thấy thoải m&aacute;i. N&oacute; được thiết kế để người d&acirc;n của đất nước đ&oacute; cảm thấy thoải m&aacute;i. &ndash; Clifton Fadiman</li>\r\n<li style=\"text-align: justify;\">Một kẻ lữ h&agrave;nh kh&ocirc;n ngoan kh&ocirc;ng bao giờ ch&ecirc; bai đất nước của ch&iacute;nh m&igrave;nh. &ndash; Carlo Gordoni</li>\r\n<li style=\"text-align: justify;\">Qu&aacute; thường xuy&ecirc;n &hellip; T&ocirc;i nghe người ta khoe khoang về số dặm người ta đi, hơn l&agrave; những g&igrave; người ta thấy. &ndash; Louis L&rsquo;Amour</li>\r\n<li style=\"text-align: justify;\">H&atilde;y th&ocirc;i lo lắng về những ổ g&agrave; tr&ecirc;n đường v&agrave; tận hưởng cuộc h&agrave;nh tr&igrave;nh. &ndash; Fitzhugh Mullan</li>\r\n<li style=\"text-align: justify;\">Chỉ đi với những ai tương đương với bạn hay tốt hơn bạn; nếu kh&ocirc;ng t&igrave;m được, h&atilde;y đi một m&igrave;nh. &ndash; The&nbsp;Dhammapada</li>\r\n<li style=\"text-align: justify;\">Thỉnh thoảng, người ta giật m&igrave;nh khi nhận ra rằng họ kh&ocirc;ng bị buộc phải trải nghiệm thế giới theo c&aacute;ch họ vẫn được bảo. &ndash; Alan Keightley</li>\r\n<li style=\"text-align: justify;\">Một nửa c&aacute;i th&uacute; của việc đi l&agrave; nghệ thuật đi lạc. &ndash; Ray Bradbury</li>\r\n<li style=\"text-align: justify;\">T&ocirc;i muốn d&agrave;nh cả đời m&igrave;nh để đi đến những nơi xa lạ, nếu như t&ocirc;i c&oacute; thể mượn một đời kh&aacute;c ở đ&acirc;u đ&oacute; để sau đ&oacute; sống ở nh&agrave;. &ndash; William Hazlitt</li>\r\n<li style=\"text-align: justify;\">T&ocirc;i th&iacute;ch đi, nhưng t&ocirc;i gh&eacute;t phải đến. &ndash; Albert Einstein</li>\r\n<li style=\"text-align: justify;\">Đừng n&oacute;i với t&ocirc;i bạn học h&agrave;nh thế n&agrave;o, h&atilde;y n&oacute;i với t&ocirc;i bạn đi bao nhi&ecirc;u. &ndash; Mohammed</li>\r\n<li style=\"text-align: justify;\">Ch&uacute;ng ta bắt đầu tha thứ một v&ugrave;ng đất ngay khi ch&uacute;ng ta rời bỏ n&oacute;. &ndash; Charles Dickens</li>\r\n<li style=\"text-align: justify;\">Khi ai đ&oacute; nhận ra rằng cuộc đời của m&igrave;nh l&agrave; v&ocirc; gi&aacute; trị, họ hoặc l&agrave; tự tử, hoặc l&agrave; x&aacute;ch ba l&ocirc; l&ecirc;n v&agrave; đi. &ndash; Edward Dehlberg</li>\r\n<li style=\"text-align: justify;\">T&ocirc;i ho&agrave;n to&agrave;n thay đổi sau khi đ&atilde; nh&igrave;n thấy &aacute;nh trăng chiếu soi ở nửa b&ecirc;n kia thế giới. &ndash; Mary Anne Radmacher Hershey</li>\r\n<li style=\"text-align: justify;\">Chỉ khi n&agrave;o đi một m&igrave;nh trong im lặng, kh&ocirc;ng h&agrave;nh l&yacute;, ta mới c&oacute; thể đi v&agrave;o tr&aacute;i tim của sự hoang d&atilde;. &ndash; John MuirMỗi ng&agrave;y l&agrave; một cuộc h&agrave;nh tr&igrave;nh, v&agrave; cuộc h&agrave;nh tr&igrave;nh bản th&acirc;n n&oacute; ch&iacute;nh l&agrave; nh&agrave;. &ndash; Matsuo Basho</li>\r\n</ol>', '2497484605d086ec436cc55.87602040.jpg', '2019-06-18', 1),
(42, 30, 'Những bài hát hay dành cho dân đi phượt', '<p style=\"text-align: justify;\"><strong>D&acirc;n đi phượt cũng như bao người, cũng biết y&ecirc;u, biết thương, biết gh&eacute;t, biết giận v&agrave; biết thưởng thức &acirc;m nhạc. Trong những năm gần đ&acirc;y c&aacute;c b&agrave;i h&aacute;t viết về mảng du lịch n&oacute;i ri&ecirc;ng v&agrave; phượt n&oacute;i chung bắt đầu được nhiều nhạc sĩ, ca sĩ quan t&acirc;m s&aacute;ng t&aacute;c v&agrave; thể hiện. C&aacute;c b&agrave;i h&aacute;t n&agrave;y gi&aacute;n tiếp truyền th&ecirc;m cảm hứng đối với nhiều bạn trẻ. Dưới đ&acirc;y l&agrave; những b&agrave;i h&aacute;t tiếng Việt d&agrave;nh cho d&acirc;n đi phượt.</strong></p>', '<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img class=\"aligncenter wp-image-44\" src=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/noi-ay-ha-okio.jpg\" sizes=\"(max-width: 600px) 100vw, 600px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2015/11/noi-ay-ha-okio.jpg 1280w, http://dandiphuot.com/wp-content/uploads/2015/11/noi-ay-ha-okio-300x169.jpg 300w, http://dandiphuot.com/wp-content/uploads/2015/11/noi-ay-ha-okio-768x432.jpg 768w, http://dandiphuot.com/wp-content/uploads/2015/11/noi-ay-ha-okio-1024x576.jpg 1024w\" alt=\"noi ay ha okio\" width=\"600\" height=\"338\" /></p>\r\n<p style=\"text-align: justify;\"><strong>Những chuyến đi d&agrave;i &ndash; Bức Tường</strong><br />Những chuyến đi d&agrave;i l&agrave; một s&aacute;ng t&aacute;c của nhạc sĩ Trần Lập (nh&oacute;m Bức Tường) một trong những d&acirc;n phượt ch&iacute;nh hiệu. C&oacute; lẽ l&agrave; một người đ&atilde; từng trải nghiệm những chuyến đi n&ecirc;n ca từ sử dụng trong ca kh&uacute;c rất hợp gu với nhiều anh chị em y&ecirc;u phượt.</p>\r\n<blockquote>\r\n<p style=\"text-align: justify;\"><em>V&igrave; đời l&agrave; những chuyến đi d&agrave;i, những giấc mơ d&agrave;i. Để được sống với đam m&ecirc;, dẫu c&oacute; dại khờ. Luyện &yacute; ch&iacute; vượt qua bao nhi&ecirc;u kh&oacute; khăn. Thử th&aacute;ch tr&ecirc;n những chặng đường. Nổ m&aacute;y l&ecirc;n, c&ugrave;ng bạn b&egrave; ta đam m&ecirc; những cung đường d&agrave;i xu&ocirc;i ngược miền xa lắc lơ, để b&aacute;nh xe cuộc đời lăn. Niềm vui kh&aacute;m ph&aacute;, c&oacute; biết bao nhi&ecirc;u điều khi thi&ecirc;n nhi&ecirc;n đ&atilde;i ưu v&agrave; cuộc sống bốn phương Đường d&agrave;i học những s&agrave;ng kh&ocirc;n.</em></p>\r\n</blockquote>\r\n<p style=\"text-align: justify;\"><strong>Đi về ch&acirc;n trời &ndash; N Tuấn Kiệt ft Ti Luong</strong><br />Với ca từ v&agrave; h&igrave;nh ảnh đẹp, video &ldquo;Đi về ch&acirc;n trời&rdquo; của N Tuấn Kiệt ft Ti Luong đ&atilde; thu h&uacute;t rất nhiều lượt xem tr&ecirc;n c&aacute;c k&ecirc;nh mạng x&atilde; hội. Ca kh&uacute;c l&agrave; những kỷ niệm c&oacute; được từ những chuyến đi, nếu ai đ&atilde; từng vi vu tr&ecirc;n những cung đường chắc chắn sẽ c&oacute; cho m&igrave;nh những đồng cảm.</p>\r\n<p style=\"text-align: justify;\"><strong>Hoa Ban Trắng &ndash; Bức Tường</strong><br />Kh&aacute;c với &ldquo;Những chuyến đi d&agrave;i&rdquo;, Hoa ban trắng l&agrave; một c&acirc;u chuyện được kể lại về t&igrave;nh y&ecirc;u của một đ&ocirc;i trai g&aacute;i người Mường ở T&acirc;y Bắc. Ca từ kh&ocirc;ng c&oacute; li&ecirc;n quan đến d&acirc;n phượt nhưng những h&igrave;nh ảnh trong video mang lại cho nhiều người xem sự th&iacute;ch th&uacute; v&agrave; muốn &ldquo;x&aacute;ch ba l&ocirc; v&agrave; đi&rdquo;.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\"><strong>Nơi ấy &ndash; H&agrave; Okio</strong><br />Nếu b&igrave;nh chọn cho ca kh&uacute;c n&agrave;o hay nhất về đề t&agrave;i du lịch bụi hay phượt th&igrave; &ldquo;Nơi ấy&rdquo; của H&agrave; Okio xứng đ&aacute;ng nằm ở vị tr&iacute; số 1. Giai điện nhẹ nh&agrave;ng, da diết, ca từ mộc mạc lồng v&agrave;o đ&oacute; l&agrave; những h&igrave;nh ảnh rất đổi th&acirc;n thương về những điểm đến m&agrave; c&oacute; lẽ ai cũng đ&atilde; một lần được xem, được thấy.</p>\r\n<blockquote>\r\n<p style=\"text-align: justify;\"><em>những nơi h&ograve; hẹn, những gương mặt quen, v&agrave; ta nhớ ơi l&agrave; nhớ, vui buồn thưở xưa mộng mơ</em></p>\r\n</blockquote>', '5180250505d087237005271.14349564.jpg', '2019-06-18', 0),
(43, 30, 'Dân đi phượt – những kẻ phá hoại', '<p style=\"text-align: justify;\"><strong>Phượt từ l&acirc;u đ&atilde; l&agrave; một từ đ&atilde; qu&aacute; quen thuộc với mọi người đặc biệt l&agrave; giới trẻ. Đối với nhiều người, Phượt kh&ocirc;ng chỉ l&agrave; sở th&iacute;ch hay tr&agrave;o lưu m&agrave; đ&atilde; trở th&agrave;nh một đam m&ecirc; rất m&atilde;nh liệt. Thế nhưng, đằng sau những c&acirc;u chuyện thiện nguyện đầy cảm động của d&acirc;n đi phượt ch&acirc;n ch&iacute;nh l&agrave; một bức tranh d&acirc;n phượt đang dần xấu đi bởi bộ phận kh&ocirc;ng nhỏ những kẻ ph&aacute; hoại đang gắn m&aacute;c &ldquo;d&acirc;n phượt&rdquo; kh&ocirc;ng ngừng &ldquo;vẽ bậy&rdquo; l&ecirc;n ch&uacute;ng.</strong></p>', '<p style=\"text-align: justify;\"><img class=\"aligncenter wp-image-49\" src=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/dan-di-phuot-pha-hoai.jpg\" sizes=\"(max-width: 600px) 100vw, 600px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2015/11/dan-di-phuot-pha-hoai.jpg 957w, http://dandiphuot.com/wp-content/uploads/2015/11/dan-di-phuot-pha-hoai-300x221.jpg 300w, http://dandiphuot.com/wp-content/uploads/2015/11/dan-di-phuot-pha-hoai-768x565.jpg 768w\" alt=\"dan di phuot pha hoai\" width=\"600\" height=\"441\" /></p>\r\n<p style=\"text-align: justify;\"><a href=\"http://dandiphuot.com/phuot-la-gi-dan-phuot-la-ai-8.html\" target=\"_blank\">Phượt l&agrave; g&igrave;?</a>&nbsp;Chỉ cần g&otilde; cụm từ n&agrave;y l&ecirc;n c&ocirc;ng cụ t&igrave;m kiếm Google, bạn sẽ dễ d&agrave;ng nhận được những định nghĩa của từ Phượt: Phượt l&agrave; những chuyến du lịch bụi bặm, phượt l&agrave; kh&aacute;m ph&aacute; v&agrave; chinh phục những v&ugrave;ng đất mới, phượt l&agrave; để t&igrave;m lại ch&iacute;nh m&igrave;nh,&hellip; Với những &yacute; nghĩa thi&ecirc;ng li&ecirc;ng, tuyệt vời l&agrave; thế, nhưng ng&agrave;y nay ngay cả những phượt thủ chuy&ecirc;n nghiệp v&agrave; k&igrave; cựu cũng phải lắc đầu ngao ng&aacute;n thừa nhận rằng phượt đang dần mất &ldquo;chất&rdquo;. Thay v&igrave; phượt với mục đ&iacute;ch kh&aacute;m ph&aacute; v&agrave; học hỏi, họ coi phượt như một tr&agrave;o lưu, đi để khoe, chứng tỏ m&igrave;nh l&agrave; người s&agrave;nh điệu, một tay chơi ch&iacute;nh hiệu m&agrave; đ&acirc;u biết rằng đ&oacute; chỉ l&agrave; một sự sống ảo thực dụng, hu&ecirc;nh hoang v&agrave; khoe mẽ rẻ tiền.</p>\r\n<p style=\"text-align: justify;\"><a href=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/phuot-thu-pha-hoai.jpg\"><img class=\"aligncenter wp-image-48\" src=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/phuot-thu-pha-hoai.jpg\" sizes=\"(max-width: 600px) 100vw, 600px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2015/11/phuot-thu-pha-hoai.jpg 500w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-thu-pha-hoai-300x200.jpg 300w\" alt=\"phuot thu pha hoai\" width=\"600\" height=\"400\" /></a></p>\r\n<p style=\"text-align: justify;\">Với phương ch&acirc;m &ldquo;tuổi trẻ kh&ocirc;ng đi phượt như ph&iacute; mất nửa đời người&rdquo;, nhưng ph&iacute; đ&acirc;u kh&ocirc;ng thấy, d&acirc;n phượt dần đ&oacute;ng vai kẻ ph&aacute; hoại với những h&agrave;nh động v&ocirc; c&ugrave;ng xấu x&iacute;. Để c&oacute; được bức ảnh selfie độc lạ, họ bất chấp, v&ocirc; tư ph&aacute; hoại cảnh quan thi&ecirc;n nhi&ecirc;n, giẫm đạp những c&aacute;nh hoa Tam gi&aacute;c mạch kh&ocirc;ng thương tiếc để lấy lối đi, hay ph&oacute;ng xe m&aacute;y v&agrave; nằm d&agrave;i tr&ecirc;n c&aacute;nh đồng hoa cải trắng Mộc Ch&acirc;u, mặc cho ch&uacute;ng ng&atilde; rạp ở ph&iacute;a sau. Để ghi lại dấu ấn c&aacute; nh&acirc;n kh&ocirc;ng bị phai theo năm th&aacute;ng, họ thẳng tay viết vẽ bậy l&ecirc;n c&aacute;c di t&iacute;ch, địa danh thi&ecirc;ng li&ecirc;ng của tổ quốc như đỉnh Fansipang, cột cờ Lũng C&uacute; với l&iacute; do đơn giản l&agrave; &ldquo;cho vui&rdquo;.</p>\r\n<p style=\"text-align: justify;\"><a href=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/phuot1.jpg\"><img class=\"aligncenter size-full wp-image-50\" src=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/phuot1.jpg\" sizes=\"(max-width: 450px) 100vw, 450px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2015/11/phuot1.jpg 450w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot1-252x300.jpg 252w\" alt=\"phuot\" width=\"450\" height=\"535\" /></a></p>\r\n<p style=\"text-align: justify;\">Kh&ocirc;ng dừng lại ở đ&oacute;, mỗi bước ch&acirc;n của phượt thủ đều ghi dấu với những b&atilde;i r&aacute;c ở ph&iacute;a sau họ. Phượt thủ ăn, ngủ v&agrave; đốt lửa trại v&ocirc; tư m&agrave; kh&ocirc;ng c&oacute; &yacute; thức g&igrave;n giữ bảo vệ m&ocirc;i trường. Với định nghĩa mới về phượt l&agrave; thước đo cho sự dũng cảm v&agrave; tinh thần d&aacute;m nghĩ d&aacute;m l&agrave;m, đ&atilde; v&ocirc; t&igrave;nh khiến phượt trở th&agrave;nh h&agrave;nh tr&igrave;nh nguy hiểm v&agrave; v&ocirc; tổ chức, minh chứng l&agrave; cuối năm 2012, h&igrave;nh ảnh một đo&agrave;n phượt gồm 50 người đ&atilde; bất chấp nguy hiểm của m&igrave;nh v&agrave; người tham gia giao th&ocirc;ng kh&aacute;c khi ngủ qua đ&ecirc;m tại một kh&uacute;c cua tr&ecirc;n đường đ&egrave;o g&acirc;y chấn động cộng đồng hay c&aacute;c tay l&aacute;i phượt th&iacute;ch ph&oacute;ng nhanh bạt mạng, chạy xe như chưa từng được chạy.</p>\r\n<p style=\"text-align: justify;\">Nhưng nguy hiểm hơn cả những h&agrave;nh động bồng bột, ngu ngốc đ&oacute; l&agrave; những &yacute; thức k&eacute;m v&agrave; hời hợt của một bộ d&acirc;n phượt hiện nay, họ d&ugrave;ng những l&yacute; lẽ rẻ tiền để biện minh cho h&agrave;nh động của m&igrave;nh, những suy nghĩ như &ldquo;t&ocirc;i c&oacute; tiền t&ocirc;i phải c&oacute; quyền&rdquo;, l&agrave;m &ldquo;cho vui&rdquo;,&hellip; nếu kh&ocirc;ng được cải thiện th&igrave; sẽ c&oacute; một thế hệ phượt sau ng&agrave;y một tệ hơn, khiến phượt dần mất đi những &yacute; nghĩa thi&ecirc;ng li&ecirc;ng vốn c&oacute;.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\">V&igrave; vậy, đ&atilde; đến l&uacute;c những d&acirc;n phượt ch&acirc;n ch&iacute;nh phải v&agrave;o cuộc ra tay, vực lại niềm tin v&agrave; x&acirc;y dựng lại một cộng đồng phượt thật l&agrave;nh mạnh v&agrave; an to&agrave;n. H&atilde;y để những chuyến thiện nguyện thấm đẫm t&igrave;nh người, những b&agrave;i giảng tại v&ugrave;ng cao, chuyến ph&aacute;t qu&agrave; quy&ecirc;n g&oacute;p đến trẻ em v&ugrave;ng s&acirc;u v&ugrave;ng xa,&hellip; l&agrave;m lu mờ đi những h&igrave;nh ảnh xấu x&iacute; m&agrave; những kẻ ph&aacute; hoại đang g&acirc;y ra.<br />H&atilde;y phượt bằng đam m&ecirc; nhưng l&agrave; đam m&ecirc; c&oacute; hiểu biết!</p>', '20517247275d0873c668d203.98152267.jpg', '2019-06-18', 0);
INSERT INTO `tintuc` (`tintuc_id`, `danhmuc_id`, `tintuc_ten`, `tintuc_mota`, `tintuc_noidung`, `tintuc_anh`, `tintuc_ngaytao`, `tintuc_hot`) VALUES
(44, 27, 'Đâu chỉ có hoa Tam giác mạch, Hà Giang còn có nhiều điều khiến người ta phải si mê.', '<p style=\"text-align: justify;\"><span style=\"color: #808080;\">Ai đ&atilde; một lần l&ecirc;n cao nguy&ecirc;n đ&aacute; H&agrave; Giang, lạc giữa những c&aacute;nh đồng tam gi&aacute;c mạch nở bồng bềnh tr&ocirc;i tới tận m&atilde;i ch&acirc;n trời. Để giữa những khoảnh ch&ecirc;nh v&ecirc;nh tr&ecirc;n sườn n&uacute;i đến những bản l&agrave;ng xa, trong những tầng kh&ocirc;ng m&acirc;y giăng mắc khắp đất trời, người ta chợt ngỡ ng&agrave;ng trước những thảm hoa li ti hồng phớt xen lẫn trắng tinh kh&ocirc;i đang t&ocirc; điểm th&ecirc;m cho m&agrave;u &aacute;o rực rỡ của người thiếu nữ miền cao giữa ng&uacute;t ng&agrave;n xanh thẳm, t&ocirc; điểm cho những cung đường đ&egrave;o ngoạn mục với c&aacute;c kh&uacute;c cua tay &aacute;o khiến những tay l&aacute;i lụa phải ch&ugrave;n tay v&agrave; che đi c&aacute;i gai g&oacute;c của một v&ugrave;ng đất chỉ c&oacute; đ&aacute; v&agrave; đ&aacute; suốt bao đời. Ấy thế nhưng H&agrave; Giang đ&acirc;u chỉ c&oacute; tam gi&aacute;c mạch khiến kẻ lữ h&agrave;nh m&ecirc; mẩn, H&agrave; Giang c&ograve;n bao điều huyền diệu ẩn sau những m&agrave;n sương giăng m&acirc;y phủ khắp n&uacute;i đồi, để người ta phải si m&ecirc; qu&ecirc;n lối, hẹn với l&ograve;ng phải l&ecirc;n H&agrave; Giang lần nữa để thưởng ngoạn trọn vẹn vẻ đẹp miền bi&ecirc;n viễn trong mỗi thời khắc trong năm.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #999999;\">&nbsp;</span></p>', '<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-1.jpg\" alt=\"H&agrave; Giang đ&acirc;u chỉ c&oacute; tam gi&aacute;c mạch khiến kẻ lữ h&agrave;nh m&ecirc; mẩn - Ảnh: Baotranart\" data-index=\"0\" /></p>\r\n<p style=\"text-align: justify;\"><em>H&agrave; Giang đ&acirc;u chỉ c&oacute; tam gi&aacute;c mạch khiến kẻ lữ h&agrave;nh m&ecirc; mẩn - Ảnh: Baotranart</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-2.jpg\" alt=\"H&agrave; Giang c&ograve;n c&oacute; bao điều thi vị m&agrave; đ&ocirc;i khi người ta lỡ bỏ qu&ecirc;n - Ảnh: Hienddang\" data-index=\"1\" /></p>\r\n<p style=\"text-align: center;\"><em>H&agrave; Giang c&ograve;n c&oacute; bao điều thi vị m&agrave; đ&ocirc;i khi người ta lỡ bỏ qu&ecirc;n - Ảnh: Hienddang</em>&nbsp;</p>\r\n<p style=\"text-align: justify;\">H&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i kh&aacute;m ph&aacute; một H&agrave; Giang với bao điều hấp dẫn, m&agrave; c&oacute; lẽ đ&ocirc;i khi người ta chợt bỏ qu&ecirc;n trong chuyến h&agrave;nh tr&igrave;nh về miền cao nguy&ecirc;n đ&aacute;.</p>\r\n<h2 style=\"text-align: justify;\">N&Uacute;I Đ&Ocirc;I C&Ocirc; TI&Ecirc;N &ndash; TUYỆT T&Aacute;C THI&Ecirc;N NHI&Ecirc;N GIỮA ĐẠI NG&Agrave;N&nbsp;</h2>\r\n<p style=\"text-align: justify;\">Nếu đ&atilde; một lần gh&eacute; cao nguy&ecirc;n đ&aacute; Đồng Văn, hẳn bạn sẽ ngỡ ng&agrave;ng trước vẻ đẹp đầy thi vị ở n&uacute;i C&ocirc; Ti&ecirc;n, nơi khiến người ta phải thốt l&ecirc;n rằng tạo h&oacute;a thực qu&aacute; hữu t&igrave;nh khi tạc n&ecirc;n một cảnh quan tuyệt mỹ giữa n&uacute;i rừng h&ugrave;ng vỹ. Nh&igrave;n từ xa, n&uacute;i C&ocirc; Ti&ecirc;n tr&ocirc;ng như một bộ ngực căng tr&ograve;n của người thiếu nữ đ&ocirc;i mươi, ẩn m&igrave;nh giữa một thung lũng nhỏ b&ecirc;n phố n&uacute;i Tam Sơn dung dị, bao quanh l&agrave; những thửa ruộng bậc thang trập tr&ugrave;ng uốn lượn, l&agrave; những nếp nh&agrave; s&agrave;n xinh xinh n&eacute;p m&igrave;nh b&igrave;nh y&ecirc;n dưới rặng c&acirc;y cao. Tất cả như những n&eacute;t chắp b&uacute;t đầy thơ của tạo h&oacute;a nhưng cũng đủ khiến kẻ kh&aacute;ch phải say m&ecirc; trong những niềm x&uacute;c cảm kh&ocirc;ng lời.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-3.jpg\" alt=\"Tạo h&oacute;a thực qu&aacute; hữu t&igrave;nh khi t&aacute;c n&ecirc;n một tuyệt t&aacute;c thi&ecirc;n nhi&ecirc;n giữa H&agrave; Giang - Ảnh: Sưu tầm\" data-index=\"2\" /></p>\r\n<p style=\"text-align: center;\"><em>Tạo h&oacute;a thực qu&aacute; hữu t&igrave;nh khi t&aacute;c n&ecirc;n một tuyệt t&aacute;c thi&ecirc;n nhi&ecirc;n giữa H&agrave; Giang - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-4.jpg\" alt=\"N&uacute;i đ&ocirc;i C&ocirc; Ti&ecirc;n tr&ocirc;ng xa như bộ ngực căng tr&ograve;n của người con g&aacute;i đương th&igrave; - Ảnh: Quanshotgun911\" data-index=\"3\" /></p>\r\n<p style=\"text-align: center;\"><em>N&uacute;i đ&ocirc;i C&ocirc; Ti&ecirc;n tr&ocirc;ng xa như bộ ngực căng tr&ograve;n của người con g&aacute;i đương th&igrave; - Ảnh: Quanshotgun911</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-5.jpg\" alt=\"C&ugrave;ng cảnh sắc Tam Sơn vẽ n&ecirc;n một bức tranh đẹp như mơ - Ảnh: Sưu tầm\" data-index=\"4\" /></p>\r\n<p style=\"text-align: center;\"><em>C&ugrave;ng cảnh sắc Tam Sơn vẽ n&ecirc;n một bức tranh đẹp như mơ - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">V&agrave; trong bức họa thi&ecirc;n nhi&ecirc;n thơ t&igrave;nh ấy, ta bắt gặp những c&aacute;nh đồng l&uacute;a lặng y&ecirc;n dưới ch&acirc;n n&uacute;i xa xăm đang đổi m&agrave;u thay &aacute;o suốt bồn m&ugrave;a, c&oacute; sắc xanh mơn mởn của l&uacute;a buổi đương th&igrave;, c&oacute; sắc v&agrave;ng ươm s&oacute;ng s&aacute;nh trong những ng&agrave;y thu, v&agrave; rồi những buổi m&ugrave;a h&egrave; khi bắt đầu cho m&ugrave;a vụ mới, ta lại thấy sắc n&acirc;u của đất lấp lo&aacute;ng trong m&agrave;u nước đổ ải y&ecirc;n b&igrave;nh. Những sự biến h&oacute;a diệu kỳ theo nhịp thời gian đưa đấy, để mỗi khi về H&agrave; Giang d&ugrave; m&ugrave;a n&agrave;o đi nữa, người ta cũng kh&ocirc;ng qu&ecirc;n gh&eacute; n&uacute;i C&ocirc; Ti&ecirc;n t&igrave;m kiếm những ngọt ng&agrave;o, trong trẻo trong nhịp đập hữu t&igrave;nh của tạo h&oacute;a mu&ocirc;n m&agrave;u.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-6.jpg\" alt=\"V&agrave; trong nhịp thời gian đưa đẩy, ta bắt gặp n&uacute;i đ&ocirc;i đang đổi m&agrave;u thay &aacute;o suốt bốn m&ugrave;a, để kẻ kh&aacute;ch vội v&atilde; đi t&igrave;m những ngọt ng&agrave;o trong trẻo chốn rẻo cao - Ảnh: Sưu tầm\" data-index=\"5\" /></p>\r\n<p style=\"text-align: center;\"><em>V&agrave; trong nhịp thời gian đưa đẩy, ta bắt gặp n&uacute;i đ&ocirc;i đang đổi m&agrave;u thay &aacute;o suốt bốn m&ugrave;a, để kẻ kh&aacute;ch vội v&atilde; đi t&igrave;m những ngọt ng&agrave;o trong trẻo chốn rẻo cao - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">PHỐ CỔ ĐỒNG VĂN &ndash; HO&Agrave;I NIỆM TRONG NHỮNG DẤU VẾT XƯA</h2>\r\n<p style=\"text-align: justify;\">Nằm ở trung t&acirc;m của thị trấn Đồng Văn, trong một thung lũng nhỏ được bao bọc bởi bốn bề v&aacute;ch đ&aacute; cao sừng sững, phổ cổ Đồng Văn như nơi lưu giữ phần hồn mảnh đất miền bi&ecirc;n viễn qua bao thế kỷ, để những dấu ấn xưa c&ograve;n tồn tại m&atilde;i đến tận ng&agrave;y nay mặc cho d&ograve;ng đời cứ m&atilde;i tr&ocirc;i qua.</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-7.jpg\" alt=\"Phố cổ Đồng Văn nằm ẩn m&igrave;nh giữa bốn bề v&aacute;ch đ&aacute; cao sừng sững - Ảnh: Sưu tầm\" data-index=\"6\" /></p>\r\n<p style=\"text-align: center;\"><em>Phố cổ Đồng Văn nằm ẩn m&igrave;nh giữa bốn bề v&aacute;ch đ&aacute; cao sừng sững - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-8.jpg\" alt=\"Nơi lưu giữ phần hồn mảnh đất miền bi&ecirc;n viễn qua bao năm th&aacute;ng - Ảnh: Sưu tầm\" data-index=\"7\" /></p>\r\n<p style=\"text-align: center;\"><em>Nơi lưu giữ phần hồn mảnh đất miền bi&ecirc;n viễn qua bao năm th&aacute;ng - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">Phố cổ Đồng Văn nhỏ nhắn lắm, chỉ vẹn vẹn 40 n&oacute;c nh&agrave; c&oacute; ni&ecirc;n đại tr&ecirc;n dưới trăm năm, nơi cư ngụ của nhiều gia đ&igrave;nh M&ocirc;ng, T&agrave;y, Hoa&hellip; từ những năm đầu thế kỷ XIX. Ấy vậy n&ecirc;n, to&agrave;n khu phố phảng phất r&otilde; n&eacute;t phong c&aacute;ch kiến tr&uacute;c của người Hoa, c&oacute; những ng&ocirc;i nh&agrave; hai tầng m&aacute;i ng&oacute;i &acirc;m dương, c&oacute; những cột đ&aacute; lớn được chạm trổ v&ocirc; c&ugrave;ng lạ mắt v&agrave; thấp tho&aacute;ng đ&acirc;u đ&oacute; dưới những m&aacute;i nh&agrave; xinh chợt thấy chiếc đ&egrave;n l&ograve;ng đỏ treo cao, sưởi ấm cả một v&ugrave;ng trời trong c&aacute;i tiết đầu đ&ocirc;ng lạnh lẽo.</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-9.jpg\" alt=\"Phảng ph&aacute;t r&otilde; n&eacute;t đặc trưng trong kiến tr&uacute;c của người Hoa - Ảnh: Sưu tầm\" data-index=\"8\" /></p>\r\n<p style=\"text-align: justify;\"><em>Phảng ph&aacute;t r&otilde; n&eacute;t đặc trưng trong kiến tr&uacute;c của người Hoa - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">Ta lại ngạc nhi&ecirc;n trước sự biến h&oacute;a diệu k&igrave; của phố cổ trong những gam m&agrave;u lạ lẫm. K&igrave;a buổi sớm tinh mơ, trong l&agrave;n sương mờ ảo giăng mắc khắp đất trời, trong vẻ b&agrave;ng bạc đến tịch li&ecirc;u của một g&oacute;c phố đang im l&igrave;m trong giấc ngủ, bỗng c&oacute; những &aacute;nh nắng rực v&agrave;ng soi rọi tr&ecirc;n những bức tường đ&aacute; x&aacute;m ngắt bao năm.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/5/du-lich-canh-dep-Ha-Giang-mytour-10.jpg\" alt=\"Để đ&ocirc;i khi ta chợt ngạc nhi&ecirc;n trước sự biến h&oacute;a diệu k&igrave; của phố cổ trong những gam m&agrave;u lạ lẫm - Ảnh: Sưu tầm\" data-index=\"9\" /></p>\r\n<p style=\"text-align: center;\"><em>Để đ&ocirc;i khi ta chợt ngạc nhi&ecirc;n trước sự biến h&oacute;a diệu k&igrave; của phố cổ trong những gam m&agrave;u lạ lẫm - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-11.jpg\" alt=\"Mờ ảo trong l&agrave;n sương mỗi sớm mai - Ảnh: Nguyen Nghiep\" data-index=\"10\" /></p>\r\n<p style=\"text-align: center;\"><em>Mờ ảo trong l&agrave;n sương mỗi sớm mai - Ảnh: Nguyen Nghiep</em></p>\r\n<p style=\"text-align: justify;\">Khi buổi chiều t&agrave; tĩnh mịch k&eacute;o về, khi &aacute;nh ho&agrave;ng h&ocirc;n phủ khắp một v&ugrave;ng trời miền cao nguy&ecirc;n đ&aacute;, t&ocirc; điểm cho đất th&ecirc;m hồng, v&agrave; những ng&ocirc;i nh&agrave; nhuốm th&ecirc;m m&agrave;u xưa cũ, cả khu phố chợt ch&igrave;m trong những khoảng lặng đến nao l&ograve;ng, thấy tiếng gọi thời gian vương v&iacute;t tr&ecirc;n từng khung cửa nhỏ xa xăm.</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-12.jpg\" alt=\"Im ắng đến nao l&ograve;ng trong &aacute;nh ma mị buổi chiều t&agrave; - Ảnh: Minhthanh2010\" data-index=\"11\" /></p>\r\n<p style=\"text-align: center;\"><em>Im ắng đến nao l&ograve;ng trong &aacute;nh ma mị buổi chiều t&agrave;&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">Rồi m&agrave;n đ&ecirc;m dần bu&ocirc;ng xuống, cả kh&ocirc;ng gian ẩn m&igrave;nh trong những mảng đen u tịch, nhưng khi &aacute;nh đ&egrave;n dầu leo l&eacute;t được thắp l&ecirc;n trong những căn nh&agrave; chốc tỏ chốc mờ, ta chợt nghe văng vẳng đ&acirc;u đ&oacute; tiếng k&egrave;n m&ocirc;i gọi bạn t&igrave;nh da diết, thấy những y&ecirc;u thương lại đong đầy tr&ecirc;n phố cổ, v&agrave; l&ograve;ng lai bồi hồi nhớ tới người thương.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-13.jpg\" alt=\"Rồi chốc tỏ chốc mờ trong &aacute;nh đ&egrave;n leo l&eacute;t thắp l&ecirc;n trong những căn nh&agrave; nhỏ - Ảnh: Tr.vuhung\" data-index=\"12\" /></p>\r\n<p style=\"text-align: center;\"><em>Rồi chốc tỏ chốc mờ trong &aacute;nh đ&egrave;n leo l&eacute;t thắp l&ecirc;n trong những căn nh&agrave; nhỏ&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;CỘT CỜ LŨNG C&Uacute; &ndash; THI&Ecirc;NG LI&Ecirc;NG NƠI V&Ugrave;NG ĐỊA ĐẦU TỔ QUỐC</p>\r\n<p style=\"text-align: justify;\">Trong bức tranh h&ugrave;ng vỹ của miền cao nguy&ecirc;n đ&aacute;, cột cờ Lũng C&uacute; hiện l&ecirc;n sừng sững giữa đất trời, như một h&igrave;nh ảnh thi&ecirc;ng li&ecirc;ng trong l&ograve;ng những người con đất Việt, để mỗi lần đặt ch&acirc;n l&ecirc;n nơi ấy, đứng dưới l&aacute; cờ đỏ sao v&agrave;ng đang tung bay trong gi&oacute; lớn, ta chợt thấy những nhiệt huyết lại d&acirc;ng tr&agrave;o v&agrave; những quyết t&acirc;m của ng&agrave;y trẻ lại sục s&ocirc;i trong huyết quản.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-14.jpg\" alt=\"Cột cờ Lũng C&uacute; hi&ecirc;n ngang giữa đại ng&agrave;n xa - Ảnh: Sưu tầm\" data-index=\"13\" /></p>\r\n<p style=\"text-align: center;\"><em>Cột cờ Lũng C&uacute; hi&ecirc;n ngang giữa đại ng&agrave;n xa - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<div class=\"box\" style=\"text-align: justify;\">\r\n<div class=\"box-body\">\r\n<p><strong>Ở đ&acirc;u khi đi du lịch tại&nbsp;<a href=\"https://mytour.vn/c27/khach-san-tai-ha-giang.html\" target=\"_blank\">H&agrave; Giang ?</a></strong></p>\r\n<ul class=\"nav-thumbnails nav-thumbnails-list\">\r\n<li class=\"col-xs-6\">\r\n<div class=\"item-thumbnail\"><a class=\"item-name\" title=\"Kh&aacute;ch Sạn Sapaly L&agrave;o Cai\" href=\"https://mytour.vn/10954-khach-san-sapaly-lao-cai.html\" target=\"_blank\">Kh&aacute;ch Sạn Sapaly L&agrave;o Cai</a><a title=\"Kh&aacute;ch Sạn Sapaly L&agrave;o Cai\" href=\"https://mytour.vn/10954-khach-san-sapaly-lao-cai.html\" target=\"_blank\"><img class=\"img-responsive\" title=\"Kh&aacute;ch Sạn Sapaly L&agrave;o Cai\" src=\"https://staticproxy.mytourcdn.com/120x90,q90/resources/pictures/hotels/14/ouk1459332557_khach_san_sapaly_lao_cai.jpg\" alt=\"Kh&aacute;ch Sạn Sapaly L&agrave;o Cai\" /></a>\r\n<div class=\"item-thumbnail-content\"><span class=\"rate\">8.2</span>\r\n<div class=\"star star-xs\">&nbsp;</div>\r\n<div class=\"item-price min_height_20\">Gi&aacute; từ:&nbsp;<strong class=\"price\">637,000&nbsp;<small>đ</small></strong></div>\r\n</div>\r\n</div>\r\n</li>\r\n<li class=\"col-xs-6\">\r\n<div class=\"item-thumbnail\"><a class=\"item-name\" title=\"Kh&aacute;ch Sạn Thi&ecirc;n Hải \" href=\"https://mytour.vn/178-khach-san-thien-hai.html\" target=\"_blank\">Kh&aacute;ch Sạn Thi&ecirc;n Hải</a><a title=\"Kh&aacute;ch Sạn Thi&ecirc;n Hải \" href=\"https://mytour.vn/178-khach-san-thien-hai.html\" target=\"_blank\"><img class=\"img-responsive\" title=\"Kh&aacute;ch Sạn Thi&ecirc;n Hải \" src=\"https://staticproxy.mytourcdn.com/120x90,q90/resources/pictures/hotels/18/och1402715444_khach-san-thien-hai.jpg\" alt=\"Kh&aacute;ch Sạn Thi&ecirc;n Hải \" /></a>\r\n<div class=\"item-thumbnail-content\"><span class=\"rate\">7.8</span>\r\n<div class=\"star star-xs\">&nbsp;</div>\r\n<div class=\"item-price min_height_20\">Gi&aacute; từ:&nbsp;<strong class=\"price\">546,000&nbsp;<small>đ</small></strong></div>\r\n</div>\r\n</div>\r\n</li>\r\n<li class=\"col-xs-6\">\r\n<div class=\"item-thumbnail\"><a class=\"item-name\" title=\"Kh&aacute;ch Sạn Ho&agrave;ng Gia L&agrave;o Cai\" href=\"https://mytour.vn/12974-khach-san-hoang-gia-lao-cai.html\" target=\"_blank\">Kh&aacute;ch Sạn Ho&agrave;ng Gia L&agrave;o Cai</a><a title=\"Kh&aacute;ch Sạn Ho&agrave;ng Gia L&agrave;o Cai\" href=\"https://mytour.vn/12974-khach-san-hoang-gia-lao-cai.html\" target=\"_blank\"><img class=\"img-responsive\" title=\"Kh&aacute;ch Sạn Ho&agrave;ng Gia L&agrave;o Cai\" src=\"https://staticproxy.mytourcdn.com/120x90,q90/resources/pictures/hotels/14/z6i1460605512_khach_san_hoang_gia_lao_cai.jpg\" alt=\"Kh&aacute;ch Sạn Ho&agrave;ng Gia L&agrave;o Cai\" /></a>\r\n<div class=\"item-thumbnail-content\"><span class=\"rate\">8.3</span>\r\n<div class=\"star star-xs\">&nbsp;</div>\r\n<div class=\"item-price min_height_20\">Gi&aacute; từ:&nbsp;<strong class=\"price\">350,000&nbsp;<small>đ</small></strong></div>\r\n</div>\r\n</div>\r\n</li>\r\n<li class=\"col-xs-6\">\r\n<div class=\"item-thumbnail\"><a class=\"item-name\" title=\"Kh&aacute;ch Sạn L&agrave;o Cai Galaxy\" href=\"https://mytour.vn/10583-khach-san-lao-cai-galaxy.html\" target=\"_blank\">Kh&aacute;ch Sạn L&agrave;o Cai Galaxy</a><a title=\"Kh&aacute;ch Sạn L&agrave;o Cai Galaxy\" href=\"https://mytour.vn/10583-khach-san-lao-cai-galaxy.html\" target=\"_blank\"><img class=\"img-responsive\" title=\"Kh&aacute;ch Sạn L&agrave;o Cai Galaxy\" src=\"https://staticproxy.mytourcdn.com/120x90,q90/resources/pictures/hotels/3/jzb1402666332_khach-san-lao-cai-galaxy.jpg\" alt=\"Kh&aacute;ch Sạn L&agrave;o Cai Galaxy\" /></a>\r\n<div class=\"item-thumbnail-content\"><span class=\"rate\">7.8</span>\r\n<div class=\"star star-xs\">&nbsp;</div>\r\n<div class=\"item-price min_height_20\">Gi&aacute; từ:&nbsp;<strong class=\"price\">528,000&nbsp;<small>đ</small></strong></div>\r\n</div>\r\n</div>\r\n</li>\r\n</ul>\r\n</div>\r\n</div>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-15.jpg\" alt=\"Để ta thấy bao nhiệt huyết tr&agrave;o d&acirc;ng khi đứng dưới ngọn cờ đang tung bay phấp phới - Ảnh: D.C studio\" data-index=\"14\" /></p>\r\n<p style=\"text-align: center;\"><em>Để ta thấy bao nhiệt huyết tr&agrave;o d&acirc;ng khi đứng dưới ngọn cờ đang tung bay phấp phới&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">Vậy n&ecirc;n cứ mỗi lần đặt ch&acirc;n l&ecirc;n Lũng C&uacute;, người ta lại th&acirc;n chinh phục 286 bậc đ&aacute; để l&ecirc;n độ cao 1700m. Đứng tr&ecirc;n đỉnh cột cờ, ph&oacute;ng tầm mắt xuống xa xa b&ecirc;n dưới, bức tranh cao nguy&ecirc;n đ&aacute; Đồng Văn hiện l&ecirc;n sống động đến kh&ocirc;ng ngờ, k&igrave;a những d&atilde;y n&uacute;i trập tr&ugrave;ng đ&aacute; tai m&egrave;o x&aacute;m ngo&eacute;t, k&igrave;a đại ng&agrave;n bao la h&ugrave;ng vỹ, rồi những thửa ruộng bậc thang thay &aacute;o đổi m&agrave;u suốt bốn m&ugrave;a xen lẫn với những m&aacute;i nh&agrave; b&igrave;nh y&ecirc;n trong c&aacute;c bản l&agrave;ng L&ocirc; L&ocirc; Chải, S&eacute;o Lủng, Cẳn Tằng&hellip;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-16.jpg\" alt=\"Thế n&ecirc;n người ta lu&ocirc;n tự th&acirc;n chinh phục h&agrave;ng trăm bậc đ&aacute; - Ảnh: Chutchit88\" data-index=\"15\" /></p>\r\n<p style=\"text-align: center;\"><em>Thế n&ecirc;n người ta lu&ocirc;n tự th&acirc;n chinh phục h&agrave;ng trăm bậc đ&aacute;&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-17.jpg\" alt=\"Đứng tr&ecirc;n đỉnh cột cờ ngắm nh&igrave;n bức họa Đồng Văn sống động ph&iacute;a dưới kia - Ảnh: Chuphuong96\" data-index=\"16\" /></p>\r\n<p style=\"text-align: center;\"><em>Đứng tr&ecirc;n đỉnh cột cờ ngắm nh&igrave;n bức họa Đồng Văn sống động ph&iacute;a dưới kia - Ảnh: Chuphuong96</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Một bức họa thi&ecirc;n nhi&ecirc;n được tạc l&ecirc;n từ b&agrave;n tay t&agrave;i hoa của mẹ tạo h&oacute;a đ&atilde; bao đời, một khung cảnh được vẽ n&ecirc;n bởi những mảng m&agrave;u đối lập nhưng cũng rất h&agrave;i h&ograve;a, tạo n&ecirc;n một Đồng Văn vừa hoang sơ, h&ugrave;ng vỹ vừa ẩn chứa những n&eacute;t thơ t&igrave;nh rất lạ, để ta t&igrave;m thấy trong n&uacute;i rừng xanh thẳm, tr&ecirc;n những sườn n&uacute;i đ&aacute; tai m&egrave;o gai g&oacute;c vẫn c&oacute; những n&eacute;t chấm ph&aacute; đầy b&igrave;nh y&ecirc;n của cuộc sống chốn non cao.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-18.jpg\" alt=\"Rồi chợt say l&ograve;ng trước một Đồng Văn h&ugrave;ng vỹ những cũng rất thơ t&igrave;nh - Ảnh: Troang\" data-index=\"17\" /></p>\r\n<p style=\"text-align: center;\"><em>Rồi chợt say l&ograve;ng trước một Đồng Văn h&ugrave;ng vỹ những cũng rất thơ t&igrave;nh&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">DINH THỰ VUA M&Egrave;O &ndash; NHỮNG ĐIỀU KỲ B&Iacute; TR&Ecirc;N ĐẤT BẮC</h2>\r\n<p style=\"text-align: justify;\">Nếu đến H&agrave; Giang, nhớ gh&eacute; S&agrave; Ph&igrave;n, t&igrave;m về dinh thự vua M&egrave;o c&oacute; ni&ecirc;n đại đ&atilde; trăm năm, nơi m&agrave; vua M&egrave;o Vương Đức Ch&iacute;nh đ&atilde; phải thu&ecirc; người x&acirc;y dựng trong suốt t&aacute;m năm, tốn k&eacute;m gần 150.000 đồng bạc trắng. Dinh thự uy nghi, lộng lẫy nằm lọt thỏm giữa n&uacute;i rừng heo h&uacute;t, nơi ta bắt gặp sự giao thoa kiến tr&uacute;c của ba nền văn h&oacute;a : Trung Quốc, người M&ocirc;ng v&agrave; người Ph&aacute;p độc đ&aacute;o v&ocirc; c&ugrave;ng.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-19.jpg\" alt=\"Dinh thự vua M&egrave;o uy nghĩ giữa n&uacute;i rừng heo h&uacute;t - Ảnh: Sưu tầm\" data-index=\"18\" /></p>\r\n<p style=\"text-align: center;\"><em>Dinh thự vua M&egrave;o uy nghĩ giữa n&uacute;i rừng heo h&uacute;t - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">To&agrave;n bộ dinh thự mang d&aacute;ng dấp của một t&ograve;a l&acirc;u đ&agrave;i lớn, tất cả c&aacute;c ng&ocirc;i nh&agrave; đều được x&acirc;y dựng bằng c&aacute;c phiến đ&aacute; xanh lớn, c&aacute;c loại gỗ qu&yacute; như gỗ th&ocirc;ng, gỗ pơ mu, c&aacute;c ng&ograve;i đất nung được chạm trổ tinh xảo v&agrave; cả những m&aacute;i ng&oacute;i &acirc;m dương mang phong c&aacute;ch cổ xưa.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/6/du-lich-canh-dep-Ha-Giang-mytour-20.jpg\" alt=\"Đ&oacute; l&agrave; những ng&ocirc;i nh&agrave; được x&acirc;y dựng như một t&ograve;a l&acirc;u đ&agrave;i lớn - Ảnh: Changii\" data-index=\"19\" /></p>\r\n<p style=\"text-align: center;\"><em>Đ&oacute; l&agrave; những ng&ocirc;i nh&agrave; được x&acirc;y dựng như một t&ograve;a l&acirc;u đ&agrave;i lớn&nbsp;</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/7/du-lich-canh-dep-Ha-Giang-mytour-21.jpg\" alt=\"Sử dụng những phiến đ&aacute; xanh, gỗ qu&yacute; v&agrave; cả m&aacute;i &acirc;m dương - Ảnh: Thutrang\" data-index=\"20\" /></p>\r\n<p style=\"text-align: center;\"><em>Sử dụng những phiến đ&aacute; xanh, gỗ qu&yacute; v&agrave; cả m&aacute;i &acirc;m dương&nbsp;</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Để rồi h&ocirc;m nay, khi đặt ch&acirc;n tới dinh thự vua M&egrave;o, người ta phải ngỡ ng&agrave;ng trước sự bề thế của một c&ocirc;ng tr&igrave;nh kiến tr&uacute;c thuở h&ocirc;m n&agrave;o, nhưng thấp tho&aacute;ng trong kh&ocirc;ng gian rộng lớn v&agrave; đầy vẻ quyền nghi đ&oacute;, thời gian cũng đ&atilde; in hằn những dấu vết của th&aacute;ng năm, những mảng r&ecirc;u phong phủ tr&ecirc;n từng đường n&eacute;t, c&aacute;i lạnh lẽo của chốn kh&ocirc;ng người ở tr&agrave;n ngập t&acirc;m hồn, khiến người ta cứ cảm nhận được những điều k&igrave; b&iacute; ẩn s&acirc;u, ngạc nhi&ecirc;n, ngỡ ng&agrave;ng rồi lại t&ograve; m&ograve; kh&aacute;m ph&aacute;.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/7/du-lich-canh-dep-Ha-Giang-mytour-22.jpg\" alt=\"Nhưng tất cả giờ đ&acirc;y đều đ&atilde; nhuốm m&agrave;u r&ecirc;u phong lạnh leo - Ảnh: Sưu tầm\" data-index=\"21\" /></p>\r\n<p style=\"text-align: center;\"><em>Nhưng tất cả giờ đ&acirc;y đều đ&atilde; nhuốm m&agrave;u r&ecirc;u phong lạnh leo&nbsp;</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/7/du-lich-canh-dep-Ha-Giang-mytour-23.jpg\" alt=\"Khiến bao ngờ phải ngỡ ng&agrave;ng t&igrave;m kiếm những điều huyền b&iacute; ẩn s&acirc;u - Ảnh: Kh&aacute;nh Hmoong\" data-index=\"22\" /></p>\r\n<p style=\"text-align: center;\"><em>Khiến bao ngờ phải ngỡ ng&agrave;ng t&igrave;m kiếm những điều huyền b&iacute; ẩn s&acirc;u&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">ẨM THỰC H&Agrave; GIANG &ndash; N&Eacute;T DUY&Ecirc;N LẠ Ở V&Ugrave;NG CAO&nbsp;</h2>\r\n<p style=\"text-align: justify;\">Người ta kh&ocirc;ng chỉ say l&ograve;ng bởi cảnh đẹp ở H&agrave; Giang, người ta c&ograve;n si m&ecirc; nền ẩm thực đầy quyến rũ ở mảnh đất n&agrave;y. Ai đến H&agrave; Giang một lần m&agrave; kh&ocirc;ng nếm thử những m&oacute;n đặc sản mang phong vị v&agrave; c&aacute;i hồn dung dị chốn rẻo cao th&igrave; c&oacute; lẽ bạn chưa thưởng thức H&agrave; Giang trọn vẹn.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/7/du-lich-canh-dep-Ha-Giang-mytour-24.jpg\" alt=\"Ẩm thực H&agrave; Giang cũng khiến bao người phải si m&ecirc; lắm - Ảnh: Sưu tầm\" data-index=\"23\" /></p>\r\n<p style=\"text-align: center;\">Ẩm thực H&agrave; Giang cũng khiến bao người phải si m&ecirc; lắm - Ảnh: Sưu tầm</p>\r\n<p style=\"text-align: justify;\">Ảm thực H&agrave; Giang độc đ&aacute;o lắm, ta chợt y&ecirc;u đến lạ m&oacute;n thắng dền c&oacute; vị ngọt l&agrave;nh của nếp, vị b&eacute;o ngậy của nước cốt dừa v&agrave; ch&uacute;t cay cay cho ấm bụng của gừng. Rồi m&oacute;n thắng cố được chế bi&ecirc;n từ xương v&agrave; nội tạng của tr&acirc;u, b&ograve; c&ugrave;ng c&aacute;c loại thảo quả thơm ngơn, th&ecirc;m ch&eacute;n rượu ng&ocirc; b&ecirc;n bếp lửa nghi ng&uacute;t kh&oacute;i th&igrave; c&ograve;n g&igrave; hơn nữa.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/7/du-lich-canh-dep-Ha-Giang-mytour-25.jpg\" alt=\"Những m&oacute;n ăn mang c&aacute;i hồn dung dị chốn rẻo cao - Ảnh: Sưu tầm\" data-index=\"24\" /></p>\r\n<p style=\"text-align: center;\"><em>Những m&oacute;n ăn mang c&aacute;i hồn dung dị chốn rẻo cao - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Ta c&ograve;n bắt gặp trong những khu chợ nhỏ m&oacute;n ch&aacute;o ấu tẩu mang n&eacute;t đặc trưng của miền cao nguy&ecirc;n đ&aacute;, nơi hội tụ c&aacute;c cung bậc m&ugrave;i vị chốn rẻo cao khiến kẻ kh&aacute;ch thử một lần rồi nghiện m&atilde;i. Rồi r&ecirc;u nướng đầy lạ lẫm, rồi thịt tr&acirc;u g&aacute;c bếp thơm tới ngất ng&acirc;y, th&ecirc;m ch&uacute;t vị mật ong bạc h&agrave; &ecirc;m dịu. H&agrave; Giang nhiều m&oacute;n ăn ngon lắm, để người lữ kh&aacute;ch đường xa mỗi lần đến đ&oacute; cứ phải m&ecirc; mệt m&atilde;i.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/7/du-lich-canh-dep-Ha-Giang-mytour-26.jpg\" alt=\"Khiến kẻ kh&aacute;ch chỉ thử một lần th&ocirc;i rồi nghiện m&atilde;i - Ảnh: Sưu tầm\" data-index=\"25\" /></p>\r\n<p style=\"text-align: center;\"><em>Khiến kẻ kh&aacute;ch chỉ thử một lần th&ocirc;i rồi nghiện m&atilde;i - Ảnh: Sưu tầm</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/14_11_2016/7/du-lich-canh-dep-Ha-Giang-mytour-27.jpg\" alt=\"Chỉ mong về nơi ấy th&ecirc;m lần nữa để thưởng thức trọn vẹn những phong vị chốn đại ng&agrave;n - Ảnh: Ainhat306\" data-index=\"26\" /></p>\r\n<p style=\"text-align: center;\"><em>Chỉ mong về nơi ấy th&ecirc;m lần nữa để thưởng thức trọn vẹn những phong vị chốn đại ng&agrave;n&nbsp;</em>&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Dường như, d&ugrave; c&oacute; đặt ch&acirc;n đến H&agrave; Giang biết bao lần đi chăng nữa, kẻ kh&aacute;ch vẫn m&atilde;i giữ vẹn nguy&ecirc;n những cảm x&uacute;c ngỡ ng&agrave;ng như buổi ban đầu. Ta bắt gặp những n&eacute;t an y&ecirc;n ẩn trong đại ng&agrave;n h&ugrave;ng vỹ, gặp những thơ t&igrave;nh tr&ecirc;n mảnh đất chỉ c&oacute; đ&aacute; x&aacute;m ngo&eacute;t bao năm, rồi đ&acirc;u đ&oacute; tr&ecirc;n những chặng đường quen lạ m&agrave; ta đ&atilde; từng qua, c&oacute; đ&ocirc;i khi chợt ph&aacute;t hiện ra biết bao điều k&igrave; b&iacute;, để l&ograve;ng lại rạo rực trong những miền thương nhớ kh&ocirc;n ngu&ocirc;i, để H&agrave; Giang m&atilde;i như một nơi chốn khiến bước ch&acirc;n người lữ kh&aacute;ch phải lạc trong những buổi ngao du chẳng muốn quay về.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', '10508743945d087a4f018cf1.11630948.jpg', '2019-06-18', 1);
INSERT INTO `tintuc` (`tintuc_id`, `danhmuc_id`, `tintuc_ten`, `tintuc_mota`, `tintuc_noidung`, `tintuc_anh`, `tintuc_ngaytao`, `tintuc_hot`) VALUES
(45, 27, 'Ngắm Vẻ Đẹp Ngoạn Mục Của Những Nấc Thang Lên Thiên Đường Ở Hà Giang ta phải si mê', '<div class=\"container\" style=\"text-align: justify;\"><em>Địa danh H&agrave; Giang nằm ở điểm cực Bắc tổ quốc, nơi c&oacute; cột mốc bi&ecirc;n giới đ&aacute;nh dấu chủ quyền thi&ecirc;ng li&ecirc;ng của nước ta. H&agrave; Giang l&agrave;m đắm say biết bao du kh&aacute;ch với những cung đường quanh co, nguy hiểm nhưng quyến rũ đến chết người, những thửa ruộng bậc thang v&agrave;ng ruộm v&agrave;o m&uacute;a l&uacute;a ch&iacute;n hay những c&ocirc; g&aacute;i bản duy&ecirc;n d&aacute;ng e ấp. Một lần đến với H&agrave; Giang để cảm nhận sức sống m&atilde;nh liệt trước những thử th&aacute;ch khắc nghiệt của thi&ecirc;n nhi&ecirc;n bạn nh&eacute;!</em></div>', '<h5 style=\"text-align: justify;\">ĐỘC H&Agrave;NH</h5>\r\n<p style=\"text-align: justify;\">Độc h&agrave;nh lu&ocirc;n l&agrave; một h&agrave;nh tr&igrave;nh mang lại những cảm x&uacute;c tr&aacute;i chiều. Một trải nghiệm mạo hiểm, khi phải đơn độc giữa những cung đường hiểm trở nơi H&agrave; Giang, l&agrave; khi xung quanh chỉ c&oacute; n&uacute;i rừng h&ugrave;ng vĩ v&agrave; kh&ocirc;ng t&igrave;m thấy được ai đ&oacute; b&ecirc;n cạnh sẻ chia. Song. độc h&agrave;nh chinh phục H&agrave; Giang cũng đem lại những điều th&uacute; vị. Vị kh&aacute;ch l&atilde;ng du trong h&agrave;nh tr&igrave;nh của bản th&acirc;n kh&ocirc;ng phải phụ thuộc v&agrave;o ai cả, một m&igrave;nh tự do, ph&oacute;ng kho&aacute;ng giữa đất trời bao la.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-1.jpg\" alt=\"Độc h&agrave;nh c&ugrave;ng chiến m&atilde; - Ảnh: tyn_crazy\" data-index=\"0\" /></p>\r\n<p style=\"text-align: center;\"><em>Độc h&agrave;nh c&ugrave;ng chiến m&atilde; - Ảnh: tyn_crazy</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Hẳn khoảnh khắc một m&igrave;nh đứng giữa cao nguy&ecirc;n đ&aacute;, bạn sẽ cảm nhận s&acirc;u sắc n&eacute;t đẹp của mảnh đất n&agrave;y. Hay đơn giản, được ch&igrave;m đắm trong cảm x&uacute;c của ri&ecirc;ng bạn, về khung cảnh nơi đ&acirc;y. Độc h&agrave;nh đ&ograve;i hỏi phượt thủ phải c&oacute; sự chuẩn bị kĩ c&agrave;ng cũng như c&oacute; kinh nghiệm để c&oacute; được một chuyến đi an to&agrave;n v&agrave; ho&agrave;n hảo.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-2.jpg\" alt=\"Khoảng lặng - Ảnh: hienddang\" data-index=\"1\" /></p>\r\n<p style=\"text-align: center;\"><em>Khoảng lặng - Ảnh: hienddang</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">CẮM TRẠI NGỦ Đ&Ecirc;M BẰNG LỀU</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">S&aacute;ng sớm ở H&agrave; Giang sương giăng lối đi, c&oacute; ch&uacute;t se lạnh của v&ugrave;ng cao, hẳn sẽ l&agrave; bầu kh&ocirc;ng kh&iacute; m&agrave; nhiều phượt thủ th&iacute;ch m&ecirc;. Một đ&ecirc;m ngủ lại trong căn lều dựng giữa đất trời, xung quanh l&agrave; thi&ecirc;n nhi&ecirc;n, l&agrave; trăng sao c&ugrave;ng bẩu bạn với những tr&aacute;i tim l&atilde;ng du. H&agrave; Giang kh&ocirc;ng thiếu những địa điểm cắm trại qua đ&ecirc;m, m&agrave; s&aacute;ng thức dậy, thấy m&igrave;nh đang đứng ở một đỉnh đ&egrave;o cao, xa xa l&agrave; cung đường quanh co &ocirc;m ấp những ch&acirc;n n&uacute;i. L&uacute;c ấy, chỉ muốn h&iacute;t một hơi thật s&acirc;u, cho bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh n&agrave;y theo bạn về th&agrave;nh phố.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-3.jpg\" alt=\"Đưa nhau đi trốn - Ảnh: yu196_\" data-index=\"2\" /></p>\r\n<p style=\"text-align: center;\"><em>Đưa nhau đi trốn - Ảnh: yu196_</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Đ&oacute;n b&igrave;nh minh tr&ecirc;n nương cao, c&ugrave;ng nh&acirc;m nhi thứ uống n&oacute;ng hổi như c&agrave; ph&ecirc; hay tr&agrave;, b&ecirc;n cạnh l&agrave; người bạn đồng h&agrave;nh th&acirc;n thuộc, l&agrave; một trong những trải nghiệm tuyệt vời m&agrave; H&agrave; Giang m&agrave; lại cho bạn.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-4.jpg\" alt=\"Sớm thức dậy ở một nơi xa - Ảnh: M Huy Photography\" data-index=\"3\" /></p>\r\n<p style=\"text-align: center;\"><em>Sớm thức dậy ở một nơi xa&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">VƯỢT S&Ocirc;NG NHO QUẾ BẰNG B&Egrave;</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">L&ecirc;nh đ&ecirc;nh tr&ecirc;n d&ograve;ng s&ocirc;ng vắng tr&ecirc;n một chiếc b&egrave; đơn giản, nh&igrave;n cảnh vật hai b&ecirc;n bờ lướt qua tầm mắt, l&agrave; một trong những điều tuyệt vời m&agrave; H&agrave; Giang d&agrave;nh tặng cho những ai y&ecirc;u th&iacute;ch sự mạo hiểm. Vượt s&ocirc;ng Nho Quế bằng b&egrave; l&agrave; c&aacute;ch để tiết kiệm thời gian khi kh&ocirc;ng muốn phải trở về bằng con đường X&iacute;n C&aacute;i xa x&ocirc;i. Tr&ecirc;n chiếc b&egrave; chỉ được cố định bằng một chiếc r&ograve;ng rọc, sẽ đưa bạn đi hết con s&ocirc;ng Nho Quế, hệt như trong một bộ phim cổ trang, th&aacute;ch thức những phượt thủ với tr&aacute;i tim dũng cảm.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-5.jpg\" alt=\"B&egrave; xu&ocirc;i về đ&acirc;u - Ảnh: Doan Manh\" data-index=\"4\" /></p>\r\n<p style=\"text-align: center;\"><em>B&egrave; xu&ocirc;i về đ&acirc;u - Ảnh: Doan Manh</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">NGỦ LẠI Ở MỘT BẢN L&Agrave;NG XA X&Ocirc;I</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Homestay hẳn kh&ocirc;ng c&ograve;n l&agrave; h&igrave;nh thức lưu tr&uacute; xa lạ đối với những du kh&aacute;ch đến với H&agrave; Giang. Song, bạn đ&atilde; thử trải nghiệm cảm gi&aacute;c xin ngủ nhờ ở một bản l&agrave;ng xa x&ocirc;i ở L&ugrave;ng C&uacute;, Kh&acirc;u Vại, ngay tại nh&agrave; của những người d&acirc;n địa phương chứ kh&ocirc;ng phải một hotel n&agrave;o chưa? Đến H&agrave; Giang, đừng ngại ngần trải qua một đ&ecirc;m với những người kh&ocirc;ng quen biết, c&ugrave;ng ngồi b&ecirc;n bếp lửa bập b&ugrave;ng, giữa tiết trời se lạnh.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-6.jpg\" alt=\"Hồn nhi&ecirc;n - Ảnh: nguyen.g.tu\" data-index=\"5\" /></p>\r\n<p style=\"text-align: center;\"><em>Hồn nhi&ecirc;n - Ảnh: nguyen.g.tu</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Một trong những lưu &yacute; nhỏ, đ&oacute; l&agrave; bạn n&ecirc;n đi &iacute;t người, lịch sự v&agrave; văn minh th&igrave; vấn đề ngủ lại kh&ocirc;ng l&agrave; qu&aacute; kh&oacute; khăn. Những điều mới lạ v&agrave; cơ hội trải nghiệm cuộc sống thật của người d&acirc;n v&ugrave;ng cao hẳn sẽ l&agrave; một lời mời gọi hấp dẫn.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-7.jpg\" alt=\"Một tho&aacute;ng b&igrave;nh dị - Ảnh: thenextstationis\" data-index=\"6\" /></p>\r\n<p style=\"text-align: center;\"><em>Một tho&aacute;ng b&igrave;nh dị - Ảnh: thenextstationis</em></p>\r\n<h2 style=\"text-align: justify;\">TREKKING TR&Ecirc;N Đ&Egrave;O M&Atilde; PH&Iacute; L&Egrave;NG</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-8.jpg\" alt=\"Chinh phục những đỉnh cao mới - Ảnh: Ng&ocirc; Huy H&ograve;a\" data-index=\"7\" /></p>\r\n<p style=\"text-align: center;\"><em>Chinh phục những đỉnh cao mới - Ảnh: Ng&ocirc; Huy H&ograve;a</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Trekking tr&ecirc;n đ&egrave;o M&agrave; Ph&iacute; L&egrave;ng, bạn c&oacute; bao giờ d&aacute;m thử qua? Đ&acirc;y l&agrave; một trải nghiệm đ&ograve;i hỏi phượt thủ phải c&oacute; sức khỏe cũng như tinh thần y&ecirc;u thể thao để c&oacute; thể chinh phục cung đường đ&egrave;o quanh co v&agrave; hiểm trở n&agrave;y bằng ch&iacute;nh đ&ocirc;i ch&acirc;n của họ. Với những ai muốn kết hợp du lịch v&agrave; r&egrave;n luyện thể thao th&igrave; trekking tr&ecirc;n cung đường đ&egrave;o đẹp nhất nơi đ&acirc;y sẽ l&agrave; lựa chọn l&yacute; tưởng.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-9.jpg\" alt=\"Tr&ecirc;n những cung đường hiểm nguy - Ảnh: Ng&ocirc; Huy H&ograve;a\" data-index=\"8\" /></p>\r\n<p style=\"text-align: center;\"><em>Tr&ecirc;n những cung đường hiểm nguy - Ảnh: Ng&ocirc; Huy H&ograve;a</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Nắng v&agrave; gi&oacute; của cao nguy&ecirc;n tạt v&agrave;o mặt, tr&ecirc;n con đường đ&egrave;o cao v&agrave; ngoằn ngo&egrave;o, từng bước ch&acirc;n đưa đến v&ugrave;ng đất mới hay đưa bạn đến đỉnh cao giới hạn của bản th&acirc;n?</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">KH&Aacute;M PH&Aacute; NHỮNG HANG ĐỘNG HOANG SƠ</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<div class=\"box\" style=\"text-align: justify;\">\r\n<div class=\"box-body\">\r\n<p>Với những phượt thủ th&igrave; cảm gi&aacute;c kh&aacute;m ph&aacute; ra một v&ugrave;ng đất lạ hẳn sẽ l&agrave; cảm gi&aacute;c tuyệt vời. Ở H&agrave; Giang, kh&aacute;m ph&aacute; những hang động mới kh&ocirc;ng c&ograve;n l&agrave; trải nghiệm kh&oacute; t&igrave;m, m&agrave; nơi đ&acirc;y c&oacute; rất nhiều hang động chưa được t&igrave;m thấy, vẫn c&ograve;n l&agrave; b&iacute; ẩn đợi chờ phượt thủ đến mở lối. Chinh phục những hang động mới, đ&ograve;i hỏi phượt thủ phải đủ dũng cảm, đủ kinh nghiệm v&agrave; trang bị cho m&igrave;nh h&agrave;nh trang cần thiết. Song h&agrave;nh với đ&oacute;, cũng l&agrave; niềm phấn kh&iacute;ch lạ thường.</p>\r\n</div>\r\n</div>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/10/du-lich-bui-Ha-Giang-mytour-10.jpg\" alt=\"H&agrave; Giang với những hang động k&igrave; b&iacute; đnag đợi phượt thủ - Ảnh: Ng&ocirc; Nữ Ho&agrave;ng Vy\" data-index=\"9\" /></p>\r\n<p style=\"text-align: center;\"><em>H&agrave; Giang với những hang động k&igrave; b&iacute; đang đợi phượt thủ</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">CHẠM TAY V&Agrave;O NHỮNG CỘC MỐC V&Ugrave;NG BI&Ecirc;N XA NHẤT</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Mảnh đất h&igrave;nh chữ S xinh đẹp với những cột mốc đ&aacute;nh dấu nơi tận c&ugrave;ng của bi&ecirc;n giới đất nước, hẳn sẽ l&agrave; một trong những thời khắc đ&aacute;ng nhớ trong suốt h&agrave;nh tr&igrave;nh rong ruổi đến những miền đất lạ của c&aacute;c phượt thủ. Đến với H&agrave; Giang, được đặt tay l&ecirc;n cột mốc cực Bắc của tổ quốc, hẳn sẽ l&agrave; cảm gi&aacute;c tự h&agrave;o, m&atilde;n nguyện v&agrave; cả hạnh ph&uacute;c bởi một chặng đường gian nan để c&oacute; thể chinh phục được nơi đ&acirc;y.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-11.jpg\" alt=\"Nơi cột cờ Lũng C&uacute; lộng gi&oacute; - Ảnh: underwooda86\" data-index=\"10\" /></p>\r\n<p style=\"text-align: justify;\"><em>Nơi cột cờ Lũng C&uacute; lộng gi&oacute; - Ảnh: underwooda86</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">H&agrave; Giang quả thực l&agrave; mảnh đất mang lại cho người ta nhiều cảm x&uacute;c xen lẫn. Bơi xa x&ocirc;i hẻo l&aacute;nh của đất nước, nơi m&agrave; địa h&igrave;nh hiểm trở, nơi m&agrave; người ta &iacute;t biết đến, những nơi ấy, một khi đặt ch&acirc;n đến, l&agrave; cả một qu&aacute; tr&igrave;nh cố gắng kh&ocirc;ng ngừng nghỉ. Mảnh đất nơi Đ&ocirc;ng Bắc n&aacute;y, đ&atilde; ghi dấu biết bao khoảnh khắc đ&aacute;ng nhớ của những phượt thủ.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-12.jpg\" alt=\"Đ&aacute;nh dấu những nơi ta đ&atilde; đi qua - Ảnh: myluong.beobeo\" data-index=\"11\" /></p>\r\n<p style=\"text-align: center;\"><em>Đ&aacute;nh dấu những nơi ta đ&atilde; đi qua</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h2>CHI&Ecirc;M NGƯỠNG NHỮNG V&Ugrave;NG ĐẤT, V&Ugrave;NG HOA</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-13.jpg\" alt=\"Hoa mơn man c&ugrave;ng l&agrave;n gi&oacute; - Ảnh: travy2410\" data-index=\"12\" /></p>\r\n<p style=\"text-align: center;\"><em>Hoa mơn man c&ugrave;ng l&agrave;n gi&oacute;&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Mỗi độ th&aacute;ng 10 về, người ta lại nhớ nhung h&igrave;nh ảnh m&ugrave;a hoa, m&ugrave;a v&agrave;ng tr&ecirc;n cao nguy&ecirc;n n&agrave;y. Nếu một ai đ&atilde; đến với H&agrave; Giang v&agrave;o th&aacute;ng 10, hẳn sẽ kh&ocirc;ng qu&ecirc;n đượcn m&agrave;u hồng t&iacute;m của c&aacute;nh đ&ograve;ng tam gi&aacute;c mạch trải khắp mọi nơi, tr&ecirc;n sườn n&uacute;i, b&ecirc;n &ocirc; cửa nhỏ của người d&acirc;n hay len lỏi nơi đ&egrave;o cao. Sắc hoa mỏng manh v&agrave; b&igrave;nh dị ấy, rung rinh trong nắng m&ugrave;a thu, lay động tr&aacute;i tim của phượt thủ. Khung h&igrave;nh ghi lại sắc hoa tr&ecirc;n n&uacute;i cao h&ugrave;ng vĩ ấy, sẽ l&agrave; khung h&igrave;nh tuyệt đẹp đ&aacute;nh dấu một nơi bạn đ&atilde; đi qua.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-14.jpg\" alt=\"Nương cao v&agrave;ng m&agrave;u nắng - Ảnh: phamloc105\" data-index=\"13\" /></p>\r\n<p style=\"text-align: center;\"><em>Nương cao v&agrave;ng m&agrave;u nắng&nbsp;</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Kh&ocirc;ng chỉ c&oacute; m&ugrave;a hoa, m&agrave; thu về, nơi đ&acirc;y c&ograve;n trải một thảm l&uacute;a v&agrave;ng rực rỡ. M&agrave;u của ấm no, của tr&ugrave; phsu nơi ruộng bậc thang l&agrave;m s&aacute;ng bừng cả một g&oacute;c trời, n&uacute;i rừng như được thổi th&ecirc;m sức sống rực rỡ.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">CHINH PHỤC NHỮNG CON ĐƯỜNG OFFROAD</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-15.jpg\" alt=\"hững cung đường thử th&aacute;ch tay l&aacute;i cự ph&aacute;ch - Ảnh: justrikki\" data-index=\"14\" /></p>\r\n<p style=\"text-align: center;\"><em>Những cung đường thử th&aacute;ch tay l&aacute;i cự ph&aacute;ch </em></p>\r\n<p style=\"text-align: justify;\">Với những tay l&aacute;i chuy&ecirc;n nghiệp, th&igrave; những cung đường c&agrave;ng kh&oacute;, khiến họ c&agrave;ng k&iacute;ch th&iacute;ch, khơi dậy bản năng chinh phục. Đến với H&agrave; Giang, c&oacute; những con đường nhỏ hẹp, gạch đ&aacute; ngổn ngang hay lầy lội mỗi độ trời đất đổ mưa, những tay l&aacute;i cự ph&aacute;ch hẳn sẽ th&iacute;ch m&ecirc;. Vượt qua những con đường kh&oacute; khăn ấy, đ&aacute;nh dấu th&ecirc;m một lần bản th&acirc;n chinh phục được những trải nghiệm kh&oacute; nhắn nơi mảnh đất đẹp xinh n&agrave;y.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-16.jpg\" alt=\"Cung đường kh&oacute; nhằn m&agrave; th&uacute; vị - Ảnh: h_a_n_n_i\" data-index=\"15\" /></p>\r\n<p style=\"text-align: center;\"><em>Cung đường kh&oacute; nhằn m&agrave; th&uacute; vị&nbsp;</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h2 style=\"text-align: justify;\">THAM GIA NHỮNG LỄ HỘI</h2>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-17.jpg\" alt=\"Điệu m&uacute;a kh&egrave;n da diết ở Kh&acirc;u Vai - Ảnh: Ning Yu Pao\" data-index=\"16\" /></p>\r\n<p style=\"text-align: center;\"><em>Điệu m&uacute;a kh&egrave;n da diết ở Kh&acirc;u Vai - Ảnh: Ning Yu Pao</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">Nhắc đến H&agrave; Giang kh&ocirc;ng thể bỏ qua chợ t&igrave;nh Kh&acirc;u Vại, một trong những lễ hội văn h&oacute;a đắc sắc khiến bao người đắm say trong điệu mua, trong tấm v&aacute;y x&ograve;e của c&ocirc; g&aacute;i v&ugrave;ng cao, hay tiếng kh&egrave;n văng văng n&uacute;i rừng của ch&agrave;ng trai. Mảnh đất của t&igrave;nh y&ecirc;u, của h&ograve; hẹn n&agrave;y đ&atilde; l&agrave;m cho biết bao người kh&aacute;ch l&atilde;ng du phải ngẩn ngơ, bồi hồi. Với sự đa dạng văn h&oacute;a của nhiều d&acirc;n tộc, H&agrave; Giang đ&atilde; trở th&agrave;nh điểm hẹn với nhiều lễ hội v&agrave;o nhiều dịp cố định trong năm.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://mytourcdn.com/upload_images/Image/Location/27_10_2016/11/du-lich-bui-Ha-Giang-mytour-18.jpg\" alt=\"Đ&ecirc;m đến lại bập b&ugrave;ng trong &aacute;nh lửa - Ảnh: Ning Yu Pao\" data-index=\"17\" /></p>\r\n<p style=\"text-align: center;\"><em>Đ&ecirc;m đến lại bập b&ugrave;ng trong &aacute;nh lửa - Ảnh: Ning Yu Pao</em></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">H&agrave; Giang đ&atilde; khiến cho những phượt thủ phải nhớ thương bởi khung cảnh thi&ecirc;n nhi&ecirc;n c&oacute; ch&uacute;t h&ugrave;ng vĩ, c&oacute; ch&uacute;t b&igrave;nh dị, c&oacute; ch&uacute;t mi&ecirc;n man n&ecirc;n thơ. V&agrave; rồi, biết bao nhiều người đến rồi đi, rồi lại đến bởi c&aacute;i t&igrave;nh nồng đậm, chứa chan của con người nơi đ&acirc;y. H&agrave; Giang lu&ocirc;n l&agrave; mảnh đất với nhiều điều hấp dẫn v&agrave; mạo hiểm đang chờ đợi những ai y&ecirc;u th&iacute;ch chinh phục đến kh&aacute;m ph&aacute; v&agrave; trải nghiệm.</p>', '10758523215d088089c7a9c2.90506654.jpg', '2019-06-18', 0),
(46, 30, 'Du lịch đường sông Tây Bắc có gì hấp dẫn?', '<p style=\"text-align: justify;\"><strong>Hầu hết du kh&aacute;ch khi được hỏi về sản phẩm du lịch đặc trưng hoặc c&aacute;c địa danh du lịch nổi tiếng ở v&ugrave;ng cao T&acirc;y Bắc gần như chỉ biết đến thị trấn Sapa, đỉnh Phanxipnag hoặc cao nguy&ecirc;n Mộc Ch&acirc;u. Những nơi tr&ecirc;n đ&uacute;ng l&agrave; điểm du lịch nổi tiếng của khu vực n&agrave;y nhưng T&acirc;y Bắc c&ograve;n c&oacute; nhiều tiềm năng hơn thế nữa, chẳng hạn như về du lịch đường s&ocirc;ng.</strong></p>', '<div id=\"attachment_8680\" class=\"wp-caption aligncenter\" style=\"text-align: justify;\"><img class=\"size-full wp-image-8680\" src=\"http://dulichbui.org/wp-content/uploads/2016/04/thung_nai_2.jpg\" alt=\"Thung Nai - H&ograve;a B&igrave;nh\" width=\"768\" height=\"436\" />\r\n<p class=\"wp-caption-text\">Thung Nai &ndash; H&ograve;a B&igrave;nh</p>\r\n</div>\r\n<p style=\"text-align: justify;\"><strong>Du lịch đường s&ocirc;ng T&acirc;y Bắc c&oacute; khả thi hay kh&ocirc;ng?</strong></p>\r\n<p style=\"text-align: justify;\">Theo con số từ Tổng cục du lịch đưa ra th&igrave; lượng du kh&aacute;ch đến với T&acirc;y Bắc trong năm 2015 khoảng 9 triệu lượt kh&aacute;ch, một con số qu&aacute; khi&ecirc;m tốn so với tiềm năng của v&ugrave;ng đất n&agrave;y. Trong hội thảo Hội thảo về thực trạng &amp; giải ph&aacute;p thu h&uacute;t kh&aacute;ch du lịch đến v&ugrave;ng T&acirc;y Bắc do Ban chỉ đạo T&acirc;y Bắc v&agrave; Tổng cục du lịch phối hợp tổ chức th&igrave; đ&atilde; c&oacute; nhiều &yacute; kiến, giải ph&aacute;p được đưa ra để n&acirc;ng cao chất lượng, dịch vụ du lịch qua đ&oacute; thu h&uacute;t được nhiều du kh&aacute;ch hơn nữa. Trong đ&oacute; &yacute; kiến của &ocirc;ng Trần Thế Dũng được nhiều c&ocirc;ng ty du lịch cũng như c&aacute;c chuy&ecirc;n gi&aacute; đ&aacute;nh gi&aacute; cao đ&oacute; ch&iacute;nh l&agrave; ph&aacute;t triển du lịch đường s&ocirc;ng ở T&acirc;y Bắc.</p>\r\n<p style=\"text-align: justify;\">Thực sự đ&acirc;y l&agrave; một &yacute; tưởng kh&ocirc;ng qu&aacute; mới mẻ, n&oacute; được nhắc đến từ nhiều năm trước nhưng gần như đ&atilde; bị bỏ qua v&agrave; thời gian gần đ&acirc;y, nhất l&agrave; sau đợt hội thảo vừa rồi th&igrave; nhiều người đ&atilde; đồng t&igrave;nh rằng đ&oacute; ch&iacute;nh l&agrave; một trong những giải ph&aacute;p để T&acirc;y Bắc c&oacute; thể hấp dẫn du kh&aacute;ch hơn nữa. Tại sao lại như vậy? Thứ nhất l&agrave; sự thuận lợi về địa h&igrave;nh. V&ugrave;ng n&uacute;i cao T&acirc;y Bắc hầu hết c&aacute;c con s&ocirc;ng đều chạy theo hướng T&acirc;y Nam &ndash; Đ&ocirc;ng Bắc v&agrave; xuối về v&ugrave;ng hạ du, mỗi con s&ocirc;ng đều đi qua nhiều địa phương kh&aacute;c nhau, du kh&aacute;ch c&oacute; thể kết hợp kh&aacute;m ph&aacute; nhiều điểm du lịch trong h&agrave;nh tr&igrave;nh của m&igrave;nh.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\">Thứ hai đ&oacute; ch&iacute;nh l&agrave; sự thuận lợi trong việc di chuyển, c&aacute;c bạn biết rồi đ&oacute; những cung đường ở T&acirc;y Bắc hầu như l&agrave; đ&egrave;o cao, vực s&acirc;u du kh&aacute;ch phải di chuyển những qu&atilde;ng đường rất d&agrave;i v&agrave; mệt mỏi, chưa kể đến vấn đề an to&agrave;n giao th&ocirc;ng, ch&uacute;ng ta c&oacute; thể kết hợp giữa việc đi đường bộ, đường thủy để du kh&aacute;ch c&oacute; thể giảm tải thời gian di chuyển cũng như bớt bị &ldquo;h&agrave;nh x&aacute;c&rdquo; tr&ecirc;n những chuyến xe d&agrave;i ng&agrave;y.</p>\r\n<p style=\"text-align: justify;\"><strong>Những con s&ocirc;ng n&agrave;o c&oacute; thể l&agrave;m được sản phẩm du lịch đường s&ocirc;ng?&nbsp;</strong></p>\r\n<p style=\"text-align: justify;\">Với kinh nghiệm của một người đ&atilde; từng đi khắp c&aacute;c địa phương v&ugrave;ng T&acirc;y Bắc cũng như l&agrave; người ti&ecirc;n phong mở ra c&aacute;c tuyến du lịch độc đ&aacute;o ở khu vực n&agrave;y, &ocirc;ng Trần Thế Dũng đề xuất l&agrave; n&ecirc;n ph&aacute;t triển du lịch đường s&ocirc;ng tr&ecirc;n c&aacute;c con như s&ocirc;ng Đ&agrave;, s&ocirc;ng Năng, s&ocirc;ng G&acirc;m. Những chuyến thủy lộ n&agrave;y d&agrave;i h&agrave;ng trăm km băng qua những địa h&igrave;nh kh&aacute;c nhau, xuy&ecirc;n qua những khu rừng nguy&ecirc;n sinh tuyệt đẹp. Cảnh vật hai b&ecirc;n bờ được v&iacute; như những bức tranh thủy mặc c&oacute; thể l&agrave;m xi&ecirc;u l&ograve;ng bất k&igrave; du kh&aacute;ch kh&oacute; t&iacute;nh n&agrave;o.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\">Chưa hết, sau khi m&atilde;n nh&atilde;n với những cảnh đẹp ven bờ th&igrave; du kh&aacute;ch c&ograve;n được trải nghiệm văn h&oacute;a v&agrave; cuộc sống của đồng b&agrave;o d&acirc;n tộc &iacute;t người sống dọc theo con s&ocirc;ng, ch&iacute;nh nơi đ&oacute; l&agrave; kho t&agrave;ng văn h&oacute;a v&ocirc; gi&aacute; da&agrave;nh cho du kh&aacute;ch, h&atilde;y ph&aacute;t triển du lịch Homestay ở đ&acirc;y, ch&uacute;ng ta vừa c&oacute; sản phẩm du lịch độc đ&aacute;o, vừa mang đến lợi &iacute;ch cho cộng đồng bản địa. Chưa kể những đặc sản tr&ecirc;n s&ocirc;ng với c&aacute;c lo&agrave;i c&aacute; qu&yacute; hiếm sẽ đưa du kh&aacute;ch đi từ bất ngờ n&agrave;y sang bất ngờ kh&aacute;c. Nếu may mắn đi tr&ugrave;ng v&agrave;o những dịp lễ hội của người d&acirc;n bản địa th&igrave; c&ograve;n g&igrave; bằng.</p>\r\n<p style=\"text-align: justify;\">Tại tỉnh H&ograve;a B&igrave;nh c&oacute; một nơi hội tụ đầy đủ những yếu tố thuận lợi để ph&aacute;t triển du lịch đường s&ocirc;ng, đ&oacute; ch&iacute;nh l&agrave; Thung Nai, nơi được v&iacute; như Hạ Long tr&ecirc;n cạn. Nơi đ&acirc;y c&oacute; những h&ograve;n đảo nhỏ nằm nhấp nh&ocirc; tr&ecirc;n một v&ugrave;ng hồ rộng lớn, khung cảnh l&atilde;ng mạn trữ t&igrave;nh đặc biệt l&agrave; trong &aacute;nh ho&agrave;ng h&ocirc;n. Một số người d&acirc;n cũng đ&atilde; bắt đầu tự l&agrave;m du lịch bằng việc cho thu&ecirc; nh&agrave; nghỉ, phục vụ ăn uống, thuyền ngắm cảnh, tham quan tr&ecirc;n hồ. Nếu ch&uacute;ng ta c&oacute; quy hoạch tốt v&agrave; thực hiện đồng bộ th&igrave; Thung Nai sẽ trở th&agrave;nh một nơi ti&ecirc;u biểu của du lịch đường s&ocirc;ng.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\">T&acirc;y Bắc chưa bao giờ hết l&ocirc;i cuốn du kh&aacute;ch khắp mọi miền tổ quốc, chỉ mới nghe đến những c&aacute;i t&ecirc;n như Sapa, Thung Nai, Mộc Ch&acirc;u, Sơn La&hellip;l&agrave; tự thấy bủn rủn hết ch&acirc;n tay, l&agrave; niềm vui sướng khi được một lần đến đ&oacute; trong đời. Chỉ cần T&acirc;y Bắc chịu kh&oacute; thay đổi một ch&uacute;t th&ocirc;i th&igrave; kh&ocirc;ng cần phải k&ecirc;u gọi, du kh&aacute;ch họ sẽ tự h&ocirc; h&agrave;o nhau m&agrave; đến bởi đơn giản v&igrave; trong tim họ đ&atilde; c&oacute; sẵn t&igrave;nh y&ecirc;u d&agrave;nh cho v&ugrave;ng đ&acirc;t n&agrave;y rồi</p>', '2808597795d0890e7e32f87.71881879.jpg', '2019-06-18', 0),
(47, 30, 'Những blog du lịch phượt bạn cần biết', '<p style=\"text-align: justify;\"><strong>Cũng như du lịch bụi, phượt ch&iacute;nh l&agrave; những chuyến đi bụi bặm v&agrave; đầy ngẫu hứng. Đ&ocirc;i khi chẳng c&oacute; lịch tr&igrave;nh sẵn hay điểm đến nhất định. Chỉ l&agrave; c&oacute; l&ograve;ng đam m&ecirc; kh&aacute;m ph&aacute; v&agrave; sự nhiệt huyết m&atilde;nh liệt. Những người đi phượt cũng kh&ocirc;ng cần qu&aacute; nhiều đồ đạc, chỉ cần trang phục gọn g&agrave;ng, một c&aacute;i balo với v&agrave;i thứ cần thiết v&agrave; c&aacute;i m&aacute;y ảnh để lưu lại những khoảnh khắc để đời hay những kỉ niệm kh&oacute; qu&ecirc;n.</strong></p>', '<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img class=\"aligncenter wp-image-32\" src=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/blog-du-lich-phuot.jpg\" sizes=\"(max-width: 600px) 100vw, 600px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2015/11/blog-du-lich-phuot.jpg 642w, http://dandiphuot.com/wp-content/uploads/2015/11/blog-du-lich-phuot-300x116.jpg 300w\" alt=\"blog du lich phuot\" width=\"600\" height=\"232\" /></p>\r\n<p style=\"text-align: justify;\">Tuy rằng bảo đi phượt l&agrave; trải nghiệm, l&agrave; t&igrave;m kiếm c&aacute;i mới nhưng đ&ocirc;i khi c&oacute; những cung đường rất nguy hiểm, kh&oacute; khăn hay những điểm đến hoang sơ, vắng vẻ &iacute;t ai biết. Do vậy bạn cũng n&ecirc;n chuẩn bị cho m&igrave;nh một số th&ocirc;ng tin cơ bản để chuyến đi an to&agrave;n, thuận lợi v&agrave; vui vẻ hơn. Những blog du lịch phượt tiếng Việt bạn cần biết nằm trong danh s&aacute;ch dưới đ&acirc;y ch&iacute;nh l&agrave; nơi tham khảo tuyệt vời d&agrave;nh cho những ai muốn tự m&igrave;nh l&agrave;m chuyến đi phượt t&igrave;m sự mới lạ.</p>\r\n<p style=\"text-align: justify;\"><strong>Dulichbui.org</strong><br />Dulichbui.org l&agrave; một trong những blog du lịch chuy&ecirc;n cung cấp c&aacute;c th&ocirc;ng tin cẩm nang, hướng dẫn d&agrave;nh cho người Việt. Trang web n&agrave;y c&oacute; những b&agrave;i viết li&ecirc;n quan về điểm đến, phương tiện đi lại, điểm tham quan, kh&aacute;ch sạn, nh&agrave; h&agrave;ng, tụ điểm vui chơi giải tr&iacute;, mua sắm,&hellip;C&aacute;c th&ocirc;ng tin đều rất ch&iacute;nh x&aacute;c, hữu &iacute;ch v&agrave; cần thiết đối với những người th&iacute;ch trải nghiệm cảm gi&aacute;c l&agrave;m một chuyến kh&aacute;m ph&aacute; ri&ecirc;ng.<br />B&ecirc;n cạnh đ&oacute;, dulichbui.org lu&ocirc;n cập nhật nhanh ch&oacute;ng những xu hướng du lịch mới, th&ocirc;ng tin khuyến m&atilde;i về dịch vụ, v&eacute; m&aacute;y bay, kh&aacute;ch sạn cũng như kinh nghiệm thực tế của c&aacute;c bạn trẻ đ&atilde; ghi lại được sau chuyến đi bụi của họ. Đ&acirc;y l&agrave; nơi chia sẻ v&agrave; l&agrave; ng&ocirc;i nh&agrave; chung d&agrave;nh cho những ai c&oacute; niềm đam m&ecirc; bất tận với du lịch, lu&ocirc;n muốn tự l&agrave;m mới m&igrave;nh.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\"><strong>Phuotvivu.com</strong><br />Phuotvivu.com l&agrave; một trang blog du lịch chia sẻ c&aacute;c kĩ năng, kinh nghiệm, th&ocirc;ng tin của những chuyến đi bụi m&agrave; người viết đ&atilde; thu thập được từ thực tế. Qua đ&oacute; sẽ gi&uacute;p c&aacute;c bạn trẻ c&oacute; th&ecirc;m kiến thức, c&oacute; sự tự tin hơn để c&oacute; thể tự m&igrave;nh thực hiện một chuyến phượt tự t&uacute;c. Kh&ocirc;ng chỉ những điểm đến trong nước m&agrave; kể cả c&aacute;c nước ở Đ&ocirc;ng Nam &Aacute; như Campuchia, Th&aacute;i Lan, Malaysia,&hellip;cũng đều c&oacute; th&ocirc;ng tin đầy đủ.</p>\r\n<p style=\"text-align: justify;\"><strong>Cuongchan.com</strong><br />Theo chia sẻ của chủ blog cuongchan.com th&igrave; trang web như l&agrave; một nơi để ghi lại những chuyến đi, tương tự như một cuốn nhật k&iacute; online. Tr&ecirc;n đ&oacute; l&agrave; những chia sẻ, kinh nghiệm trong thực tế m&agrave; người viết đ&atilde; trải qua. Bạn c&oacute; thể v&agrave;o xem c&aacute;c b&agrave;i viết để r&uacute;t ra kinh nghiệm cho ch&iacute;nh bản th&acirc;n v&agrave; tự l&agrave;m một chuyến phượt của ri&ecirc;ng m&igrave;nh.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\"><strong>Toidi.net</strong><br />L&agrave; một hướng dẫn vi&ecirc;n du lịch v&agrave; c&oacute; niềm đam m&ecirc; phượt n&ecirc;n Andy Nguyễn đ&atilde; lập ra trang toidi.net với mong muốn tạo ra một cộng đồng. Nơi để mọi người chia sẽ cảm x&uacute;c, kinh nghiệm sau những lần đi phượt v&agrave; c&oacute; thể kết nối với nhau. Trang web n&agrave;y cũng đem lại kh&aacute; nhiều th&ocirc;ng tin về địa điểm, ẩm thực, nơi ở,&hellip; của nhiều điểm đến trong v&agrave; ngo&agrave;i nước. Điều n&agrave;y sẽ cung cấp c&aacute;c th&ocirc;ng tin cần thiết cho những ai muốn tự m&igrave;nh kh&aacute;m ph&aacute; bản th&acirc;n bằng một chuyến đi phượt tự do.</p>\r\n<p style=\"text-align: justify;\"><strong>Thichdibui.blogspot.com</strong><br />Mục đ&iacute;ch thichdibui.blogspot.com ra đời l&agrave; để truyền cảm hứng cho người Việt Nam, đặc biệt l&agrave; những bạn trẻ d&aacute;m nghĩ v&agrave; d&aacute;m l&agrave;m. Những b&agrave;i viết tr&ecirc;n web đều viết theo dạng nhật k&iacute; n&ecirc;n sẽ lồng cảm x&uacute;c thực tế của người viết v&agrave;o, tạo n&ecirc;n sự hứng khởi cho người đọc. Bạn c&oacute; thể bắt gặp được sự đồng điệu, những cảm x&uacute;c ch&acirc;n thật m&agrave; bản th&acirc;n m&igrave;nh đ&atilde; từng trải qua sau mỗi chuyến đi bụi ngay trong những b&agrave;i viết. Ngo&agrave;i ra, trang web cũng chia sẻ một số th&ocirc;ng tin hữu &iacute;ch cho những ai mới lần đầu đi phượt.</p>', '9660188115d0891a2c4a435.48902616.jpg', '2019-06-18', 1),
(48, 30, 'Phượt là gì? Dân phượt là ai?', '<p style=\"text-align: justify;\"><strong>&nbsp;Trong những năm gần đ&acirc;y từ &ldquo;phượt&rdquo; được rất nhiều người sử dụng khi n&oacute;i về một loại h&igrave;nh du lịch (hay l&agrave; một tr&agrave;o lưu du lịch) tự t&uacute;c mang t&iacute;nh trải nghiệm cao. Tuy nhi&ecirc;n bản chất của từ phượt c&oacute; nghĩa l&agrave; g&igrave;? d&acirc;n đi phượt l&agrave; ai? th&igrave; kh&ocirc;ng phải ai cũng hiểu r&otilde;.</strong></p>', '<p style=\"text-align: justify;\">&nbsp;</p>\r\n<div id=\"attachment_9\" class=\"wp-caption aligncenter\" style=\"text-align: justify;\"><img class=\"wp-image-9\" src=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/phuot-la-gi-1024x682.jpg\" sizes=\"(max-width: 550px) 100vw, 550px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2015/11/phuot-la-gi-1024x682.jpg 1024w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-la-gi-300x200.jpg 300w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-la-gi-768x512.jpg 768w\" alt=\"Phượt l&agrave; g&igrave;?\" width=\"550\" height=\"367\" />\r\n<p class=\"wp-caption-text\">Phượt l&agrave; g&igrave;?</p>\r\n</div>\r\n<p style=\"text-align: justify;\"><strong>Ai nghĩ ra từ phượt?</strong><br />Tr&ecirc;n một số diễn đ&agrave;n nhiều th&agrave;nh vi&ecirc;n đưa ra đề t&agrave;i &ldquo;phượt l&agrave; g&igrave;?&rdquo; để b&agrave;n t&aacute;n, c&oacute; nhiều định nghĩa được đưa ra, cũng c&oacute; những th&agrave;nh vi&ecirc;n cho rằng kh&ocirc;ng n&ecirc;n định nghĩa v&igrave; điều n&agrave;y sẽ l&agrave;m mất đi gi&aacute; trị của n&oacute; hay l&agrave;m mất đi &yacute; nghĩa của từ n&agrave;y.<br />T&igrave;m hiểu về nguồn gốc của từ &ldquo;phượt&rdquo; người viết được biết rằng chữ &ldquo;phượt&rdquo; được xuất hiện lần đầu trong tập truyện ngắn Me T&acirc;y của t&aacute;c giả Do&atilde;n Dũng &ndash; một &ldquo;l&atilde;o l&agrave;ng&rdquo; trong giới du lịch tự t&uacute;c. T&aacute;c giả Do&atilde;n Dũng được biết đến tr&ecirc;n c&aacute;c diễn đ&agrave;n du lịch với nick l&agrave; Cao Sơn. C&oacute; thể n&oacute;i ch&iacute;nh Do&atilde;n Dũng l&agrave; cha đẻ của từ n&agrave;y.</p>\r\n<p style=\"text-align: justify;\"><strong>Phượt l&agrave; g&igrave;?</strong><br />Tuy l&agrave; người khai sinh ra từ &ldquo;phượt&rdquo; nhưng khi đi được hỏi &ldquo;phượt l&agrave; g&igrave;&rdquo; th&igrave; ch&iacute;nh t&aacute;c giả Do&atilde;n Dũng lại trả lời rất mơ hồ &ldquo;Trong cuộc sống c&oacute; rất nhiều từ mới xuất hiện một c&aacute;ch ngẫu nhi&ecirc;n. Anh bắt tui giải th&iacute;ch nguy&ecirc;n cớ từ phượt th&igrave; cũng như bắt con g&agrave; phải ph&acirc;n t&iacute;ch th&agrave;nh phần cấu tạo quả trứng n&oacute; vừa đẻ ra. Kh&oacute; lắm!&rdquo;<br />Vậy ngay cả ch&iacute;nh t&aacute;c giả cũng chưa r&otilde; lắm về từ phượt n&ecirc;n việc tranh c&atilde;i hay cố gắng đưa ra kh&aacute;i niệm cho từ &ldquo;phượt&rdquo; thực ra cũng c&oacute; thể xem l&agrave; kh&ocirc;ng cần thiết hoặc nh&igrave;n theo một g&oacute;c độ kh&aacute;c th&igrave; định nghĩa của từ &ldquo;phượt&rdquo; ng&agrave;y xưa v&agrave; nay c&oacute; thể đ&atilde; kh&aacute;c.</p>\r\n<div class=\"google-auto-placed ap_container\" style=\"text-align: justify;\">&nbsp;</div>\r\n<p style=\"text-align: justify;\"><strong>D&acirc;n phượt l&agrave; ai?</strong><br />C&oacute; một số bạn trẻ nghĩ rằng việc x&aacute;ch xe l&ecirc;n v&agrave; đi l&agrave; m&igrave;nh trở th&agrave;nh d&acirc;n phượt, cứ đi nhiều l&agrave; th&agrave;nh d&acirc;n phượt ch&iacute;nh hiệu m&agrave; qu&ecirc;n mất rằng &ldquo;chiếc &aacute;o kh&ocirc;ng l&agrave;m n&ecirc;n thầy tu&rdquo;.<br />Với những người đi du lịch từ phượt c&oacute; g&igrave; đ&oacute; rất thi&ecirc;ng li&ecirc;ng v&agrave; những bạn trẻ như đ&atilde; n&oacute;i ở tr&ecirc;n kh&ocirc;ng đại diện cho &ldquo;d&acirc;n phượt&rdquo;. C&aacute;i m&agrave; d&acirc;n phượt tự h&agrave;o c&oacute; lẽ đ&oacute; l&agrave; trải nghiệm v&agrave; tr&aacute;ch nhiệm, trải nghiệm mọi thứ tr&ecirc;n đường đi từ những cung đường, từ những người m&igrave;nh gặp, từ c&aacute;c điểm dừng ch&acirc;n, từ những điều nhỏ nhất cho đến trải nghiệm sau khi chuyến đi kết th&uacute;c; tr&aacute;ch nhiệm của một người du kh&aacute;ch biết trải nghiệm, lấy g&igrave; v&agrave; để lại g&igrave; tại mỗi điểm đến, tr&aacute;ch nhiệm lan tỏa niềm cảm hứng cho những người kh&aacute;c,&hellip;</p>\r\n<p style=\"text-align: justify;\"><img class=\"aligncenter wp-image-10\" src=\"http://localhost:8080/newddp/wp-content/uploads/2015/11/phuot.jpg\" sizes=\"(max-width: 550px) 100vw, 550px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2015/11/phuot.jpg 800w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-150x150.jpg 150w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-300x300.jpg 300w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-768x768.jpg 768w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-160x160.jpg 160w, http://dandiphuot.com/wp-content/uploads/2015/11/phuot-320x320.jpg 320w\" alt=\"phuot\" width=\"550\" height=\"550\" /></p>\r\n<p style=\"text-align: justify;\"><strong>T&oacute;m lại việc định nghĩa phượt l&agrave; g&igrave;, d&acirc;n đi phượt l&agrave; ai thực sự l&agrave; kh&ocirc;ng cần thiết bởi lẽ mỗi một du kh&aacute;ch c&oacute; g&igrave; tr&ecirc;n mỗi chuyến đi l&agrave; điều quan trọng hơn cả, quan trọng hơn cả việc bạn đ&atilde; đi bao nhi&ecirc;u nơi, bạn đi với ai, bạn đi thế n&agrave;o.</strong></p>', '4624063245d08928641c502.07444136.jpg', '2019-06-18', 1),
(49, 28, 'Chia sẻ Top 8 địa điểm du lịch và kinh nghiệm đi phượt miền bắc mùa hè', '<p style=\"text-align: justify;\">Địa diểm du lịch miền Bắc c&oacute; những nơi n&agrave;o nhiều người thường đi, những nơi n&agrave;o nổi tiếng của thắng cảnh Vietnam. B&ecirc;n dưới đ&acirc;y l&agrave; những địa điểm m&agrave; nhiều người đ&atilde; chứng thực v&agrave; đ&aacute;nh gi&aacute; l&agrave; đ&aacute;ng để đến khi đi du lịch ở Miền Bắc.&nbsp; D&ugrave; đến bất kỳ địa điểm n&agrave;o, th&igrave; ch&uacute;ng ta cũng cần tham khảo&nbsp;kinh nghiệm của người đi trước v&agrave; những vật dụng cần thiết cũng như những lưu &yacute; cần thiết khi đi du lịch. Trong b&agrave;i viết dưới đ&acirc;y l&agrave; những kinh nghiệm đi phượt để gi&uacute;p bạn hiểu s&acirc;u hơn về vấn đề n&agrave;y.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', '<p>Những địa điểm du lịch miền Bắc m&ugrave;a h&egrave; nổi tiếng</p>\r\n<p><img src=\"asset/img/images/13.jpg\" alt=\"\" /><img src=\"assets\\img\\images\\13.jpg\" alt=\"\" /></p>\r\n<p>Cao nguy&ecirc;n Mộc Ch&acirc;u (huyện Mộc Ch&acirc;u, tỉnh Sơn La) c&oacute; độ cao trung b&igrave;nh 1.050 m so với mực nước biển, mang đặc trưng của kh&iacute; hậu cận &ocirc;n đới. Với rất nhiều khung cảnh đẹp của c&aacute;c đồi ch&egrave;, n&ocirc;ng trường, rừng n&uacute;i v&agrave; s&ocirc;ng suối&hellip; đ&acirc;y l&agrave; &ldquo;địa chỉ đỏ&rdquo; d&agrave;nh cho d&acirc;n &ldquo;phượt&rdquo; cũng như những người m&ecirc; nhiếp ảnh ở H&agrave; Nội v&agrave; c&aacute;c tỉnh miền Bắc Việt Nam.</p>\r\n<p>Địa điểm phượt miền Bắc m&ugrave;a h&egrave; 2&nbsp;&ndash; Hai ng&agrave;y với Tam Đảo</p>\r\n<p>Tam Đảo l&agrave; một thị trấn nhỏ thuộc huyện Tam Đảo, c&aacute;ch H&agrave; Nội khoảng 80km. Nơi đ&acirc;y được người Ph&aacute;p ph&aacute;t hiện v&agrave;o cuối thế kỷ 19 đầu thế kỷ 20. Tất cả 200 biệt thự nằm trong khu nghỉ m&aacute;t bao gồm cả kh&aacute;ch sạn, nh&agrave; h&agrave;ng, s&acirc;n chơi thể thao, bể bơi, s&agrave;n nhảy đều được người Ph&aacute;p x&acirc;y dựng từ thời đ&oacute; n&ecirc;n những c&ocirc;ng tr&igrave;nh ở đ&acirc;y đều mang hơi hướng kiến tr&uacute;c Ph&aacute;p, mặc d&ugrave; số lượng c&ograve;n lại kh&ocirc;ng nhiều, tuy nhi&ecirc;n cũng đủ để tạo n&ecirc;n một thị trấn Tam Đảo rất ri&ecirc;ng.</p>\r\n<div class=\"fb-quote fb_iframe_widget\">&nbsp;</div>\r\n<div id=\"attachment_10964\" class=\"wp-caption aligncenter\"><img class=\"wp-image-10964\" src=\"https://vntrip.cdn.vccloud.vn/cam-nang/wp-content/uploads/2017/07/tam-dao-1.jpg\" sizes=\"(max-width: 600px) 100vw, 600px\" srcset=\"https://vntrip.cdn.vccloud.vn/cam-nang/wp-content/uploads/2017/07/tam-dao-1.jpg 1024w, https://vntrip.cdn.vccloud.vn/cam-nang/wp-content/uploads/2017/07/tam-dao-1-768x431.jpg 768w\" alt=\"phong cảnh thị trấn tam đảo\" width=\"600\" height=\"337\" />\r\n<p class=\"wp-caption-text\">Phong cảnh thị trấn Tam Đảo (ảnh sưu tầm)</p>\r\n</div>\r\n<p>Tiết trời ở Tam Đảo m&aacute;t mẻ quanh năm, c&oacute; thể cảm nhận được 4 m&ugrave;a trong một ng&agrave;y, c&oacute; lẽ bởi thế m&agrave; rất nhiều bạn trẻ muốn đến đ&acirc;y du lịch v&agrave;o những dịp cuối tuần. Khung cảnh thi&ecirc;n nhi&ecirc;n v&ocirc; c&ugrave;ng thơ mộng nhưng cũng kh&ocirc;ng k&eacute;m phần h&ugrave;ng vĩ. S&aacute;ng sớm, l&agrave;n sương kh&oacute;i trắng xo&aacute; bao quanh cả thị trấn, kh&ocirc;ng kh&iacute; trong l&agrave;nh lại hơi se lạnh đem đến một cảm gi&aacute;c thư th&aacute;i đến k&igrave; lạ. Thi&ecirc;n nhi&ecirc;n với những dấu vết thời gian đ&atilde; ban tặng cho nơi đ&acirc;y một khung cảnh tuyệt vời: vừa thơ mộng, u tịch, vừa h&ugrave;ng vĩ, huyền ảo trong m&acirc;y trời, sương kh&oacute;i.</p>\r\n<p>Địa điểm phượt miền Bắc m&ugrave;a h&egrave; 3 &ndash; Ba V&igrave;</p>\r\n<p>C&aacute;ch trung t&acirc;m H&agrave; Nội50 km, vườn quốc gia Ba V&igrave; chạy dọc theo hướng Đ&ocirc;ng Bắc &ndash; T&acirc;y Nam của d&atilde;y n&uacute;i Ba V&igrave; với đỉnh Tản Vi&ecirc;n cao 1.296m, đỉnh Ngọc Hoa cao 1.120m. Từ đầu thế kỉ 20, Ba V&igrave; đ&atilde; l&agrave; địa danh nổi tiếng nhờ sự đa dạng của c&aacute;c hệ sinh th&aacute;i v&agrave; c&oacute; phong cảnh đẹp, kh&iacute; hậu m&aacute;t mẻ.</p>', '130983825d08b019400114.71960367.jpg', '2019-06-18', 0),
(50, 25, '7 lời khuyên hữu ích khi đi du lịch bạn nên biết', '<h2>&nbsp;</h2>', '<h2 style=\"padding-left: 30px; text-align: justify;\"><img class=\"wp-image-1093 size-full aligncenter\" title=\"7 lời khuy&ecirc;n hữu &iacute;ch trong khi đi du lịch\" src=\"https://hotel81.xanhha.vn/wp-content/uploads/2017/03/7-loi-khuyen-huu-ich-khi-di-du-lich.jpg\" sizes=\"(max-width: 650px) 100vw, 650px\" srcset=\"https://hotel81.xanhha.vn/wp-content/uploads/2017/03/7-loi-khuyen-huu-ich-khi-di-du-lich.jpg 650w, https://hotel81.xanhha.vn/wp-content/uploads/2017/03/7-loi-khuyen-huu-ich-khi-di-du-lich-300x204.jpg 300w\" alt=\"7 lời khuy&ecirc;n hữu &iacute;ch trong khi đi du lịch\" width=\"650\" height=\"441\" /></h2>\r\n<p style=\"padding-left: 30px; text-align: justify;\">1. Bạn n&ecirc;n chủ động ch&agrave;o hỏi v&agrave; tỏ ra th&acirc;n thiện với c&aacute;c du kh&aacute;ch c&ugrave;ng chuyến đi. Việc l&agrave;m n&agrave;y c&oacute; thể mang lại cho bạn sự gi&uacute;p đỡ của những người xung quanh khi cần.</p>\r\n<p style=\"padding-left: 30px; text-align: justify;\">2. H&atilde;y lu&ocirc;n sử dụng taxi hoặc xe bu&yacute;t l&agrave;m phương tiện di chuyển thắng tới nơi nghỉ ch&acirc;n. Chẳng ai d&aacute;m chắc về những nguy hiểm bạn c&oacute; thể gặp phải tr&ecirc;n đoạn đường từ xe bu&yacute;t đến kh&aacute;ch sạn.</p>\r\n<p style=\"padding-left: 30px; text-align: justify;\">3. H&iacute;t thở thật s&acirc;u khi ngồi tr&ecirc;n xe để tr&aacute;nh bị say xe. Ngo&agrave;i ra bạn cũng c&oacute; mang theo vỏ qu&yacute;t để ngửi hoặc uống thuốc chống say trước khi l&ecirc;n xe di chuyển.</p>\r\n<p style=\"padding-left: 30px; text-align: justify;\">4. Tr&aacute;nh &ldquo;ph&ocirc; trương&rdquo; những m&oacute;n đồ bạn c&oacute; như v&iacute; tiền, điện thoại, m&aacute;y ảnh, trang sức,&hellip; bởi kẻ trộm lu&ocirc;n r&igrave;nh rập xung quanh chỉ &ldquo;th&oacute;&rdquo; những g&igrave; ch&uacute;ng thấy. V&iacute; dụ đơn giản như: đi dạo phố v&agrave; chụp ảnh, bạn n&ecirc;n cất m&aacute;y ảnh đi khi kh&ocirc;ng d&ugrave;ng n&oacute;.</p>\r\n<p style=\"padding-left: 30px; text-align: justify;\">5. Chỉ cầm theo bản sao hộ chiếu khi ra khỏi kh&aacute;ch sạn. Một v&agrave;i điểm vui chơi giải tr&iacute; sẽ y&ecirc;u cầu xuất tr&igrave;nh hộ chiếu nhưng bản sao cũng ho&agrave;n to&agrave;n được chấp nhận.</p>\r\n<p style=\"padding-left: 30px; text-align: justify;\">6. Nhớ mang theo chuyến đi một v&agrave;i ổ kh&oacute;a nhỏ. Ch&uacute;ng sẽ ph&aacute;t huy hiệu quả trong trường hợp c&oacute; kẻ xấu đột nhập v&agrave;o ph&ograve;ng.</p>\r\n<p style=\"padding-left: 30px; text-align: justify;\">7. Đừng cố tỏ ra bạn gi&agrave;u từ quần &aacute;o đến t&uacute;i x&aacute;ch hay việc make up kĩ c&agrave;ng bởi ch&uacute;ng sẽ mang nguy hiểm đến cho bạn. Mua một bộ quần &aacute;o bản địa như qu&agrave; kỷ niệm v&agrave; mặc khi ra ngo&agrave;i l&agrave; một c&aacute;ch l&agrave;m m&agrave; bạn c&oacute; thể tham khảo.</p>\r\n<p style=\"padding-left: 30px; text-align: justify;\">Những trải nghiệm sau mỗi chuyến du lịch sẽ gi&uacute;p cho bạn c&oacute; th&ecirc;m nhiều kinh nghiệm v&agrave; l&agrave;m phong ph&uacute; th&ecirc;m cho vốn sống. H&atilde;y lu&ocirc;n t&ocirc;n trọng những người xung quanh bạn v&agrave; giữ mối quan hệ vừa phải với những du kh&aacute;ch kh&aacute;c sẽ mang lại nhiều lợi &iacute;ch cho chuyến du lịch của bạn.</p>', '14790235065d090d29eff323.56746301.jpg', '2019-06-18', 1);
INSERT INTO `tintuc` (`tintuc_id`, `danhmuc_id`, `tintuc_ten`, `tintuc_mota`, `tintuc_noidung`, `tintuc_anh`, `tintuc_ngaytao`, `tintuc_hot`) VALUES
(51, 28, 'Kinh nghiệm cho những người lần đầu đi Phượt', '<p style=\"text-align: justify;\">Trong một v&agrave;i năm trở lại đ&acirc;y phượt đ&atilde; trở th&agrave;nh một điều m&agrave; mọi người phải n&oacute;i đến l&agrave; phong tr&agrave;o, ai cũng c&oacute; thể tự tổ chức những chuyến đi, l&ocirc;i k&eacute;o bạn b&egrave; tham gia. Tuy nhi&ecirc;n, bản th&acirc;n phượt l&agrave; một h&igrave;nh thức du lịch mạo hiểm m&agrave; kh&ocirc;ng phải ai cũng đủ kỹ năng v&agrave; sức khỏe để c&oacute; thể ĐẢM BẢO AN TO&Agrave;N cho m&igrave;nh v&agrave; bạn b&egrave; c&ugrave;ng đi. Phượt kh&ocirc;ng kh&oacute;, nhưng PHƯỢT AN TO&Agrave;N kh&ocirc;ng phải ai cũng c&oacute; thể thực hiện được,&nbsp; bạn đ&atilde; c&oacute; đủ những kỹ năng v&agrave; kinh nghiệm cần thiết trước mỗi chuyến đi chưa?</p>', '<p style=\"text-align: justify;\"><a href=\"http://3.bp.blogspot.com/-BScGTvwMxBw/UqgLxcWBA3I/AAAAAAAAD4I/t0Gtylts25E/s1600/DSC_0577.JPG\"><img class=\"alignnone lazyloaded\" src=\"http://3.bp.blogspot.com/-BScGTvwMxBw/UqgLxcWBA3I/AAAAAAAAD4I/t0Gtylts25E/s1600/DSC_0577.JPG\" alt=\"lần đầu đi phượt\" width=\"640\" height=\"378\" border=\"0\" data-lazy-src=\"http://3.bp.blogspot.com/-BScGTvwMxBw/UqgLxcWBA3I/AAAAAAAAD4I/t0Gtylts25E/s1600/DSC_0577.JPG\" data-was-processed=\"true\" /></a></p>\r\n<p style=\"text-align: justify;\">B&agrave;i viết thể hiện quan điểm ri&ecirc;ng của t&aacute;c giả, c&oacute; nhiều th&ocirc;ng tin c&oacute; thể chưa đ&uacute;ng ho&agrave;n to&agrave;n 100% trong tất cả mọi trường hợp, nhưng tất cả những g&igrave; C&ugrave;ng Phượt muốn n&oacute;i với c&aacute;c bạn lần đầu đi phượt l&agrave;&nbsp;<em>&ldquo;H&atilde;y thật cẩn thận trong mỗi chuyến đi, cơ hội để đi th&igrave; bạn c&oacute; nhiều nhưng t&iacute;nh mạng th&igrave; bạn chỉ c&oacute; một. Đi cẩn thận v&agrave; phải lu&ocirc;n an to&agrave;n, đi được th&igrave; nhớ phải về được&rdquo;</em></p>\r\n<p style=\"text-align: justify;\"><strong>Một v&agrave;i th&ocirc;ng tin cảnh b&aacute;o về việc mất an to&agrave;n tr&ecirc;n đường phượt</strong></p>\r\n<p class=\"caps\" style=\"text-align: justify;\">&ndash;&nbsp;<strong>Th&aacute;ng 09/2007</strong>: Bạn Minh Ngọc (nh&agrave; F50) ko bao giờ trở về tại khu vực th&aacute;c Bản Giốc &ndash; Cao Bằng.<br />&ndash;&nbsp;<strong>Th&aacute;ng 02/2009</strong>: 2 bạn nh&agrave; Kỳ Diệu gặp nạn tr&ecirc;n đường đi Bắc Kạn, Bạn H&agrave; v&agrave; 1 người nữa ra đi, c&ograve;n bạn Yến chấn thương nặng phải gi&agrave;nh giật sự sống nhiều ng&agrave;y tại Việt Đức<br />&ndash;&nbsp;<strong>Th&aacute;ng 10/2009</strong>: Bạn Huy Việt (nh&agrave; Fan7) ra đi tr&ecirc;n d&ograve;ng s&ocirc;ng Hồng trong chuyến đi l&agrave;ng Cổ Đ&ocirc; &ndash; Ba V&igrave;.<br />&ndash;&nbsp;<strong>Th&aacute;ng 09/2010</strong>: Trong chuyến đi T&acirc;y Bắc 2 bạn Hiền v&agrave; Nguy&ecirc;n (nh&agrave; D&igrave;m H&agrave;ng Pro) xảy ch&acirc;n rớt xuống suối, bị cuốn xuống ch&acirc;n th&aacute;c tại khu vực Than Uy&ecirc;n.<br />&ndash;&nbsp;<strong>Th&aacute;ng 11/2010</strong>: &nbsp;Tại địa phận Cao Phong tr&ecirc;n đường đi Mộc Ch&acirc;u 2 bạn nh&agrave; Trekkingfan gặp tai nạn, bạn xế bị chấn thương, c&ograve;n bạn Nhung đ&atilde; ko qua khỏi.<br />&ndash;&nbsp;<strong>Th&aacute;ng 10/2011</strong>: Bạn Ch&acirc;u (VietClimp), một th&agrave;nh vi&ecirc;n du lịch nhiều kinh nghiệm đ&atilde; ra đi tr&ecirc;n đường đi C&uacute;c Phương tham dự đại hội box Du lịch.<br />&ndash;&nbsp;<strong>Th&aacute;ng 07/2012</strong>: bạn Hanamichi.tron qua đời v&igrave; kiệt sức tr&ecirc;n đường trek Cực Đ&ocirc;ng (Mũi Đ&ocirc;i, Vạn Ninh, Kh&aacute;nh H&ograve;a).<br />&ndash;&nbsp;<strong>Th&aacute;ng 10/2012</strong>: Bạn toancbr (Nh&agrave; Nesat) gặp nạn tr&ecirc;n đường từ Ph&uacute; Thọ về H&agrave; Nội.<br />&ndash;&nbsp;<strong>Th&aacute;ng 07/2013</strong>: Trong 1 chuyến leo Fansipan bạn Ngọc &Aacute;nh tự &yacute; t&aacute;ch đo&agrave;n trong khi di chuyển từ 2800m về Trạm T&ocirc;n v&agrave; mất t&iacute;ch cho đến nay.<br />&ndash;&nbsp;<strong>Th&aacute;ng 08/2013</strong>: Anh Chu Hồng Đăng (Cheetah), kẻ hộ mệnh tr&ecirc;n đường của d&acirc;n du lịch bụi đ&atilde; về 1 miền xa tr&ecirc;n đường từ T&uacute; Lệ về H&agrave; Nội.<br />&ndash;&nbsp;<strong>Th&aacute;ng 12/2013</strong>: Tại địa phận Lương Sơn &ndash; H&ograve;a B&igrave;nh, 1 xe trong đo&agrave;n phượt 60 người gặp nạn, bạn Thu Hiền ra đi ngay lần đầu ti&ecirc;n đi phượt, bạn xế chấn thương sọ n&atilde;o.</p>\r\n<p style=\"text-align: justify;\"><em>Faith Ttvn &ndash; Admin F233</em></p>\r\n<p style=\"text-align: justify;\"><strong>NẾU BẠN L&Agrave; TH&Agrave;NH VI&Ecirc;N THAM GIA</strong></p>\r\n<p style=\"text-align: justify;\"><a href=\"http://2.bp.blogspot.com/-av2uGpo8knA/UqgJ5_FHNgI/AAAAAAAAD30/8oHYX0Pl6No/s1600/IMG_0103.JPG\"><img class=\"lazyloaded\" src=\"http://2.bp.blogspot.com/-av2uGpo8knA/UqgJ5_FHNgI/AAAAAAAAD30/8oHYX0Pl6No/s1600/IMG_0103.JPG\" alt=\"\" width=\"641\" height=\"417\" border=\"0\" data-lazy-src=\"http://2.bp.blogspot.com/-av2uGpo8knA/UqgJ5_FHNgI/AAAAAAAAD30/8oHYX0Pl6No/s1600/IMG_0103.JPG\" data-was-processed=\"true\" /></a></p>\r\n<ul>\r\n<li>Kiểm tra th&ocirc;ng tin về người leader của m&igrave;nh, đ&acirc;y l&agrave; người dẫn dắt v&agrave; đảm bảo an to&agrave;n cho h&agrave;nh tr&igrave;nh của cả nh&oacute;m, nếu leader của bạn l&agrave; một người mới, chưa c&oacute; nhiều kinh nghiệm, h&atilde;y c&acirc;n nhắc về khả năng tham gia chuyến đi.</li>\r\n<li>Nếu chưa từng đi xe m&aacute;y đường d&agrave;i với qu&atilde;ng đường &gt; 100km v&agrave; thời gian l&acirc;u hơn 3h, h&atilde;y c&acirc;n nhắc lại về việc tham gia. Đi xe m&aacute;y với qu&atilde;ng đường lớn v&agrave; thời gian d&agrave;i lu&ocirc;n đ&ograve;i hỏi bạn đảm bảo những yếu tố sức khỏe vững v&agrave; khả năng bền bỉ cao.</li>\r\n<li>Nếu trong đo&agrave;n nhiều th&agrave;nh vi&ecirc;n mới như bạn , h&atilde;y suy nghĩ kỹ. Một người leader d&ugrave; c&oacute; giỏi đến mấy cũng kh&ocirc;ng thể ho&agrave;n to&agrave;n l&agrave; người đảm bảo an to&agrave;n hết cho qu&aacute; nhiều th&agrave;nh vi&ecirc;n chưa c&oacute; kinh nghiệm.</li>\r\n<li>Nếu số xe nhiều hơn 6, h&atilde;y suy nghĩ lại. Nếu bỏ qua hết những l&yacute; do tr&ecirc;n v&agrave; vẫn muốn tham gia, đọc tiếp một v&agrave;i lời khuy&ecirc;n tiếp theo ph&iacute;a dưới</li>\r\n<li>Tự kiểm tra v&agrave; bảo dưỡng xe to&agrave;n bộ, thay lốp, phanh nếu đ&atilde; qu&aacute; m&ograve;n (chủ động l&agrave;m bởi c&oacute; những leader sẽ kh&ocirc;ng nhắc bạn)</li>\r\n<li>Tự trang bị đồ bảo hộ cho m&igrave;nh, mũ bảo hiểm &iacute;t nhất l&agrave; loại nửa đầu c&oacute; k&iacute;nh chắn gi&oacute; v&agrave; phải c&oacute; chất lượng tốt, bộ bọc khuỷu tay, đầu gối, găng tay &hellip; tất cả những thứ n&agrave;y bạn đều c&oacute; thể dễ d&agrave;ng t&igrave;m mua. Hạn chế mức độ nguy hiểm từ những việc nhỏ nhất.</li>\r\n<li>Đọc kỹ h&agrave;nh tr&igrave;nh của to&agrave;n bộ chuyến đi, nếu trong to&agrave;n bộ c&aacute;c ng&agrave;y đi bạn đều phải di chuyển &gt;200km một ng&agrave;y, h&atilde;y suy nghĩ lại. Đi 200km xe m&aacute;y tưởng chừng như đơn giản nhưng lại v&ocirc; c&ugrave;ng vất vả đấy nh&eacute;.</li>\r\n<li>Đừng chạy qu&aacute; 50km/h v&agrave;o ban ng&agrave;y v&agrave; 40km/h v&agrave;o ban đ&ecirc;m. Đi 200km với tốc độ 50km/h bạn sẽ mất 4 tiếng, chạy tốc độ cao hơn c&oacute; thể bạn mất 3 tiếng, nhưng đừng tham 1 tiếng để phải hối hận cả đời. Buổi tối, tầm nh&igrave;n bị hạn chế, chạy tốc độ cao bạn sẽ kh&ocirc;ng thể nh&igrave;n thấy v&agrave; tr&aacute;nh d&ugrave; chỉ xuất hiện 1 vi&ecirc;n đ&aacute; ở tr&ecirc;n đường, đi phượt như thế cũng đ&atilde; qu&aacute; nguy hiểm.</li>\r\n<li>Chưa c&oacute; kinh nghiệm, đừng tham gia những chuyến phải chạy đ&ecirc;m. Buổi đ&ecirc;m thường l&agrave; giờ chạy của c&aacute;c loại xe tải trọng lớn, xe container, xe kh&aacute;ch &hellip; &aacute;nh đ&egrave;n pha của xe v&ocirc; c&ugrave;ng s&aacute;ng, c&aacute;nh l&aacute;i xe cũng kh&ocirc;ng nhiều người c&oacute; c&aacute;i t&acirc;m m&agrave; hạ pha để tr&aacute;nh cho những người đi xe m&aacute;y ngược chiều đ&acirc;u thế n&ecirc;n chạy xe trong điều kiện tầm nh&igrave;n của bạn bị cản như thế v&ocirc; c&ugrave;ng nguy hiểm. Những chiếc xe lớn như thế cũng lu&ocirc;n chạy với tốc độ rất cao v&agrave;o ban đ&ecirc;m, bạn c&oacute; muốn tự đẩy m&igrave;nh v&agrave;o ho&agrave;n cảnh nguy hiểm đ&oacute; ?</li>\r\n<li>Ở th&agrave;nh phố, d&ugrave;ng c&ograve;i khiến người d&acirc;n kh&oacute; chịu nhưng tr&ecirc;n đường phượt h&atilde;y tập th&oacute;i quen sử dụng c&ograve;i xe, v&agrave;o kh&uacute;c cua h&atilde;y bấm c&ograve;i b&aacute;o hiệu cho xe ngược chiều, v&agrave;o khu vực đ&ocirc;ng d&acirc;n h&atilde;y bấm c&ograve;i b&aacute;o hiệu cho người d&acirc;n</li>\r\n<li>Kh&ocirc;ng biết bơi th&igrave; tr&aacute;nh xa khu vực s&ocirc;ng suối, nếu trong chuyến đi c&oacute; lịch tr&igrave;nh phải di chuyển bằng b&egrave;, mảng, thuyền &hellip; th&igrave; tự trang bị th&ecirc;m v&agrave;o h&agrave;nh l&yacute; của m&igrave;nh 1 c&aacute;i &aacute;o phao đi.</li>\r\n<li>Cố gắng nhớ c&aacute;c loại biển b&aacute;o nguy hiểm, những biển b&aacute;o n&agrave;y v&ocirc; c&ugrave;ng quan trọng v&agrave; sẽ gi&uacute;p đỡ bạn rất nhiều khi cần xử l&yacute; t&igrave;nh huống tr&ecirc;n đường đ&oacute;.</li>\r\n</ul>\r\n<ul>\r\n<li>H&atilde;y đặt chữ&nbsp;<strong>T&Acirc;M</strong>&nbsp;l&ecirc;n tr&ecirc;n chữ&nbsp;<strong>T&Ocirc;I</strong>. Bạn tổ chức đo&agrave;n &iacute;t người, đảm bảo cho mọi th&agrave;nh vi&ecirc;n c&oacute; một chuyến đi vui vẻ nhưng vẫn an to&agrave;n, đ&oacute; l&agrave; c&aacute;i T&Acirc;M. Tổ chức một đo&agrave;n đ&ocirc;ng người, k&eacute;o đi để tạo th&agrave;nh t&iacute;ch ri&ecirc;ng cho bản th&acirc;n m&igrave;nh trong việc dẫn dắt một số lượng lớn người đi phượt, đ&oacute; l&agrave; c&aacute;i T&Ocirc;I. Đừng bắt chước người kh&aacute;c l&agrave;m leader tổ chức một chuyến đi nếu bạn kh&ocirc;ng c&oacute; đủ kinh nghiệm v&agrave; kỹ năng cần thiết, nếu bạn vẫn l&agrave;m th&igrave; đ&oacute; l&agrave; c&aacute;i T&Ocirc;I</li>\r\n<li>Đừng đứng ra tổ chức chuyến đi nếu bạn chưa c&oacute; đủ kinh nghiệm l&agrave;m người quản l&yacute;, kinh nghiệm xử l&yacute; c&aacute;c t&igrave;nh huống ph&aacute;t sinh, kinh nghiệm đi đường &hellip;. Trong mọi trường hợp xảy ra, bạn sẽ l&agrave; người chịu ho&agrave;n to&agrave;n tr&aacute;ch nhiệm về sự an to&agrave;n của tất cả th&agrave;nh vi&ecirc;n đo&agrave;n bạn. Nếu vẫn muốn l&agrave; người tổ chức, h&atilde;y đọc th&ecirc;m một số lời khuy&ecirc;n ph&iacute;a dưới.</li>\r\n<li>Đừng tổ chức qu&aacute; 6 xe, 4 xe cho một đo&agrave;n chưa c&oacute; nhiều kinh nghiệm l&agrave; hợp l&yacute;, c&aacute;c bạn sẽ dễ hỗ trợ nhau hơn, sắp xếp ph&ograve;ng nghỉ v&agrave; ăn uống cũng đơn giản v&agrave; gọn nhẹ hơn</li>\r\n<li>Lập lịch tr&igrave;nh thật kỹ, chi tiết đến từng km cần đi, từng điểm đến cần dừng &hellip; để c&aacute;c th&agrave;nh vi&ecirc;n nắm r&otilde; ngay từ khi c&ograve;n ở nh&agrave; để họ c&oacute; thể h&igrave;nh dung về những g&igrave; sắp diễn ra.</li>\r\n<li>Thiết lập kỷ luật chặt cho chuyến đi của m&igrave;nh, h&atilde;y y&ecirc;u cầu ngay từ đầu c&aacute;c th&agrave;nh vi&ecirc;n phải tu&acirc;n theo quy định của đo&agrave;n v&agrave; leader (tất nhi&ecirc;n với điều kiện những quy định đ&oacute; được đảm bảo l&agrave; đ&uacute;ng đắn v&agrave; hợp l&yacute;), nếu c&oacute; một th&agrave;nh vi&ecirc;n n&agrave;o đ&oacute; phản đối những y&ecirc;u cầu n&agrave;y, th&agrave;nh vi&ecirc;n đ&oacute; c&oacute; thể kh&ocirc;ng tham gia chuyến đi.</li>\r\n<li>Nếu c&oacute; bất đồng tr&ecirc;n đường đi, h&atilde;y đối thoại v&agrave; thảo luận, đừng &aacute;p đặt những điều m&agrave; chỉ một m&igrave;nh bạn cho l&agrave; đ&uacute;ng (v&agrave; phần lớn mọi người c&oacute; &yacute; kiến ngược lại với bạn).</li>\r\n<li>Lo nơi nghỉ ngơi v&agrave; ăn uống chu đ&aacute;o cho đo&agrave;n, 2 yếu tố đ&oacute; quyết định rất nhiều đến việc đảm bảo sức khỏe, một phần của việc đảm bảo an to&agrave;n cho chuyến đi. Kh&ocirc;ng đặt được kh&aacute;ch sạn th&igrave; cũng h&atilde;y li&ecirc;n hệ với người d&acirc;n địa phương để t&igrave;m phương &aacute;n ngủ nhờ, đừng để c&aacute;c th&agrave;nh vi&ecirc;n phải ngủ vạ vật ngo&agrave;i đường hay bất cứ một nơi n&agrave;o tương tự. Trừ những chuyến treking hoặc d&atilde; ngoại ngủ lều.</li>\r\n<li>Đừng để th&agrave;nh vi&ecirc;n chạy qu&aacute; 200km/1 ng&agrave;y, chạy nhiều hơn đi được qu&atilde;ng đường d&agrave;i hơn nhưng thời gian để ngắm cảnh, vui chơi lại &iacute;t hơn. Bạn đi để cảm nhận v&agrave; thưởng thức vẻ đẹp của mỗi v&ugrave;ng đi qua chứ kh&ocirc;ng phải đi để đ&aacute;nh dấu việc m&igrave;nh đ&atilde; đến điểm đ&oacute;.</li>\r\n<li>L&agrave; leader, tr&ecirc;n đường bạn l&agrave; người định tốc cho cả đo&agrave;n ph&iacute;a sau, đảm bảo giữ ở mức 50km/h l&agrave; tối đa, đừng chạy nhanh hơn</li>\r\n<li>Đừng để đo&agrave;n của m&igrave;nh phải chạy khi trời đ&atilde; tối, muộn nhất 18h h&atilde;y để mọi người nghỉ ngơi lấy lại sức cho ng&agrave;y tiếp theo.</li>\r\n<li>Thiết lập giới hạn cho việc đi ngủ, y&ecirc;u cầu cả đo&agrave;n ngủ trước 22h để đảm bảo c&aacute;c th&agrave;nh vi&ecirc;n ngủ đủ 7-8h mỗi ng&agrave;y.</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<div class=\"code-block code-block-21\">&nbsp;</div>\r\n<div class=\"code-block code-block-21\">&nbsp;</div>', '966107145d090f240e60e0.18188688.jpg', '2019-06-18', 1),
(52, 28, 'Kinh nghiệm đi xe máy trên đường phượt', '<p style=\"text-align: justify;\">Xe m&aacute;y, phương tiện đi lại phổ biến nhất tại Việt Nam ở thời điểm hiện tại, đ&acirc;y cũng l&agrave; phương tiện đụơc nhiều người y&ecirc;u th&iacute;ch sử dụng khi phong tr&agrave;o x&ecirc; dịch b&ugrave;ng nổ ra. Đối với nhiều bạn trẻ, phượt xe m&aacute;y như l&agrave; một niềm đam m&ecirc;, với những bạn đ&atilde; thường xuy&ecirc;n đi th&igrave; kh&ocirc;ng cần phải n&oacute;i tuy nhi&ecirc;n đối với những bạn mới lần đầu sử dụng xe m&aacute;y để đi phượt th&igrave; kinh nghiệm đi tr&ecirc;n đường v&agrave; xử l&yacute; t&igrave;nh huống l&agrave; v&ocirc; c&ugrave;ng quan trọng trong việc gi&uacute;p bạn ho&agrave;n th&agrave;nh chuyến đi nhưng cũng vẫn lu&ocirc;n an to&agrave;n. Với một ch&uacute;t kinh nghiệm thực tế, C&ugrave;ng Phượt&rsquo;s team xin chia sẻ với c&aacute;c bạn với mong muốn c&aacute;c bạn d&ugrave; di chuyển tới bất cứ nơi đ&acirc;u th&igrave; cũng vẫn lu&ocirc;n an to&agrave;n.</p>', '<p style=\"text-align: justify;\"><a href=\"https://3.bp.blogspot.com/-jsCl68OoFAo/UrFtJpshzCI/AAAAAAAAEM4/AbiLWRWvO6Y/s1600/kinh-nghiem-di-xe-may-tren-duong-phuot.jpg\"><img class=\"alignnone lazyloaded\" src=\"https://3.bp.blogspot.com/-jsCl68OoFAo/UrFtJpshzCI/AAAAAAAAEM4/AbiLWRWvO6Y/s1600/kinh-nghiem-di-xe-may-tren-duong-phuot.jpg\" alt=\"Kinh nghiệm đi xe m&aacute;y tr&ecirc;n đường phượt\" width=\"641\" height=\"426\" border=\"0\" data-lazy-src=\"https://3.bp.blogspot.com/-jsCl68OoFAo/UrFtJpshzCI/AAAAAAAAEM4/AbiLWRWvO6Y/s1600/kinh-nghiem-di-xe-may-tren-duong-phuot.jpg\" data-was-processed=\"true\" /></a></p>\r\n<p style=\"text-align: justify;\"><strong>Ch&uacute; &yacute;</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Những kinh nghiệm đi xe m&aacute;y trong b&agrave;i n&agrave;y được tổng hợp dựa tr&ecirc;n kinh nghiệm thực tế từ những người đi trước. Bạn chỉ n&ecirc;n xem như một nguồn th&ocirc;ng tin tham khảo để sử dụng khi đi phượt.</li>\r\n<li>Ch&uacute;ng t&ocirc;i kh&ocirc;ng y&ecirc;u cầu / bắt buộc bạn phải l&agrave;m theo những vấn đề &nbsp;m&agrave; b&agrave;i viết đưa ra v&igrave; vậy bạn c&acirc;n nhắc v&agrave; tự đưa ra quyết định của ri&ecirc;ng m&igrave;nh.</li>\r\n<li>Cuối c&ugrave;ng, xin h&atilde;y&nbsp;<strong>CẨN THẬN</strong>&nbsp;hết mức c&oacute; thể để đảm bảo an to&agrave;n cho ch&iacute;nh bản th&acirc;n bạn.</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">Ch&uacute;c c&aacute;c bạn c&oacute; một chuyến đi vui vẻ v&agrave; an to&agrave;n&nbsp;</p>\r\n<h3 style=\"text-align: justify;\"><span style=\"color: #ff0000;\"><strong>N&ecirc;n sử sụng loại xe n&agrave;o?</strong></span></h3>\r\n<p style=\"text-align: justify;\">Hiện nay với sự phổ biến của c&aacute;c loại xe ga, kh&aacute; nhiều bạn sử dụng phương tiện dạng n&agrave;y cho c&aacute;c chuyến đi phượt của m&igrave;nh, tuy nhi&ecirc;n theo kinh nghiệm của C&ugrave;ng Phượt c&aacute;c bạn n&ecirc;n sử dụng xe số để thuận lợi hơn. Một số loại xe phổ biến l&agrave; Future Neo, Wave RS, Jupiter, Sirius &hellip;.Dưới đ&acirc;y l&agrave; ưu điểm của xe số so với xe ga</p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Nhẹ nh&agrave;ng, dễ đi</li>\r\n<li>Dễ sửa chữa</li>\r\n<li>Gầm cao</li>\r\n<li>Dễ buộc đồ hơn</li>\r\n<li>Dễ kiểm so&aacute;t tốc độ khi xuống dốc hơn</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">Chuẩn bị trước chuyến đi v&agrave; vật dụng cần mang theo<br />Trước chuyến đi bạn n&ecirc;n mang xe của m&igrave;nh đi bảo dưỡng to&agrave;n bộ, kiểm tra m&aacute; phanh, kiểm tra độ m&ograve;n của lốp, thay mới săm nếu săm cũ của bạn đ&atilde; c&oacute; qu&aacute; nhiều vết v&aacute;, thay dầu m&aacute;y.. Việc n&agrave;y kh&aacute; quan trọng cho sự an to&agrave;n của ch&iacute;nh bạn tr&ecirc;n đường. Nếu lốp xe qu&aacute; cũ, c&oacute; vết nứt, r&atilde;nh đ&atilde; bị m&agrave;i m&ograve;n sẽ ảnh hưởng đến độ b&aacute;m đường. Nếu săm xe c&oacute; nhiều vết v&aacute; trong qu&aacute; tr&igrave;nh chạy xe (đường d&agrave;i) ma s&aacute;t sinh ra c&oacute; thể sẽ l&agrave;m bung vết v&aacute; cũ. Kiểm tra đ&egrave;n pha, đ&egrave;n sau v&agrave; cả đ&egrave;n phanh nữa, khi đang đi tr&ecirc;n đường m&agrave; bạn nhấn phanh để giảm tốc độ nhưng c&aacute;c xe ph&iacute;a sau kh&ocirc;ng biết v&agrave; vẫn lao tới th&igrave; sẽ nguy hiểm cho cả 2 xe. Lốp xe n&ecirc;n để ở mức độ vừa phải, căng qu&aacute; sẽ khiến xe kh&ocirc;ng b&aacute;m đường v&agrave; dễ trượt khi trời mưa c&ograve;n non qu&aacute; sẽ dễ khiến dập săm (vừa phải thế n&agrave;o t&ugrave;y xe của từng người).</p>\r\n<p style=\"text-align: justify;\">Bạn cũng c&oacute; thể sử dụng keo tự v&aacute; đổ v&agrave;o săm để nếu tr&ecirc;n đường c&oacute; bị c&aacute;n phải đinh xe bạn sẽ kh&ocirc;ng gặp nguy hiểm, keo cũng c&oacute; thể tự v&aacute; lu&ocirc;n vết thủng với điều kiện bạn sử dụng đ&uacute;ng c&aacute;ch. Việc bơm keo v&agrave;o ruột xe cần tu&acirc;n thủ theo tr&igrave;nh tự sau: Lắc đều chai keo, lấy nắp đen của chai keo (đồng thời cũng l&agrave; dụng cụ mở van săm) mở van săm ra. Cắm ống dẫn keo v&agrave;o chai keo v&agrave; van ruột xe. B&oacute;p mạnh b&igrave;nh keo để bơm keo v&agrave;o ruột xe, vừa bơm vừa xoay đều lốp để keo chảy đều, van của săm lu&ocirc;n cần cao hơn mặt đất khoảng 15cm. Cuối c&ugrave;ng lau b&ecirc;n trong van, r&aacute;p kim v&ograve;i v&agrave; bơm hơi v&agrave;o ruột xe. CP khuy&ecirc;n bạn sử dụng keo Tiến Hưng bởi qua thực tế sử dụng cả keo n&agrave;y v&agrave; c&aacute;c loại keo của TQ thấy keo của TH kh&ocirc;ng ảnh hưởng đến v&agrave;nh của xe cũng như c&oacute; t&aacute;c dụng kh&aacute; tốt.</p>\r\n<p style=\"text-align: justify;\"><strong>Một số vật dụng li&ecirc;n quan đến xe m&aacute;y bạn n&ecirc;n mang theo (Những vật dụng c&aacute; nh&acirc;n kh&aacute;c c&aacute;c bạn vui l&ograve;ng xem ở b&agrave;i &ldquo;<a href=\"http://cungphuot.info/kinh-nghiem-chuan-bi-do-di-phuot-post4966.cp\">Mang g&igrave; khi đi phượt</a>&ldquo;)</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Gương tr&aacute;i (Luật GTĐB quy định xe bạn chỉ cần c&oacute; gương b&ecirc;n tr&aacute;i) tuy nhi&ecirc;n bạn n&ecirc;n lắp cả 2 gương để tăng tầm quan s&aacute;t.</li>\r\n<li>Giấy tờ li&ecirc;n quan đến xe bao gồm : Đăng k&yacute; xe, giấy ph&eacute;p l&aacute;i xe, Bảo hiểm tr&aacute;ch nhiệm d&acirc;n sự, Giấy nộp ph&iacute; đường bộ.</li>\r\n<li>Sử dụng tấm d&aacute;n phản quang ở ph&iacute;a đu&ocirc;i xe để c&aacute;c xe trong đo&agrave;n dễ nhận ra nhau từ xa khi đi buổi tối.</li>\r\n<li>Mũ bảo hiểm c&oacute; k&iacute;nh (&iacute;t nhất l&agrave; loại nửa đầu chắc chắn, kh&ocirc;ng n&ecirc;n d&ugrave;ng mũ thời trang), quần &aacute;o v&agrave; c&aacute;c loại bảo vệ cơ thể như bọc khuỷu tay, bọc gối, găng tay &hellip; (c&aacute;i n&agrave;y t&ugrave;y từng bạn, c&oacute; những bạn kh&ocirc;ng quen hoặc kh&ocirc;ng th&iacute;ch d&ugrave;ng nhưng nếu c&oacute; n&oacute; sẽ bảo vệ bạn rất tốt trong trường hợp ng&atilde; xe).</li>\r\n<li>Một bộ đồ nghề sửa xe cơ bản : Bộ tr&ograve;ng th&aacute;o lốp, bộ m&oacute;c lốp v&agrave; dụng cụ v&aacute;,bơm tay hoặc bơm ch&acirc;n, một v&agrave;i dụng cụ cơ bản như : Tua v&iacute;t, k&igrave;m, mỏ lết &hellip; Một đ&ocirc;i săm dự ph&ograve;ng (1 trước v&agrave; 1 sau) cho xe của bạn, 1 bugi ph&ugrave; hợp với xe. Tất nhi&ecirc;n với điều kiện trong đo&agrave;n bạn phải c&oacute; người biết một ch&uacute;t về lĩnh vực sửa xe n&agrave;y. C&ograve;n kh&ocirc;ng lời khuy&ecirc;n của ch&uacute;ng t&ocirc;i l&agrave; h&atilde;y sử dụng keo tự v&aacute; đ&atilde; nhắc ở ph&iacute;a tr&ecirc;n.</li>\r\n<li>Nếu trời mưa, sử dụng quần &aacute;o mưa bộ, kh&ocirc;ng n&ecirc;n sử dụng &aacute;o mưa c&aacute;nh dơi bởi bề mặt rộng khiến diện t&iacute;ch gặp gi&oacute; nhiều, sẽ ảnh hướng tới việc điều khiển xe.</li>\r\n<li>Mang một v&agrave;i đoạn ruy băng s&aacute;ng m&agrave;u (thường l&agrave; xanh chuối hoặc v&agrave;ng) để l&agrave;m dấu buộc lại cho xe sau với những cung đường kh&ocirc;ng phải l&agrave; quốc lộ, tỉnh lộ (bản lộ chẳng hạn :D ).</li>\r\n<li>Chai 1,5 lit hoặc hơn đối với những cung đường m&agrave; đổ đầy b&igrave;nh xăng cũng kh&ocirc;ng thể đi từ A cho đến B v&agrave; giữa AB kh&ocirc;ng c&oacute; c&acirc;y xăng n&agrave;o cả (v&iacute; dụ T&acirc;y C&ocirc;n Lĩnh).</li>\r\n<li>D&acirc;y thừng, d&acirc;y d&ugrave; v&agrave; d&acirc;y cao su (loại d&acirc;y sợi vu&ocirc;ng bạn hay thấy người d&acirc;n c&aacute;c tỉnh miền n&uacute;i ph&iacute;a bắc d&ugrave;ng để buộc đồ) d&ugrave;ng để k&eacute;o xe khi cần.</li>\r\n<li>\r\n<h3><span id=\"D7851n_273oagraven_vagrave_ch7889t_273oagraven\"><strong>Dẫn đo&agrave;n v&agrave; chốt đo&agrave;n</strong></span></h3>\r\n<p>Với những đo&agrave;n c&oacute; từ 4 xe trở l&ecirc;n v&agrave; chưa c&oacute; nhiều kinh nghiệm đi đường trường bằng xe m&aacute;y th&igrave; n&ecirc;n ph&acirc;n c&ocirc;ng người dẫn đo&agrave;n v&agrave; người chốt đo&agrave;n để :</p>\r\n<ul>\r\n<li>Dẫn đường cho to&agrave;n bộ đo&agrave;n khi di chuyển, giảm tốc ở những đoạn đường ph&ugrave; hợp, b&aacute;o hiệu cho to&agrave;n đo&agrave;n ở những đoạn đường nguy hiểm v&agrave; thiếu an to&agrave;n.</li>\r\n<li>Chốt đo&agrave;n để đảm bảo kh&ocirc;ng c&oacute; xe n&agrave;o trong đo&agrave;n bị tụt lại ph&iacute;a sau, bị t&aacute;ch khỏi đo&agrave;n hoặc xử l&yacute; c&aacute;c t&igrave;nh huống khi c&oacute; xe trong đo&agrave;n gặp vấn đề (v&iacute; dụ : hỏng xe) &hellip; th&ocirc;ng thường chốt đo&agrave;n sẽ l&agrave; xe giữ bộ dụng cụ sửa xe. N&ecirc;n c&oacute; 2 xe đi c&ugrave;ng v&agrave; thay nhau chốt đo&agrave;n hoặc theo d&otilde;i lẫn nhau bởi c&aacute;c th&agrave;nh vi&ecirc;n kh&aacute;c thường sẽ b&aacute;m theo xe dẫn đầu v&agrave; kh&ocirc;ng để &yacute; tới chốt đo&agrave;n, nếu c&oacute; vấn đề xảy ra 2 xe vẫn hơn l&agrave; 1 xe.</li>\r\n<li>Y&ecirc;u cầu c&aacute;c th&agrave;nh vi&ecirc;n trong đo&agrave;n lu&ocirc;n đi sau xe dẫn đo&agrave;n để dễ kiểm so&aacute;t c&aacute;c t&igrave;nh huống ph&aacute;t sinh.</li>\r\n</ul>\r\n<h3><span id=\"Kinh_nghi7879m_ch7841y_xe_trecircn_2734327901ng\"><strong>Kinh nghiệm chạy xe tr&ecirc;n đường</strong></span></h3>\r\n<p>Dưới đ&acirc;y l&agrave; một số kinh nghiệm tổng hợp về việc di chuyển tr&ecirc;n đường khi đi phượt bằng xe m&aacute;y, bạn n&ecirc;n đọc kỹ để giảm thiểu rủi ro c&aacute;c nguy cơ gặp phải tr&ecirc;n đường</p>\r\n<h4><span id=\"Ch7845p_hagravenh_lu7853t_giao_thocircng\"><strong>Chấp h&agrave;nh luật giao th&ocirc;ng</strong></span></h4>\r\n<p>&ndash; Lu&ocirc;n chấp h&agrave;nh luật giao th&ocirc;ng khi đi đường, kh&ocirc;ng đi h&agrave;ng 3 (tốt nhất chỉ n&ecirc;n đi h&agrave;ng 1) kh&ocirc;ng lạng l&aacute;ch đ&aacute;nh v&otilde;ng, đi đ&uacute;ng l&agrave;n đường của m&igrave;nh, chạy đ&uacute;ng tốc độ quy định (ch&uacute; &yacute; c&aacute;c loại biển b&aacute;o khi v&agrave;o khu vực đ&ocirc;ng d&acirc;n cư)</p>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://4.bp.blogspot.com/-b3-e52U_u0M/Uir7s0iSbwI/AAAAAAAABgc/Jwvkkx9S_8Q/s1600/khu-vuc-dong-dan-cu.jpg\"><img class=\"lazyloaded\" src=\"https://4.bp.blogspot.com/-b3-e52U_u0M/Uir7s0iSbwI/AAAAAAAABgc/Jwvkkx9S_8Q/s200/khu-vuc-dong-dan-cu.jpg\" alt=\"\" width=\"393\" height=\"326\" border=\"0\" data-lazy-src=\"https://4.bp.blogspot.com/-b3-e52U_u0M/Uir7s0iSbwI/AAAAAAAABgc/Jwvkkx9S_8Q/s200/khu-vuc-dong-dan-cu.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>Biển b&aacute;o khu vực đ&ocirc;ng d&acirc;n cư</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Điều 6. Tốc độ tối đa cho ph&eacute;p xe cơ giới tham gia giao th&ocirc;ng tr&ecirc;n đường bộ trong khu vực đ&ocirc;ng d&acirc;n cư</p>\r\n<div class=\"wp-caption aligncenter\"><img class=\"lazyloaded\" src=\"https://media.cungphuot.info/2013/09/kinh-nghiem/toc-do-toi-da-khu-dan-cu.jpg\" width=\"2042\" height=\"3454\" data-lazy-src=\"https://media.cungphuot.info/2013/09/kinh-nghiem/toc-do-toi-da-khu-dan-cu.jpg\" data-was-processed=\"true\" />\r\n<p class=\"wp-caption-text\">Tốc độ tối đa trong khu d&acirc;n cư cập nhật 2016 (Ảnh &ndash; VnExpress)</p>\r\n</div>\r\n<p>&ndash; Nắm vững luật Giao th&ocirc;ng đường bộ để c&oacute; thể xử l&yacute; c&aacute;c t&igrave;nh huống khi gặp vấn đề với CSGT. Khi đ&atilde; bị phạt v&agrave; x&aacute;c định r&otilde; l&agrave; m&igrave;nh sai th&igrave; kh&ocirc;ng n&ecirc;n g&acirc;y căng thẳng, h&atilde;y chủ động xin nộp phạt tại chỗ để kh&ocirc;ng phải quay lại v&agrave;o ng&agrave;y kh&aacute;c chỉ để nộp phạt. C&ugrave;ng Phượt kh&ocirc;ng c&ocirc;ng khai khuyến kh&iacute;ch bạn c&oacute; những h&agrave;nh vi ti&ecirc;u cực tuy nhi&ecirc;n bạn n&ecirc;n xử l&yacute; thế n&agrave;o để gọn g&agrave;ng v&agrave; &iacute;t gặp rắc rối nhất v&agrave; cũng l&agrave; để đảm bảo h&agrave;nh tr&igrave;nh của bạn kh&ocirc;ng bị gi&aacute;n đoạn.</p>\r\n<h4><span id=\"Khi_273i_trecircn_2734327901ng\"><strong>Khi đi tr&ecirc;n đường</strong></span></h4>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://1.bp.blogspot.com/-ql2Qb0zGdB8/UqKOXnp6R4I/AAAAAAAADys/pSeLsxnwo84/s1600/khuc-cua-tay-ao.jpg\"><img class=\"lazyloaded\" src=\"https://1.bp.blogspot.com/-ql2Qb0zGdB8/UqKOXnp6R4I/AAAAAAAADys/pSeLsxnwo84/s1600/khuc-cua-tay-ao.jpg\" alt=\"\" width=\"640\" height=\"360\" border=\"0\" data-lazy-src=\"https://1.bp.blogspot.com/-ql2Qb0zGdB8/UqKOXnp6R4I/AAAAAAAADys/pSeLsxnwo84/s1600/khuc-cua-tay-ao.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>Kh&ocirc;ng vượt ở những đoạn tầm nh&igrave;n bị hạn chế</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&ndash; Đi ở 2/3 l&agrave;n đường của m&igrave;nh, kh&ocirc;ng lấn l&agrave;n (b&aacute;m s&aacute;t theo vạch kẻ đường ở giữa) đối với những đường kh&ocirc;ng c&oacute; vạch kẻ đường th&igrave; phải ước lượng phần l&agrave;n đường của m&igrave;nh được đi. Kh&ocirc;ng đi s&aacute;t lề đường qu&aacute; bởi kinh nghiệm cho thấy khu vực n&agrave;y l&agrave; khu vực thường c&oacute; d&iacute;nh đinh</p>\r\n<p>&ndash; Kh&ocirc;ng n&ecirc;n uống bia rượu khi nghỉ giữa c&aacute;c chặng, vừa đảm bảo an to&agrave;n cho ch&iacute;nh bạn vừa tr&aacute;nh gặp phải rắc rối với lực lượng CSGT.</p>\r\n<p>&ndash; Kh&ocirc;ng vượt khi ph&iacute;a trước tầm nh&igrave;n bị hạn chế, ph&iacute;a trước l&agrave; kh&uacute;c cua. Cố gắng hạn chế tối đa việc vượt phải (c&aacute;c xe khi chạy hầu hết đ&atilde; b&aacute;m đường b&ecirc;n phải n&ecirc;n nếu bạn vẫn lựa chọn vượt phải th&igrave; phần đường d&agrave;nh cho bạn kh&ocirc;ng nhiều, rất nguy hiểm) khi vượt c&aacute;c xe tải, container hoặc c&aacute;c xe si&ecirc;u trường si&ecirc;u trọng n&ecirc;n giữ khoảng c&aacute;ch an to&agrave;n với th&acirc;n xe, kh&ocirc;ng n&ecirc;n chạy s&aacute;t qu&aacute; bởi c&aacute;c xe n&agrave;y khi chạy thường tạo ra lực h&uacute;t xung quanh rất lớn, nếu tay l&aacute;i kh&ocirc;ng vững bạn c&oacute; thể bị gi&oacute; tạo ra từ đ&acirc;y h&uacute;t v&agrave;o gầm xe. Nếu những xe n&agrave;y chạy với tốc độ cao, n&ecirc;n nhường kh&ocirc;ng n&ecirc;n cố vượt hoặc chấp nhận đi chậm theo sau v&agrave; vượt khi v&agrave;o khu vực d&acirc;n cư (l&uacute;c n&agrave;y xe đ&atilde; giảm tốc) Khi vượt phải nh&aacute;y xi nhan v&agrave; c&ograve;i li&ecirc;n tục, đi v&agrave;o khu vực gương chiếu hậu của xe tải c&oacute; thể nh&igrave;n được bạn v&agrave; ra t&iacute;n hiệu đồng &yacute; vượt.</p>\r\n<div class=\"code-block code-block-21\"><ins class=\"adsbygoogle\" data-ad-layout=\"in-article\" data-ad-format=\"fluid\" data-ad-client=\"ca-pub-4774931184618926\" data-ad-slot=\"8547795510\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_2_expand\"><ins id=\"aswift_2_anchor\"><iframe id=\"aswift_2\" name=\"aswift_2\" width=\"658\" height=\"165\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\"></iframe></ins></ins></ins></div>\r\n<p>&ndash; Nếu gặp xe đi ngược chiều ph&oacute;ng nhanh v&agrave; lấn đường, bạn n&ecirc;n chủ động giảm tốc độ v&agrave; đi s&aacute;t v&agrave;o lề, theo d&otilde;i t&igrave;nh huống xảy ra để tiện xử l&yacute;.<br />&ndash; C&aacute;c &ocirc;m ngồi sau cũng thường xuy&ecirc;n phải ch&uacute; &yacute; quan s&aacute;t đường để b&aacute;o hiệu c&aacute;c t&igrave;nh huống ph&iacute;a trước cho xe đi sau bằng tay (khi cần giảm tốc, khi sắp c&oacute; chướng ngại vật, khi xe trước dừng lại &hellip;)</p>\r\n<p>&ndash; Ở điều khiện thời tiết kh&ocirc; r&aacute;o, đường kh&ocirc;ng trơn trượt n&ecirc;n giữ khoảng c&aacute;ch giữa c&aacute;c xe l&agrave; 50m, ở điều kiện tầm nh&igrave;n bị hạn chế hoặc trời tối, trời mưa &hellip; th&igrave; khoảng c&aacute;ch n&agrave;y c&oacute; thể thu hẹp lại (do l&uacute;c n&agrave;y tốc độ xe đ&atilde; giảm)</p>\r\n<p>&ndash; Khi đi buổi tối cố gắng nh&aacute;y pha một c&aacute;ch hợp l&yacute; để c&aacute;c xe ngược chiều kh&ocirc;ng chiếu thẳng pha v&agrave;o mắt bạn, nếu xe đi ngược chiều kh&ocirc;ng hạ pha bạn n&ecirc;n chủ động giảm tốc v&agrave; đi s&aacute;t v&agrave;o lề, kh&ocirc;ng n&ecirc;n ăn thua bằng c&aacute;ch chiếu pha ngược lại. Bạn c&oacute; thể sử dụng th&ecirc;m 1 chiếc k&iacute;nh mắt m&agrave;u v&agrave;ng để sử dụng cho đi tối, sẽ hạn chế được kh&aacute; tốt nếu c&oacute; bị rọi thẳng đ&egrave;n pha v&agrave;o mắt.</p>\r\n<p>&ndash; Khi đo&agrave;n dừng xe c&aacute;c xe n&ecirc;n dừng s&aacute;t v&agrave;o hết mức c&oacute; thể với lề đường, dừng th&agrave;nh h&agrave;ng 1 mỗi xe c&aacute;ch nhau khoảng v&agrave;i m&eacute;t, kh&ocirc;ng n&ecirc;n dừng tập trung to&agrave;n bộ c&aacute;c xe lại cạnh nhau để tr&aacute;nh ảnh hưởng giao th&ocirc;ng tr&ecirc;n đường.</p>\r\n<p><strong>c. Khi đi xe tr&ecirc;n đường n&uacute;i, đường b&ugrave;n, đường xấu</strong></p>\r\n<p>&ndash; Khi đi đường n&uacute;i lu&ocirc;n ch&uacute; &nbsp;&yacute; hệ thống biển b&aacute;o để biết ph&iacute;a trước như n&agrave;o (cua li&ecirc;n tục, đường sạt lở, sắp l&ecirc;n dốc &hellip;)</p>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://4.bp.blogspot.com/-t05XbC2b5x0/UqKO9Gf4kcI/AAAAAAAADzE/MUg2Fcvua3c/s1600/di-xe-len-doc.png\"><img class=\"lazyloaded\" src=\"https://4.bp.blogspot.com/-t05XbC2b5x0/UqKO9Gf4kcI/AAAAAAAADzE/MUg2Fcvua3c/s1600/di-xe-len-doc.png\" alt=\"\" width=\"400\" height=\"240\" border=\"0\" data-lazy-src=\"https://4.bp.blogspot.com/-t05XbC2b5x0/UqKO9Gf4kcI/AAAAAAAADzE/MUg2Fcvua3c/s1600/di-xe-len-doc.png\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>L&ecirc;n dốc bằng số n&agrave;o th&igrave; xuống số đ&oacute;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&ndash; Khi bạn điều khiển xe m&aacute;y l&ecirc;n một con dốc kh&ocirc;ng qu&aacute; cao, bạn c&oacute; thể tăng tốc vừa phải, tạo đ&agrave; để vượt qua dốc. Song, đối với những con dốc đứng, c&oacute; độ dốc lớn, bạn nhất định phải trả số về số thấp theo tốc độ bạn nghĩ c&oacute; thể đưa bạn l&ecirc;n đến đỉnh dốc. Cố gắng tr&aacute;nh việc chuyển đổi số giữa dốc. Nếu tốc độ ban đầu giảm giữa chừng, về số trước khi xe c&oacute; bất cứ dấu hiệu chết m&aacute;y n&agrave;o. Bạn n&ecirc;n ph&aacute;n đo&aacute;n để trả số về cấp th&iacute;ch hợp. Một v&agrave;i lần kinh nghiệm l&ecirc;n dốc sẽ hướng dẫn cho bạn l&agrave;m tốt điều n&agrave;y. Khi bạn l&ecirc;n gần tới đỉnh dốc, h&atilde;y giảm tay ga, giữ nguy&ecirc;n số v&agrave; bu&ocirc;ng tr&ocirc;i qua đỉnh dốc. Chủ động về số nhanh khi gặp c&aacute;c phương tiện kh&aacute;c đi ngược chiều ở thời điểm bạn l&ecirc;n tới gần đỉnh dốc, v&igrave; như vậy, bạn sẽ kh&ocirc;ng bị đuối đ&agrave;, dẫn đến chết m&aacute;y<br />&ndash; Khi xuống dốc, đối với dốc vừa, h&atilde;y trả tay ga về, sử dụng hơi n&eacute;n giảm của động cơ để giảm tốc độ. Phanh trước v&agrave; sau chỉ sử dụng để hỗ trợ trong trường hợp thật cần thiết. Đối với dốc đứng, trả về số thấp nhất khi bắt đầu xuống dốc. Điều n&agrave;y sẽ l&agrave;m cho phanh động cơ c&oacute; hiệu quả hơn. Nếu đường dốc giống nhau cả l&ecirc;n lẫn xuống hay nếu bạn l&aacute;i xuống dốc bằng l&uacute;c bạn l&aacute;i l&ecirc;n, h&atilde;y sử dụng đ&uacute;ng với số m&agrave; bạn đ&atilde; sử dụng khi l&ecirc;n. C&aacute;ch tốt nhất l&agrave; kh&ocirc;ng n&ecirc;n chuyển số giữa dốc. Tuy nhi&ecirc;n, khi thật cần thiết, h&atilde;y trả số về cấp thấp hơn trước khi xe bạn tăng tốc qu&aacute; nhanh.</p>\r\n<p>&ndash; Nếu dốc qu&aacute; d&agrave;i v&agrave; dốc (đoạn S&igrave;n Hồ chẳng hạn) th&igrave; cứ khoảng 10-15km n&ecirc;n dừng lại khoảng 5&prime; để cho m&aacute;y xe bớt n&oacute;ng, đừng đổ nước v&agrave;o m&aacute;y l&uacute;c đ&oacute; nh&eacute;.</p>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://2.bp.blogspot.com/-ie31r0t53Jw/UqKOnsr57CI/AAAAAAAADy0/R3oqx9Yt2O0/s1600/duong-tron.jpg\"><img class=\"lazyloaded\" src=\"https://2.bp.blogspot.com/-ie31r0t53Jw/UqKOnsr57CI/AAAAAAAADy0/R3oqx9Yt2O0/s1600/duong-tron.jpg\" alt=\"\" width=\"641\" height=\"378\" border=\"0\" data-lazy-src=\"https://2.bp.blogspot.com/-ie31r0t53Jw/UqKOnsr57CI/AAAAAAAADy0/R3oqx9Yt2O0/s1600/duong-tron.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>Đường trơn qu&aacute; th&igrave; y&ecirc;u cầu &ocirc;m xuống đi bộ qua đoạn đ&oacute;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&ndash; Khi gặp đường b&ugrave;n lầy lội hoặc đoạn đường trơn th&igrave; n&ecirc;n giảm tốc độ, y&ecirc;u cầu &ocirc;m xuống đi bộ nếu đoạn đường kh&oacute;, c&acirc;n chỉnh lại đồ đạc để đảm bảo sự c&acirc;n bằng của xe (t&ugrave;y t&igrave;nh huống cụ thể m&agrave; n&ecirc;n giữ hay n&ecirc;n để nguy&ecirc;n đồ đạc tr&ecirc;n xe), giữ chắc tay l&aacute;i v&agrave; ga đều, n&ecirc;n lần lượt từng xe qua chứ kh&ocirc;ng n&ecirc;n vượt nhau ở những đoạn đường thế n&agrave;y.</p>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://4.bp.blogspot.com/-JHZkQaUfeW8/UqKOstwToWI/AAAAAAAADy8/3TOkKe9PLAk/s1600/suoi.jpg\"><img class=\"lazyloaded\" src=\"https://4.bp.blogspot.com/-JHZkQaUfeW8/UqKOstwToWI/AAAAAAAADy8/3TOkKe9PLAk/s1600/suoi.jpg\" alt=\"\" width=\"642\" height=\"377\" border=\"0\" data-lazy-src=\"https://4.bp.blogspot.com/-JHZkQaUfeW8/UqKOstwToWI/AAAAAAAADy8/3TOkKe9PLAk/s1600/suoi.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>Nước suối chảy mạnh th&igrave; nhất định kh&ocirc;ng được liều đi qua</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&ndash; Khi qua ngầm hoặc qua suối n&ecirc;n kiểm tra mức độ chảy xiết của d&ograve;ng, nếu nước chảy mạnh th&igrave; n&ecirc;n dừng lại t&igrave;m đường kh&aacute;c. Trong trường hợp bắt buộc phải qua n&ecirc;n t&igrave;m kiếm sự gi&uacute;p đỡ từ người d&acirc;n địa phương, kh&ocirc;ng n&ecirc;n cố liều m&igrave;nh băng qua sẽ rất nguy hiểm.</p>\r\n<h4><span id=\"M7897t_s7889_bi7875n_c7843nh_baacuteo_nguy_hi7875m_trecircn_2734327901ng\"><strong>Một số biển cảnh b&aacute;o nguy hiểm tr&ecirc;n đường.</strong></span></h4>\r\n<p><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/201a.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/201a.gif\" data-was-processed=\"true\" /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/201b.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/201b.gif\" data-was-processed=\"true\" /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/202.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/202.gif\" data-was-processed=\"true\" /><br />Nh&oacute;m biển b&aacute;o chỗ ngoặt, lần lượt l&agrave; b&aacute;o nguy hiểm khi rẽ tr&aacute;i, rẽ phải v&agrave; b&aacute;o c&aacute;c chỗ ngoặt li&ecirc;n tiếp.Biển b&aacute;o chỗ ngoặt li&ecirc;n tiếp bạn sẽ rất hay gặp tr&ecirc;n đường n&uacute;i.<br /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/215a.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/215a.gif\" data-was-processed=\"true\" /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/215b.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/215b.gif\" data-was-processed=\"true\" /><br />Nh&oacute;m biển cảnh b&aacute;o ph&iacute;a trước l&agrave; vực s&acirc;u, cần ch&uacute; &yacute; để tr&aacute;nh bị rơi xuống vực<br /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/216.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/216.gif\" data-was-processed=\"true\" /><br />Biển b&aacute;o đường ngầm hoặc đập tr&agrave;n, khi đi đường trời mưa m&agrave; gặp biển b&aacute;o n&agrave;y bạn phải hết sức cẩn thận c&acirc;n nhắc xem c&oacute; tiếp tục đi qua kh&ocirc;ng bởi l&uacute;c n&agrave;y nước suối đổ về rất mạnh v&agrave; xiết, nếu kh&ocirc;ng cẩn thận c&oacute; thể bị lũ cuốn tr&ocirc;i.<br /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/219.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/219.gif\" data-was-processed=\"true\" /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/220.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/220.gif\" data-was-processed=\"true\" /><br />Biển b&aacute;o dốc l&ecirc;n v&agrave; dốc xuống k&egrave;m theo tỉ lệ % của dốc. Khi gặp biển b&aacute;o n&agrave;y bạn n&ecirc;n chuẩn bị giảm tốc (nếu xuống dốc) hoặc về số tăng tốc nhẹ để lấy đ&agrave; (l&ecirc;n dốc).<br /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/222.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/222.gif\" data-was-processed=\"true\" /><img class=\"lazyloading\" src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/228.gif\" alt=\"\" data-lazy-src=\"http://www.luatgiaothongduongbo.net/img1/store/Bien_bao_nguy_hiem/228.gif\" data-was-processed=\"true\" /><br />Biển b&aacute;o đường trơn v&agrave; đ&aacute; lở, gặp biển b&aacute;o n&agrave;y phải hết sức cẩn thận khi trời mưa, giảm tốc độ v&agrave; tăng cường ch&uacute; &yacute; quan s&aacute;t.</p>\r\n</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', '859772055d0912177a9387.05221326.jpg', '2019-06-18', 1),
(53, 28, 'Cách nhận biết dòng chảy xa bờ', '<p style=\"text-align: justify;\">D&ograve;ng chảy r&uacute;t xa bờ, c&ograve;n gọi l&agrave; d&ograve;ng chảy xa bờ, d&ograve;ng rip hay d&ograve;ng r&uacute;t bờ, l&agrave; một d&ograve;ng nước mạnh chảy từ bờ hướng ra biển. S&oacute;ng đ&aacute;nh v&agrave; đưa nước biển v&agrave;o bờ, nhưng khi nước biển được li&ecirc;n tục đưa v&agrave;o bờ th&igrave; ch&uacute;ng tập hợp lại th&agrave;nh một d&ograve;ng đi ngược ra biển.&nbsp;Thực tế hiện nay, kh&ocirc;ng nhiều người biết về sự nguy hiểm của d&ograve;ng chảy n&agrave;y trong khi n&oacute; xuất hiện kh&aacute; thường xuy&ecirc;n ở một số khu vực b&atilde;i biển của Việt Nam, C&ugrave;ng Phượt chia sẻ một số th&ocirc;ng tin cũng như c&aacute;c phương &aacute;n xử l&yacute; trong trường hợp c&aacute;c bạn rơi v&agrave;o t&igrave;nh huống tương tự, việc nắm vững những th&ocirc;ng tin n&agrave;y sẽ mang lại cho bạn sự an to&agrave;n hơn khi tắm biển.</p>', '<div id=\"attachment_18825\" class=\"wp-caption aligncenter\" style=\"text-align: justify;\"><img class=\"wp-image-18825 size-full lazyloaded\" src=\"https://cungphuot.info/wp-content/uploads/2015/03/dong-chay-xa-bo.jpg\" alt=\"\" width=\"800\" height=\"477\" data-lazy-src=\"https://cungphuot.info/wp-content/uploads/2015/03/dong-chay-xa-bo.jpg\" data-was-processed=\"true\" />\r\n<p id=\"caption-attachment-18825\" class=\"wp-caption-text\">D&ograve;ng chảy xa bờ được lợi dụng để đưa người chơi lướt s&oacute;ng ra xa (Ảnh &ndash;&nbsp;Laurarama)</p>\r\n</div>\r\n<p style=\"text-align: justify;\">Vận tốc trung b&igrave;nh của d&ograve;ng chảy c&oacute; thể thay đổi từ 0,5 m đến 1m/gi&acirc;y, khi đ&oacute; kh&ocirc;ng ai c&oacute; khả năng bơi ngược n&oacute; để v&agrave;o bờ. C&oacute; khi do những thay đổi đột ngột của s&oacute;ng biển, vận tốc d&ograve;ng chảy r&uacute;t xa bờ c&oacute; thể l&ecirc;n đến 2,5 m/gi&acirc;y, nhanh hơn cả vận tốc bơi của một vận động vi&ecirc;n bơi lội Olympic. D&ograve;ng chảy r&uacute;t xa bờ thường hẹp, c&oacute; chiều ngang khoảng 1&ndash;3 m. Tuy nhi&ecirc;n, c&oacute; khi d&ograve;ng chảy n&agrave;y c&oacute; bề rộng dọc bờ từ 3-30m v&agrave; chiều d&agrave;i ra biển từ 100-150m. Trong một ng&agrave;y ch&uacute;ng c&oacute; thể di chuyển đến những vị tr&iacute; kh&aacute;c nhau trong v&ugrave;ng đới s&oacute;ng đổ. Hiện tượng n&agrave;y c&oacute; thể xảy ra ở bất cứ v&ugrave;ng đới s&oacute;ng đổ n&agrave;o của đại dương, biển, v&agrave; những hồ lớn.</p>\r\n<p style=\"text-align: justify;\">Một khi bị kẹt v&agrave;o d&ograve;ng chảy r&uacute;t xa bờ th&igrave; chớ n&ecirc;n cố bơi ngược d&ograve;ng để v&agrave;o bờ. Bạn sẽ bị kiệt sức v&agrave; đuối nước. Chẳng &iacute;ch g&igrave; khi gắng sức chống cự với d&ograve;ng chảy v&igrave; n&oacute; lu&ocirc;n lu&ocirc;n thắng. N&ecirc;n nhớ rằng d&ograve;ng chảy kh&ocirc;ng nhấn ch&igrave;m bạn xuống nước, n&oacute; chỉ k&eacute;o bạn ra xa khỏi bờ, v&agrave; n&oacute; chỉ hoạt động trong một phạm vi hẹp. Điều bạn cần l&agrave;m l&agrave; t&igrave;m c&aacute;ch tho&aacute;t khỏi n&oacute;. Mau ch&oacute;ng nhận ra t&igrave;nh trạng của m&igrave;nh (đang bị kẹt trong d&ograve;ng chảy r&uacute;t xa bờ), giữ b&igrave;nh tĩnh, v&agrave; bơi theo hướng song song với bờ cho đến khi bạn tho&aacute;t khỏi d&ograve;ng chảy. T&ugrave;y theo từng trường hợp, c&oacute; thể bạn phải bơi h&agrave;ng trăm m&eacute;t hoặc hơn rồi mới c&oacute; thể tho&aacute;t khỏi n&oacute; v&agrave; bắt đầu bơi v&agrave;o bờ. Khi bơi v&agrave;o bờ &ndash; để tr&aacute;nh khỏi bị rơi v&agrave;o một d&ograve;ng chảy r&uacute;t xa bờ kh&aacute;c cần nhắm v&agrave;o những địa điểm c&oacute; s&oacute;ng đập v&agrave;o bờ, cũng n&ecirc;n quan s&aacute;t để nhận ra những vật thể tr&ocirc;i nổi tr&ecirc;n mặt nước đang dạt dần v&agrave;o bờ.</p>\r\n<p style=\"text-align: justify;\">Những ai th&iacute;ch tắm biển cần phải hiểu r&otilde; mức độ nguy hiểm của d&ograve;ng chảy r&uacute;t xa bờ, học biết c&aacute;ch nhận ra ch&uacute;ng, v&agrave; biết c&aacute;ch tho&aacute;t khỏi ch&uacute;ng, nếu c&oacute; thể chỉ n&ecirc;n đến những b&atilde;i tắm c&oacute; lực lượng cứu hộ chuy&ecirc;n nghiệp.N&ecirc;n học bơi, c&ograve;n nếu đ&atilde; biết bơi th&igrave; kh&ocirc;ng n&ecirc;n bơi một m&igrave;nh.</p>\r\n<div class=\"code-block code-block-21\" style=\"text-align: justify;\"><ins class=\"adsbygoogle\" data-ad-layout=\"in-article\" data-ad-format=\"fluid\" data-ad-client=\"ca-pub-4774931184618926\" data-ad-slot=\"8547795510\" data-adsbygoogle-status=\"done\"><ins id=\"aswift_2_expand\"><ins id=\"aswift_2_anchor\"><iframe id=\"aswift_2\" name=\"aswift_2\" width=\"658\" height=\"165\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\"></iframe></ins></ins></ins></div>\r\n<p style=\"text-align: justify;\"><strong>Ph&ograve;ng chống</strong></p>\r\n<p style=\"text-align: justify;\">&ndash; Đối với những người tắm biển, đặc biệt đối với kh&aacute;ch du lịch ở c&aacute;c nơi xa đến tắm biển, do chưa hiều biết về d&ograve;ng chảy xa bờ v&agrave; c&aacute;c đặc điểm của b&atilde;i tắm, cho n&ecirc;n cần t&igrave;m hiểu c&aacute;c ti&ecirc;u chuẩn an to&agrave;n của một b&atilde;i tắm, c&oacute; thể bằng c&aacute;ch bạn hỏi những người d&acirc;n địa phương sống xung quanh b&atilde;i tắm, c&aacute;c nh&acirc;n vi&ecirc;n của đội cứu hộ cứu nạn hoặc đọc kỹ c&aacute;c biển cảnh b&aacute;o được lắp đặt tại c&aacute;c điểm c&oacute; khả năng d&ograve;ng&nbsp;chảy xa bờ&nbsp;thường xuy&ecirc;n xuất hiện nhất.</p>\r\n<p style=\"text-align: justify;\">&ndash; Khi đ&atilde; bị&nbsp;d&ograve;ng chảy xa bờ&nbsp;cuốn tr&ocirc;i th&igrave; cần phải nắm được c&aacute;c phương ph&aacute;p ph&ograve;ng tr&aacute;nh theo c&aacute;c bước thực hiện&nbsp;sau đ&acirc;y:</p>\r\n<ul>\r\n<li style=\"text-align: justify;\">Kh&ocirc;ng n&ecirc;n hoảng sợ v&agrave; phải hết sức b&igrave;nh tĩnh,</li>\r\n<li style=\"text-align: justify;\">Cố gắng bơi bơi thả tr&ocirc;i tự do xu&ocirc;i theo d&ograve;ng&nbsp;chảy xa bờ&nbsp;để tiết kiệm năng lượng, cấm kh&ocirc;ng bơi ngược&nbsp;d&ograve;ng chống lại theo hướng ngược v&agrave;o bờ,</li>\r\n<li style=\"text-align: justify;\">Để tho&aacute;t khỏi sự ảnh hưởng của d&ograve;ng&nbsp;chảy xa bờ, cần bơi thả lỏng để t&igrave;m thời cơ bơi l&aacute;ch qua trục của d&ograve;ng về hai ph&iacute;a. Sau khi tho&aacute;t khỏi d&ograve;ng chảy sẽ t&igrave;m c&aacute;ch bơi từ từ v&agrave;o bờ,</li>\r\n<li style=\"text-align: justify;\">Nếu kh&ocirc;ng bơi tho&aacute;t khỏi d&ograve;ng chảy do d&ograve;ng c&oacute; độ xo&aacute;y, th&igrave; phải sử dụng phương ph&aacute;p bơi thả nổi hoặc bơi đứng, đồng thời k&ecirc;u cứu sự gi&uacute;p đỡ của những người tr&ecirc;n bờ hoặc nh&acirc;n vi&ecirc;n đội cứu hộ, cứu nạn bờ biển,</li>\r\n<li style=\"text-align: justify;\">Người bị nạn, nếu cảm thấy đuối sức kh&ocirc;ng c&oacute; thể bơi được v&agrave;o bờ th&igrave; phải k&ecirc;u to v&agrave; vẩy tay y&ecirc;u cầu sự cứu hộ khẩn cấp từ tr&ecirc;n bờ. Nếu người bị nạn sau khi lấy lại được sự b&igrave;nh tĩnh hoặc sau khi d&ograve;ng chảy đ&atilde; yếu dần ở khu vực xa bờ, th&igrave; cố gắng bơi l&aacute;ch qua d&ograve;ng v&agrave; sau đ&oacute; theo hướng thẳng v&agrave;o bờ. Kh&ocirc;ng n&ecirc;n hoảng sợ,&nbsp;<strong>d&ograve;ng chảy xa bờ sẽ chấm dứt v&agrave; ti&ecirc;u t&aacute;n ở v&ugrave;ng nước s&acirc;u xa bờ</strong>.</li>\r\n</ul>', '20000630555d091532720f48.14425595.jpg', '2019-06-18', 1);
INSERT INTO `tintuc` (`tintuc_id`, `danhmuc_id`, `tintuc_ten`, `tintuc_mota`, `tintuc_noidung`, `tintuc_anh`, `tintuc_ngaytao`, `tintuc_hot`) VALUES
(54, 28, 'Kinh nghiệm chuẩn bị đồ đi Phượt', '<p style=\"text-align: justify;\">Trước mỗi chuyến đi việc chuẩn bị đồ đạc mang theo l&agrave; v&ocirc; c&ugrave;ng quan trọng, nhiều qu&aacute; th&igrave; sẽ khiến trọng lượng bị tăng dẫn tới việc cồng kềnh cho chuyến đi, nhưng &iacute;t qu&aacute; th&igrave; khi bạn cần lại kh&ocirc;ng c&oacute; để sử dụng, tất cả mọi thứ n&ecirc;n vừa đủ. Việc lựa chọn ĐỦ những đồ vật thiết yếu sẽ gi&uacute;p bạn c&oacute; thứ m&igrave;nh khi cần v&agrave; kh&ocirc;ng mấy khi mang thừa h&agrave;nh l&yacute; (nhất l&agrave; khi di chuyển bằng m&aacute;y bay).&nbsp;Trong phạm vi b&agrave;i viết n&agrave;y, C&ugrave;ng Phượt chỉ n&ecirc;u ra c&aacute;c vật dụng cần thiết, bạn h&atilde;y t&ugrave;y theo chuyến đi của m&igrave;nh m&agrave; bỏ bớt những vật dụng kh&ocirc;ng d&ugrave;ng tới.</p>', '<p style=\"text-align: justify;\"><a href=\"https://4.bp.blogspot.com/-QBG4r1Oc69g/UrFjaXgRZ5I/AAAAAAAAELQ/_oNSpSQc26U/s1600/chuan-bi-do-khi-di-phuot.jpg\"><img class=\"alignnone lazyloaded\" src=\"https://4.bp.blogspot.com/-QBG4r1Oc69g/UrFjaXgRZ5I/AAAAAAAAELQ/_oNSpSQc26U/s1600/chuan-bi-do-khi-di-phuot.jpg\" alt=\"chuẩn bị đồ đi phượt\" width=\"640\" height=\"480\" border=\"0\" data-lazy-src=\"https://4.bp.blogspot.com/-QBG4r1Oc69g/UrFjaXgRZ5I/AAAAAAAAELQ/_oNSpSQc26U/s1600/chuan-bi-do-khi-di-phuot.jpg\" data-was-processed=\"true\" /></a></p>\r\n<p style=\"text-align: justify;\">C&aacute;c bạn vui l&ograve;ng g&oacute;p &yacute;, bổ sung c&aacute;c vật dụng bạn cho l&agrave; cần thiết bằng hệ thống comment ngay dưới b&agrave;i viết để C&ugrave;ng Phượt bổ sung, những g&oacute;p &yacute; n&agrave;y sẽ gi&uacute;p b&agrave;i viết được đầy đủ hơn, l&agrave; kinh nghiệm hữu &iacute;ch cho những bạn kh&aacute;c trước mỗi chuyến đi. Xin ch&acirc;n th&agrave;nh cảm ơn.</p>\r\n<h3 style=\"text-align: justify;\"><span id=\"2727891_dugraveng_sinh_ho7841t_caacute_nhacircn_hagraveng_ngagravey\"><strong>Đồ d&ugrave;ng sinh hoạt c&aacute; nh&acirc;n h&agrave;ng ng&agrave;y</strong></span></h3>\r\n<p style=\"text-align: justify;\">Đồ d&ugrave;ng c&aacute; nh&acirc;n ở đ&acirc;y đơn thuần l&agrave; những vật dụng sinh hoạt h&agrave;ng ng&agrave;y d&agrave;nh ri&ecirc;ng cho c&aacute; nh&acirc;n bạn sử dụng, những đồ d&ugrave;ng kh&aacute;c sẽ được sắp xếp v&agrave;o những mục ph&ugrave; hợp ở ph&iacute;a dưới. C&ugrave;ng Phượt gợi &yacute; bạn đồ d&ugrave;ng cho một chuyến đi với khoảng thời gian k&eacute;o d&agrave;i từ 3-5 ng&agrave;y.</p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Quần d&agrave;i : 1-3 chiếc trong đ&oacute; c&oacute; 1 chiếc sử dụng thường xuy&ecirc;n tr&ecirc;n đường, 1-2 chiếc c&ograve;n lại chỉ sử dụng để l&agrave;m phương &aacute;n dự ph&ograve;ng khi qu&aacute; bẩn hoặc ướt (kh&ocirc;ng n&ecirc;n thay quần d&agrave;i đi đường h&agrave;ng ng&agrave;y l&agrave;m g&igrave; v&igrave; nếu như thế th&igrave; bao nhi&ecirc;u cũng kh&ocirc;ng đủ).</li>\r\n<li>&Aacute;o ph&ocirc;ng mỏng mặc ở trong c&ugrave;ng, số lượng &aacute;o n&agrave;y th&igrave; tương đương với số ng&agrave;y bạn đi, mang khoảng từ 3-5 chiếc l&agrave; okie.</li>\r\n<li>&Aacute;o len mỏng cho m&ugrave;a h&egrave; v&agrave; &aacute;o len d&agrave;y cho m&ugrave;a đ&ocirc;ng : 2 chiếc</li>\r\n<li>&Aacute;o kho&aacute;c gi&oacute; : c&oacute; t&aacute;c dụng chắn gi&oacute; rất tốt, nếu c&oacute; thể bạn n&ecirc;n mua loại &aacute;o c&oacute; khả năng chống cả nước để kh&ocirc;ng bị ngấm sương v&agrave;o người. &Aacute;o gi&oacute; thường được d&ugrave;ng với những nơi thời tiết chỉ hơi se lạnh.</li>\r\n<li>&Aacute;o kho&aacute;c d&agrave;y : &nbsp;V&agrave;o m&ugrave;a đ&ocirc;ng nhiệt độ ở c&aacute;c v&ugrave;ng n&uacute;i c&oacute; những nơi xuống rất thấp nhất l&agrave; chiều tối, ch&iacute;nh v&igrave; vậy n&ecirc;n cần một chiếc &aacute;o giữ ấm tốt nhưng vẫn phải gọn, bạn c&oacute; thể lựa chọn những loại &aacute;o l&ocirc;ng vũ nhẹ.</li>\r\n<li>Khăn qu&agrave;ng cổ : Với m&ugrave;a h&egrave; bạn chỉ cần một v&agrave;i chiếc khăn rằn d&ugrave;ng để quấn cổ v&agrave; quấn đầu sẽ c&oacute; t&aacute;c dụng chắn nắng (kh&ocirc;ng bị ch&aacute;y đen khu vực cổ) v&agrave; thấm mồ h&ocirc;i tr&ecirc;n đầu, nếu khăn đủ d&agrave;i th&igrave; chỉ cần d&ugrave;ng 1 chiếc cho cả 3 nhiệm vụ; quấn cổ, quấn đầu, bịt mặt. V&agrave;o m&ugrave;a đ&ocirc;ng thay khăn rằn bằng khăn len để giữ nhiệt tốt hơn.</li>\r\n<li>Bịt tai để giữ ấm nếu trời lạnh.</li>\r\n<li>Khoảng 3-5 đ&ocirc;i tất v&agrave; 3-5 bộ quần/&aacute;o l&oacute;t. Tất c&oacute; t&aacute;c dụng giữ ấm cho gan b&agrave;n ch&acirc;n n&ecirc;n rất quan trọng, bạn cứ mang nhiều đi một ch&uacute;t để thay trong trường hợp bị ướt.</li>\r\n<li>Gi&agrave;y : n&ecirc;n sử dụng loại gi&agrave;y thể thao c&oacute; độ b&aacute;m tốt, đi thoải m&aacute;i cho ch&acirc;n. Với c&aacute;c bạn nam tr&aacute;nh d&ugrave;ng gi&agrave;y da, với c&aacute;c bạn nữ tr&aacute;nh d&ugrave;ng gi&agrave;y cao g&oacute;t, gi&agrave;y b&uacute;p b&ecirc;. Trong trường hợp ph&acirc;n v&acirc;n giữa c&aacute;c loại gi&agrave;y bạn cứ d&ugrave;ng gi&agrave;y bộ đội.</li>\r\n<li>Khăn mặt, b&agrave;n chải, kem đ&aacute;nh răng, dầu gội đầu, x&agrave; ph&ograve;ng tất cả những thứ n&agrave;y bạn n&ecirc;n bỏ gọn v&agrave;o trong một chiếc t&uacute;i đựng đồ c&aacute; nh&acirc;n.</li>\r\n<li>T&uacute;i ngủ cho những chuyến đi ngủ lều.</li>\r\n<li>Với ngần đ&oacute; thứ đồ th&igrave; bạn sẽ cần đến một chiếc ba l&ocirc; khoảng 50L l&agrave; vừa, C&ugrave;ng Phượt khuy&ecirc;n bạn với 1 xe m&aacute;y th&igrave; sử dụng 2 ba l&ocirc; của xế khoảng 60L v&agrave; của &ocirc;m khoảng 50L l&agrave; vừa, một số đồ d&ugrave;ng kh&aacute;c bạn c&oacute; thể để ở ngo&agrave;i.</li>\r\n</ul>\r\n<h3 style=\"text-align: justify;\"><span id=\"Gi7845y_t7901_tugravey_thacircn\"><strong>Giấy tờ t&ugrave;y th&acirc;n</strong></span></h3>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://4.bp.blogspot.com/-MsI9nzIiQro/UrFmK5Dw62I/AAAAAAAAELk/RkaUeAYITvU/s1600/chuan-bi-do-khi-di-phuot-3.jpg\"><img class=\"lazyloaded\" src=\"https://4.bp.blogspot.com/-MsI9nzIiQro/UrFmK5Dw62I/AAAAAAAAELk/RkaUeAYITvU/s1600/chuan-bi-do-khi-di-phuot-3.jpg\" alt=\"\" width=\"586\" height=\"586\" border=\"0\" data-lazy-src=\"https://4.bp.blogspot.com/-MsI9nzIiQro/UrFmK5Dw62I/AAAAAAAAELk/RkaUeAYITvU/s1600/chuan-bi-do-khi-di-phuot-3.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>Nhớ mang theo đầy đủ giấy tờ t&ugrave;y th&acirc;n nh&eacute;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<ul>\r\n<li style=\"text-align: justify;\">Giấy ph&eacute;p l&aacute;i xe : Thứ bắt buộc phải c&oacute; nếu chuyến đi của bạn c&oacute; một h&agrave;nh tr&igrave;nh bất kỳ sử dụng xe m&aacute;y, đ&acirc;y cũng l&agrave; giấy tờ bạn c&oacute; thể sử dụng thay thế CMND khi l&ecirc;n m&aacute;y bay.</li>\r\n<li style=\"text-align: justify;\">Đăng k&yacute; xe, bảo hiểm xe, giấy chứng nhận nộp ph&iacute; đường bộ : Những thứ cần thiết để tr&aacute;nh gặp rắc rối với CSGT khi đi tr&ecirc;n đường</li>\r\n<li style=\"text-align: justify;\">Hộ chiếu&nbsp;+ CMND : Khi v&agrave;o khu vực bi&ecirc;n giới, theo quy định bạn sẽ phải xuất tr&igrave;nh giấy tờ t&ugrave;y th&acirc;n với đồn bi&ecirc;n ph&ograve;ng quản l&yacute; khu vực đ&oacute; ch&iacute;nh v&igrave; vậy bạn n&ecirc;n mang theo CMND, trong trường hợp ở một số cửa khẩu c&aacute;c bạn muốn sang ph&iacute;a b&ecirc;n kia bi&ecirc;n giới du lịch th&igrave; bạn cần phải c&oacute; th&ecirc;m hộ chiếu.</li>\r\n<li style=\"text-align: justify;\">Nếu bạn kh&ocirc;ng muốn mang theo tiền nhiều th&igrave; nhớ mang theo thẻ ATM, thường ở trung t&acirc;m c&aacute;c huyện sẽ c&oacute; ATM của ng&acirc;n h&agrave;ng Agribank n&ecirc;n c&aacute;c bạn nhớ mang theo loại thẻ c&oacute; thể r&uacute;t được ở ng&acirc;n h&agrave;ng ng&agrave;y (hệ thống Smartlink l&agrave; okie).\r\n<h3 style=\"text-align: justify;\"><span id=\"2727891_dugraveng_caacute_nhacircn_khi_273i_2734327901ng\"><strong>Đồ d&ugrave;ng c&aacute; nh&acirc;n khi đi đường</strong></span></h3>\r\n<p style=\"text-align: justify;\"><a href=\"http://1.bp.blogspot.com/-OGkqPTD_jUc/UrFmigh966I/AAAAAAAAELs/uMbUkXHDlzo/s1600/chuan-bi-do-khi-di-phuot-4.JPG\"><img class=\"lazyloaded\" src=\"http://1.bp.blogspot.com/-OGkqPTD_jUc/UrFmigh966I/AAAAAAAAELs/uMbUkXHDlzo/s320/chuan-bi-do-khi-di-phuot-4.JPG\" alt=\"\" width=\"568\" height=\"568\" border=\"0\" data-lazy-src=\"http://1.bp.blogspot.com/-OGkqPTD_jUc/UrFmigh966I/AAAAAAAAELs/uMbUkXHDlzo/s320/chuan-bi-do-khi-di-phuot-4.JPG\" data-was-processed=\"true\" /></a></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Mũ bảo hiểm k&iacute;n đầu hoặc &iacute;t nhất l&agrave; loại mũ nửa đầu, kh&ocirc;ng d&ugrave;ng những loại mũ bảo hiểm thời trang bởi độ an to&agrave;n thấp v&agrave; nguy cơ g&acirc;y nguy hiểm cho người đội khi c&oacute; tai nạn xảy ra. Đi phượt đ&atilde; l&agrave; mạo hiểm nhưng việc giảm được rủi ro nhiều hay &iacute;t lại phụ thuộc v&agrave;o ch&iacute;nh những việc nhỏ như n&agrave;y</li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><a href=\"https://2.bp.blogspot.com/-TPEOCpWZrBM/UrFn7WchyRI/AAAAAAAAEL4/xce9G2tfwwk/s1600/chuan-bi-do-khi-di-phuot-5.jpg\"><img class=\"lazyloaded\" src=\"https://2.bp.blogspot.com/-TPEOCpWZrBM/UrFn7WchyRI/AAAAAAAAEL4/xce9G2tfwwk/s1600/chuan-bi-do-khi-di-phuot-5.jpg\" alt=\"\" width=\"641\" height=\"577\" border=\"0\" data-lazy-src=\"https://2.bp.blogspot.com/-TPEOCpWZrBM/UrFn7WchyRI/AAAAAAAAEL4/xce9G2tfwwk/s1600/chuan-bi-do-khi-di-phuot-5.jpg\" data-was-processed=\"true\" /></a></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Bộ bọc khuỷu tay v&agrave; đầu gối để giảm chấn thương khi chẳng may c&oacute; bị ng&atilde; xe, 2 thứ n&agrave;y bạn c&oacute; thể dễ d&agrave;ng mua ở những cửa h&agrave;ng chuy&ecirc;n b&aacute;n đồ đi phượt. Nếu c&oacute; điều kiện, h&atilde;y sắm th&ecirc;m một bộ &aacute;o gi&aacute;p to&agrave;n th&acirc;n.</li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><a href=\"https://1.bp.blogspot.com/-ZTfAxhljE5Y/UrFovcXbCMI/AAAAAAAAEMA/_lon4DUjbpo/s1600/chuan-bi-do-khi-di-phuot-6.jpg\"><img class=\"lazyloaded\" src=\"https://1.bp.blogspot.com/-ZTfAxhljE5Y/UrFovcXbCMI/AAAAAAAAEMA/_lon4DUjbpo/s1600/chuan-bi-do-khi-di-phuot-6.jpg\" alt=\"\" width=\"641\" height=\"426\" border=\"0\" data-lazy-src=\"https://1.bp.blogspot.com/-ZTfAxhljE5Y/UrFovcXbCMI/AAAAAAAAEMA/_lon4DUjbpo/s1600/chuan-bi-do-khi-di-phuot-6.jpg\" data-was-processed=\"true\" /></a></p>\r\n<div class=\"code-block code-block-21\" style=\"text-align: justify;\">&nbsp;</div>\r\n<ul style=\"text-align: justify;\">\r\n<li>Một chiếc k&iacute;nh r&acirc;m hoặc k&iacute;nh trắng loại &ocirc;m s&aacute;t được v&agrave;o mắt, đối với những bạn cận c&oacute; thể d&ugrave;ng loại k&iacute;nh c&oacute; gọng đặc biệt c&oacute; thể lắp th&ecirc;m mắt cận ở ph&iacute;a trong.</li>\r\n<li>Găng tay : n&ecirc;n chọn loại găng c&oacute; mặt nh&aacute;m ph&iacute;a trong gi&uacute;p tạo độ b&aacute;m khi k&eacute;o ga. V&agrave;o m&ugrave;a đ&ocirc;ng c&oacute; thể sử dụng loại găng tay da c&oacute; khả năng chống được nước để giữ ấm cho b&agrave;n tay.</li>\r\n<li>&Aacute;o mưa bộ : chuẩn bị mỗi người (xế v&agrave; &ocirc;m) một bộ &aacute;o mưa, kh&ocirc;ng n&ecirc;n d&ugrave;ng &aacute;o mưa giấy hoặc &aacute;o mưa c&aacute;nh dơi (&aacute;o mưa tr&ugrave;m đầu) bởi sẽ l&agrave;m ảnh hưởng đến qu&aacute; tr&igrave;nh đi xe m&aacute;y.</li>\r\n<li>Ủng đi mưa , c&oacute; thể d&ugrave;ng ủng nilon loại đi một lần rồi đ&aacute;p hoặc mua một đ&ocirc;i ủng tử tế để d&ugrave;ng nhiều lần.</li>\r\n<li>T&uacute;i đeo bụng d&ugrave;ng để đựng c&aacute;c loại giấy tờ, v&iacute;, c&aacute;c thiết bị điện tử để cho gọn, tr&aacute;nh vướng v&iacute;u khi ngồi tr&ecirc;n xe m&aacute;y.</li>\r\n<li>B&igrave;nh nước giữ nhiệt v&agrave;o m&ugrave;a đ&ocirc;ng (m&ugrave;a h&egrave; th&igrave; mua mấy chai lavie cho tiện).</li>\r\n</ul>\r\n<h3 style=\"text-align: justify;\"><span id=\"Caacutec_2737891_dugraveng_chung_cho_c7843_273oagraven\"><strong>C&aacute;c đồ d&ugrave;ng chung cho cả đo&agrave;n</strong></span></h3>\r\n<p style=\"text-align: justify;\"><a href=\"https://4.bp.blogspot.com/-WCkxYWtTjxM/UrFuvy3txjI/AAAAAAAAENE/qP218NxBybA/s1600/chuan-bi-do-khi-di-phuot-10.jpg\"><img class=\"lazyloaded\" src=\"https://4.bp.blogspot.com/-WCkxYWtTjxM/UrFuvy3txjI/AAAAAAAAENE/qP218NxBybA/s1600/chuan-bi-do-khi-di-phuot-10.jpg\" alt=\"\" width=\"640\" height=\"640\" border=\"0\" data-lazy-src=\"https://4.bp.blogspot.com/-WCkxYWtTjxM/UrFuvy3txjI/AAAAAAAAENE/qP218NxBybA/s1600/chuan-bi-do-khi-di-phuot-10.jpg\" data-was-processed=\"true\" /></a></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>T&uacute;i nilon to v&agrave; nhỏ, loại 20kg d&ugrave;ng để bọc balo, loại 5-10kg d&ugrave;ng để đựng đồ linh tinh, quần &aacute;o bẩn. C&aacute;c loại t&uacute;i n&agrave;y c&oacute; thể mua ở H&agrave;ng Chiếu.</li>\r\n<li>Ổ cắm nối d&agrave;i : đặc th&ugrave; c&aacute;c kh&aacute;ch sạn nh&agrave; nghỉ thường kh&ocirc;ng c&oacute; nhiều ổ cắm trong khi anh em trong đo&agrave;n th&igrave; c&oacute; v&ocirc; số thiết bị điện tử mang theo cần sạc n&ecirc;n l&agrave; kiểu g&igrave; cũng tranh nhau, cả đo&agrave;n n&ecirc;n mang theo v&agrave;i c&aacute;i ổ Lioa cho chắc chắn.</li>\r\n<li>M&aacute;y sấy : sau mỗi ng&agrave;y di chuyển th&igrave; c&oacute; thể một số trang phục (khăn, găng tay, khẩu trang ..) c&oacute; thể bị ướt m&agrave; kh&ocirc;ng thể giặt ngay th&igrave; m&aacute;y sấy l&agrave; cứu c&aacute;nh trong những trường hợp n&agrave;y. Ngo&agrave;i ra n&oacute; c&ograve;n c&oacute; t&aacute;c dụng l&agrave;m kh&ocirc; đầu t&oacute;c nhanh để c&oacute; thể sớm đi ngủ buổi tối (nhất l&agrave; với c&aacute;c bạn nữ).</li>\r\n<li>Bộ dụng cụ y tế n&ecirc;n c&oacute; những thứ sau : Thuốc đau bụng Berberin, thuốc cảm, b&ocirc;ng băng, cồn s&aacute;t tr&ugrave;ng, miếng d&aacute;n hạ sốt, vi&ecirc;n sủi giảm sốt, một v&agrave;i vi&ecirc;n giảm đau, Orezol để b&ugrave; nước, một hộp salonpas loại d&aacute;n v&agrave; 1 tu&yacute;p salonpas gel &hellip;</li>\r\n<li>Đi v&agrave;o m&ugrave;a lạnh th&igrave; mang theo &iacute;t miếng d&aacute;n giữ nhiệt.</li>\r\n<li>Đồ ăn thức uống tr&ecirc;n đường : N&ecirc;n mua c&aacute;c loại b&aacute;nh kh&ocirc; ăn nhanh, Chocolate, nước lọc, một v&agrave;i lọ Redbull, tr&agrave;, coffee &hellip; (nhớ mang theo &iacute;t tr&agrave; gừng)</li>\r\n<li>Chuyến đi m&agrave; phải đi bộ nhiều th&igrave; mang theo &iacute;t đường gluco, pha v&agrave;o nước uống cho đỡ mỏi cơ.</li>\r\n<li>Giấy ăn, khăn ướt.</li>\r\n<li>Nếu đo&agrave;n bạn th&iacute;ch nh&atilde;, mang theo v&agrave;i c&aacute;i ca bằng kim loại&nbsp;+ 1 g&oacute;i cồn kh&ocirc;, l&uacute;c n&agrave;o nghỉ th&igrave; l&ocirc;i ra đun nước pha tr&agrave; uống.</li>\r\n<li>Một cuộn d&acirc;y d&ugrave; d&agrave;i v&agrave;o, khoảng tầm 20-30m, l&agrave;m g&igrave; th&igrave; chưa biết nhưng kiểu g&igrave; cũng c&oacute; l&uacute;c d&ugrave;ng đến (k&eacute;o xe chẳng hạn)</li>\r\n<li>Một v&agrave;i đoạn ruy băng m&agrave;u s&aacute;ng (xanh kiểu m&agrave;u xanh phản quang chẳng hạn) d&ugrave;ng để l&agrave;m dấu hiệu nhận biết cho xe đi sau đối với một số đoạn đường vắng người qua lại v&agrave; đo&agrave;n bị chia t&aacute;ch m&agrave; kh&ocirc;ng li&ecirc;n lạc được.</li>\r\n</ul>\r\n<h3 style=\"text-align: justify;\"><span id=\"Caacutec_2737891_dugraveng_c7847n_thi7871t_khaacutec\"><strong>C&aacute;c đồ d&ugrave;ng cần thiết kh&aacute;c</strong></span></h3>\r\n<p style=\"text-align: justify;\"><a href=\"https://2.bp.blogspot.com/-sx_y2N_DRh8/UrFpIse9v9I/AAAAAAAAEMI/nqynEW2DYLo/s1600/chuan-bi-do-khi-di-phuot-7.jpg\"><img class=\"lazyloaded\" src=\"https://2.bp.blogspot.com/-sx_y2N_DRh8/UrFpIse9v9I/AAAAAAAAEMI/nqynEW2DYLo/s1600/chuan-bi-do-khi-di-phuot-7.jpg\" alt=\"\" width=\"640\" height=\"533\" border=\"0\" data-lazy-src=\"https://2.bp.blogspot.com/-sx_y2N_DRh8/UrFpIse9v9I/AAAAAAAAEMI/nqynEW2DYLo/s1600/chuan-bi-do-khi-di-phuot-7.jpg\" data-was-processed=\"true\" /></a></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Dao đa năng, đ&egrave;n pin, bật lửa</li>\r\n<li>Điện thoại (chọn loại s&oacute;ng tốt pin tr&acirc;u l&agrave; tốt nhất), mang theo smartphone th&igrave; nhớ kiếm mấy cục pin dự ph&ograve;ng</li>\r\n<li>M&aacute;y ảnh (nếu kh&ocirc;ng chơi ảnh v&agrave; muốn nhẹ nh&agrave;ng th&igrave; mang mấy c&aacute;i m&aacute;y du lịch th&ocirc;i cho gọn, tất nhi&ecirc;n nếu c&oacute; m&aacute;y xịn, &nbsp;muốn ảnh đẹp v&agrave; kh&ocirc;ng x&oacute;t m&aacute;y, c&aacute;c bạn cứ tự nhi&ecirc;n sử dụng).</li>\r\n</ul>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://1.bp.blogspot.com/-xEHKSA0l6WI/UrFpNbCuBRI/AAAAAAAAEMQ/wGIteAtekP8/s1600/keep-phone.jpg\"><img class=\"lazyloaded\" src=\"https://1.bp.blogspot.com/-xEHKSA0l6WI/UrFpNbCuBRI/AAAAAAAAEMQ/wGIteAtekP8/s1600/keep-phone.jpg\" alt=\"\" width=\"640\" height=\"491\" border=\"0\" data-lazy-src=\"https://1.bp.blogspot.com/-xEHKSA0l6WI/UrFpNbCuBRI/AAAAAAAAEMQ/wGIteAtekP8/s1600/keep-phone.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>Bao cao su c&oacute; khả năng chống nước cho thiết bị điện tử rất tốt</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://4.bp.blogspot.com/-u3c0rBJumy4/UrFpNhgUdBI/AAAAAAAAEMU/qGMUGNyEI2E/s1600/su-dung-bang-ve-sinh-de-lot-giay.jpg\"><img class=\"lazyloaded\" src=\"https://4.bp.blogspot.com/-u3c0rBJumy4/UrFpNhgUdBI/AAAAAAAAEMU/qGMUGNyEI2E/s1600/su-dung-bang-ve-sinh-de-lot-giay.jpg\" alt=\"\" width=\"477\" height=\"640\" border=\"0\" data-lazy-src=\"https://4.bp.blogspot.com/-u3c0rBJumy4/UrFpNhgUdBI/AAAAAAAAEMU/qGMUGNyEI2E/s1600/su-dung-bang-ve-sinh-de-lot-giay.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>L&oacute;t BVS v&agrave;o dưới đế gi&agrave;y c&oacute; khả năng h&uacute;t ẩm cao, tr&aacute;nh nhiễm lạnh cho gan b&agrave;n ch&acirc;n</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<ul style=\"text-align: justify;\">\r\n<li>Bao cao su v&agrave; băng vệ sinh (đừng nghĩ g&igrave; nh&eacute;, đơn giản l&agrave; d&ugrave;ng bao cao su để nh&eacute;t đồ điện tử v&agrave;o chống ướt v&agrave; băng vệ sinh l&oacute;t đế gi&agrave;y để tr&aacute;nh bị ngấm lạnh gan b&agrave;n ch&acirc;n (nhất l&agrave; khi đi rừng)</li>\r\n</ul>\r\n<h3 style=\"text-align: justify;\"><span id=\"2727891_dugraveng_liecircn_quan_2737871n_ph432417ng_ti7879n_273i_l7841i\"><strong>Đồ d&ugrave;ng li&ecirc;n quan đến phương tiện đi lại</strong></span></h3>\r\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"https://2.bp.blogspot.com/-6a-i6lxFyZU/UrFr0O3kHFI/AAAAAAAAEMs/zIQm84nm5s8/s1600/chuan-bi-do-khi-di-phuot-9.jpg\"><img class=\"lazyloaded\" src=\"https://2.bp.blogspot.com/-6a-i6lxFyZU/UrFr0O3kHFI/AAAAAAAAEMs/zIQm84nm5s8/s1600/chuan-bi-do-khi-di-phuot-9.jpg\" alt=\"\" width=\"640\" height=\"424\" border=\"0\" data-lazy-src=\"https://2.bp.blogspot.com/-6a-i6lxFyZU/UrFr0O3kHFI/AAAAAAAAEMs/zIQm84nm5s8/s1600/chuan-bi-do-khi-di-phuot-9.jpg\" data-was-processed=\"true\" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><em>C&oacute; tiền l&agrave;m bộ n&agrave;y rồi mỗi chuyến nhặt ra v&agrave;i thứ mang đi th&igrave; tuyệt :))</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"caps\" style=\"text-align: justify;\">Ch&uacute; &yacute; : Việc tự sửa xe tr&ecirc;n đường chỉ mang t&iacute;nh nhất thời để đảm bảo h&agrave;nh tr&igrave;nh, ngay khi tới được địa điểm c&oacute; h&agrave;ng sửa xe chuy&ecirc;n dụng bạn n&ecirc;n mang xe v&agrave;o th&ocirc;ng b&aacute;o với họ về lỗi v&agrave; t&igrave;nh trạng hiện tại để được kiểm tra lại một c&aacute;ch to&agrave;n diện nhất nhằm đảm bảo an to&agrave;n.</p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Gương chiếu hậu : Luật giao th&ocirc;ng đường bộ quy định chỉ cần c&oacute; gương b&ecirc;n tr&aacute;i tuy nhi&ecirc;n tốt nhất bạn n&ecirc;n lắp đủ cả 2 gương để tăng tầm quan s&aacute;t tr&ecirc;n đường.</li>\r\n<li>Đồ sửa xe m&aacute;y cơ bản : &nbsp;Khi hỏng xe tại khu vực c&oacute; d&acirc;n cư tốt nhất h&atilde;y mang xe đến thẳng h&agrave;ng, vừa nhanh lại vừa đảm bảo. Chỉ khi đi v&agrave;o những khu vực thưa thớt hoặc kh&ocirc;ng c&oacute; d&acirc;n sinh sống th&igrave; mới phải tự m&igrave;nh sửa xe, ở đ&acirc;y ch&uacute;ng m&igrave;nh chỉ gợi &yacute; một số dụng cụ cơ bản để sửa chữa những lỗi nhỏ tr&ecirc;n đường : 1 bộ m&oacute;c lốp, 1 bộ dụng cụ v&aacute; xe (keo, miếng v&aacute;, miếng ch&agrave; săm, 1 bơm tay hoặc bơm ch&acirc;n t&ugrave;y bạn), 1 k&igrave;m, 1 mỏ lết loại nhỏ, 1 bộ cờ l&ecirc; (loại 1 đầu mở, 1 đầu tr&ograve;n) &nbsp;c&oacute; k&iacute;ch thước lần lượt 10-12-14-17-19mm, v&agrave;i thanh chữ T (d&ugrave;ng để vặn ốc ở phanh sau, th&aacute;o ốc thay dầu), 1 tuốc nơ v&iacute;t (4 cạnh v&agrave; 2 cạnh), 1 bộ đồ th&aacute;o bugi</li>\r\n<li>Mang theo 2 săm dự trữ ph&ugrave; hợp với từng xe (phổ biến l&agrave; c&aacute;c loại 225-250-275) săm b&aacute;nh sau thường to hơn săm b&aacute;nh trước. C&aacute;c d&ograve;ng Dream v&agrave; Wave thường l&agrave; 225-250, c&aacute;c d&ograve;ng Jupiter v&agrave; Future thường l&agrave; 250-275 (c&aacute;c bạn xem lại tr&ecirc;n lốp xe của m&igrave;nh cho chắc nh&eacute;).</li>\r\n<li>1 v&agrave;i lọ keo tự v&aacute; Tiến Hưng (tốt nhất l&agrave; n&ecirc;n đổ v&agrave;o săm trước khi đi)</li>\r\n<li>Một v&ograve;i cao su d&agrave;i khoảng 1m d&ugrave;ng để h&uacute;t xăng khi cần thiết.</li>\r\n<li>Mang theo 1 bugi ph&ugrave; hợp với xe của m&igrave;nh.</li>\r\n<li>D&acirc;y cao su : d&ugrave;ng để buộc chặt đồ v&agrave;o xe để khi di chuyển kh&ocirc;ng bị văng hay rơi đồ.</li>\r\n<li>Th&ecirc;m một bộ ch&igrave;a kh&oacute;a xe m&aacute;y, trong trường hợp t&iacute;nh bạn hay qu&ecirc;n v&agrave; kh&ocirc;ng cẩn thận th&igrave; cứ mang đi, chẳng thừa đ&acirc;u. Giao 1 bộ cho bạn c&ograve;n lại đi c&ugrave;ng xe với m&igrave;nh (hoặc xe kh&aacute;c, t&ugrave;y bạn) cho chắc</li>\r\n</ul>\r\n<h3 style=\"text-align: justify;\"><span id=\"Ki7875m_tra_xe_tr4327899c_khi_273i\"><strong>Kiểm tra xe trước khi đi</strong></span></h3>\r\n<p style=\"text-align: justify;\"><a href=\"http://4.bp.blogspot.com/-exUIDrRVyJ4/UrFqM3HqS5I/AAAAAAAAEMg/hncydYtSsHU/s1600/chuan-bi-do-khi-di-phuot-8.JPG\"><img class=\"lazyloaded\" src=\"http://4.bp.blogspot.com/-exUIDrRVyJ4/UrFqM3HqS5I/AAAAAAAAEMg/hncydYtSsHU/s1600/chuan-bi-do-khi-di-phuot-8.JPG\" alt=\"\" width=\"640\" height=\"403\" border=\"0\" data-lazy-src=\"http://4.bp.blogspot.com/-exUIDrRVyJ4/UrFqM3HqS5I/AAAAAAAAEMg/hncydYtSsHU/s1600/chuan-bi-do-khi-di-phuot-8.JPG\" data-was-processed=\"true\" /></a></p>\r\n<ul>\r\n<li style=\"text-align: justify;\">Nếu kh&ocirc;ng biết g&igrave; th&igrave; mang xe đi bảo dưỡng to&agrave;n bộ cho l&agrave;nh (nếu biết một ch&uacute;t th&igrave; cũng vẫn mang đi bảo dưỡng nhưng ch&uacute; &yacute; mấy điều ph&iacute;a dưới nữa :3)</li>\r\n<li style=\"text-align: justify;\">L&agrave;m lọ dầu (nhớt) mới đi cho m&aacute;t m&aacute;y, m&aacute; phanh m&ograve;n th&igrave; thay, hết dầu phanh th&igrave; đổ, săm c&oacute; vết v&aacute; th&igrave; chuyển c&aacute;i mới (đi đường d&agrave;i lốp n&oacute;ng, vết v&aacute; dễ bị bong), lốp m&ograve;n rồi th&igrave; đừng c&oacute; tiếc. Ch&uacute; &yacute; với c&aacute;c bạn đi xe của Yamaha th&igrave; đừng d&ugrave;ng lốp gốc của xe, c&aacute;i lốp đấy chỉ chạy đường phố th&ocirc;i, đi đường n&uacute;i b&aacute;m đường với b&aacute;m cua ch&aacute;n lắm. C&aacute;c bạn cứ thay lốp Future m&agrave; đi v&igrave; n&oacute; c&ugrave;ng k&iacute;ch thước lốp với nhau, hoặc chọn lu&ocirc;n lốp Inoue.</li>\r\n<li style=\"text-align: justify;\">Nh&ocirc;ng x&iacute;ch m&agrave; m&ograve;n qu&aacute; th&igrave; l&agrave;m bộ mới, căng x&iacute;ch l&ecirc;n một ch&uacute;t, đừng để tr&ugrave;ng qu&aacute; đến l&uacute;c leo dốc chả c&oacute; t&iacute; lực n&agrave;o đ&acirc;u</li>\r\n<li style=\"text-align: justify;\">C&aacute;c loại đ&egrave;n xem c&oacute; s&aacute;ng kh&ocirc;ng, đ&egrave;n hậu c&oacute; s&aacute;ng khi đạp/b&oacute;p phanh (thắng) kh&ocirc;ng.</li>\r\n<li style=\"text-align: justify;\">Kiểm tra xem ắc quy okie kh&ocirc;ng, đi đường m&agrave; chết ắc quy th&igrave; cũng ng&aacute;n đ&oacute;</li>\r\n</ul>\r\n</li>\r\n</ul>', '14324379415d0917cbc0cad9.99694481.jpg', '2019-06-18', 1),
(55, 29, 'Hạnh phúc lang thang', '<p style=\"text-align: justify;\"><strong>Hạnh ph&uacute;c lang thang l&agrave; t&ecirc;n một quyển s&aacute;ch rất nổi tiếng trong giới đi Phượt, n&oacute; l&agrave; đ&uacute;c kết những trải nghiệm của c&aacute;c anh, chị c&oacute; th&acirc;m ni&ecirc;n trong việc đi du lịch bụi. V&agrave; t&ocirc;i, một người đam m&ecirc; Phượt xin mượn ph&eacute;p mượn c&aacute;i ti&ecirc;u đề tr&ecirc;n để được gi&atilde;i b&agrave;y những suy nghĩ, kh&aacute;t khao được l&ecirc;n đường của ch&iacute;nh bản th&acirc;n m&igrave;nh.</strong></p>', '<p style=\"text-align: justify;\"><a href=\"http://localhost:8080/newddp/wp-content/uploads/2016/05/o-TRAVELING-ALONE-facebook.jpg\"><img class=\"aligncenter size-full wp-image-555\" src=\"http://localhost:8080/newddp/wp-content/uploads/2016/05/o-TRAVELING-ALONE-facebook.jpg\" sizes=\"(max-width: 768px) 100vw, 768px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2016/05/o-TRAVELING-ALONE-facebook.jpg 768w, http://dandiphuot.com/wp-content/uploads/2016/05/o-TRAVELING-ALONE-facebook-300x168.jpg 300w\" alt=\"travel-alone\" width=\"768\" height=\"429\" /></a></p>\r\n<p style=\"text-align: justify;\">Phượt với t&ocirc;i l&agrave; đam m&ecirc; chứ kh&ocirc;ng phải &aacute;p lực, khi n&agrave;o th&iacute;ch v&agrave; ph&ugrave; hợp l&agrave; t&ocirc;i lại l&ecirc;n đường, kh&ocirc;ng cắm đầu cắm cổ đi cho bằng chị, bằng em m&agrave; mỗi chuyến đi t&ocirc;i cố gắng trải nghiệm được nhiều nhất c&oacute; thể ở những v&ugrave;ng đất mới. C&oacute; thể t&ocirc;i chưa đi nhiều nhưng t&ocirc;i chắc chắn rằng mỗi chuyến đi của m&igrave;nh l&agrave; một h&agrave;nh tr&igrave;nh k&igrave; th&uacute; v&agrave; đầy ắp những kỉ niệm. Phượt với t&ocirc;i l&agrave; thế đ&oacute;.</p>\r\n<p style=\"text-align: justify;\">Khi c&ograve;n ở c&aacute;i tuổi 18, 20 gần như th&aacute;ng n&agrave;o t&ocirc;i cũng phải kiếm cho m&igrave;nh một l&iacute; do để l&ecirc;n đường, c&oacute; thể chỉ đi đ&acirc;u đ&oacute; trong ng&agrave;y hay chỉ l&agrave; về nh&agrave; đứa bạn chơi 1, 2 bữa l&agrave; thấy sướng rồi. Cảm gi&aacute;c rời khỏi th&agrave;nh phố chật chội, x&ocirc; bồ v&agrave; lao m&igrave;nh về những v&ugrave;ng qu&ecirc; y&ecirc;n ả cũng l&agrave;m cho t&ocirc;i mỉm cười sung sướng khi nhớ lại.</p>\r\n<p style=\"text-align: justify;\">C&aacute;i tuổi đầy nhiệt huyết v&agrave; năng lượng đ&oacute; gi&uacute;p t&ocirc;i kh&ocirc;ng biết sợ l&agrave; g&igrave;, th&iacute;ch l&agrave; đi, muốn l&agrave; l&agrave;m chẳng cần suy nghĩ nhiều tới những việc kh&aacute;c, đ&uacute;ng l&agrave; tuổi trẻ ng&acirc;y thơ v&agrave; bồng bột. Ơn trời chưa c&oacute; hậu quả n&agrave;o qu&aacute; lớn xảy ra, t&ocirc;i nhớ những đ&ecirc;m dậy từ 1,2h s&aacute;ng v&agrave; lao xe ra đường, khi th&agrave;nh phố c&ograve;n ch&igrave;m trong giấc ngủ, đ&acirc;u c&oacute; biết được ngo&agrave;i kia đang c&oacute; đứa lồng lộn l&ecirc;n để chinh phục những cung đường v&agrave; cũng để thỏa m&atilde;n đam m&ecirc; của n&oacute;.</p>\r\n<p style=\"text-align: justify;\">Giờ đ&acirc;y ở c&aacute;i tuổi kh&ocirc;ng gi&agrave; m&agrave; cũng chả c&ograve;n trẻ nữa, kh&ocirc;ng c&ograve;n c&aacute;i cảnh th&iacute;ch l&agrave; ph&oacute;ng đi ngay hay muốn l&agrave;m g&igrave; th&igrave; l&agrave;m. Mỗi chuyến đi với t&ocirc;i l&agrave; một sự chuẩn bị kĩ c&agrave;ng để c&oacute; thể trải nghiệm được nhiều thứ hơn tr&ecirc;n h&agrave;nh tr&igrave;nh của m&igrave;nh. Nhưng vẫn c&ograve;n đ&oacute; kh&aacute;t khao kh&aacute;m ph&aacute;, chinh phục v&agrave; t&igrave;m hiểu về những v&ugrave;ng đất lạ. N&oacute; như một thứ ma t&uacute;y cực mạnh m&agrave; t&ocirc;i kh&ocirc;ng thể n&agrave;o từ bỏ hay dứt ra được. T&ocirc;i y&ecirc;u những cung đường.</p>\r\n<p style=\"text-align: justify;\">Những ng&agrave;y ngồi nh&agrave; v&agrave; v&ugrave;i đầu trong c&ocirc;ng việc, l&acirc;u l&acirc;u lượn lờ tr&ecirc;n mạng thấy những th&ocirc;ng tin về c&aacute;c chuyến đi Phượt của c&aacute;c bạn trẻ 9x ng&agrave;y nay. &Ocirc;i sao m&agrave; giống m&igrave;nh của nhiều năm về trước thế, nhưng hơi kh&aacute;c một ch&uacute;t l&agrave; c&oacute; rất nhiều bạn bản lĩnh hơn rất nhiều, những địa danh họ tới kh&ocirc;ng chỉ g&oacute;i gọn trong mảnh đất h&igrave;nh chữ S nữa m&agrave; đ&atilde; vươn m&igrave;nh ra khắp c&aacute;c ch&acirc;u lục. Mơ ước của t&ocirc;i về một thế hệ ta balo h&ograve;a m&igrave;nh v&agrave;o biển lớn, mạnh dạn đi, mạnh dạn sống v&agrave; trải nghiệm đ&atilde; sắp th&agrave;nh hiện thực rồi chăng.</p>\r\n<p style=\"text-align: justify;\">Nhiều người cho rằng ĐI như một t&ocirc;n gi&aacute;o, n&oacute; mang đến cho c&aacute;c t&iacute;n đồ sự b&igrave;nh y&ecirc;n trong t&acirc;m hồn khi gặp kh&oacute; khăn, bế tắc trong cuộc sống. Một sự so s&aacute;nh t&iacute;nh ra cũng kh&ocirc;ng c&oacute; g&igrave; l&agrave; qu&aacute; khập khiễng. H&atilde;y tưởng tượng m&agrave; xem, một ng&agrave;y đẹp trời ngồi ngắm nh&igrave;n biển m&acirc;y lặng lẽ tr&ocirc;i qua ở v&ugrave;ng T&acirc;y Bắc hay được tiếp x&uacute;c với đồng b&agrave;o d&acirc;n tộc v&ugrave;ng cao, chứng kiến cuộc sống kh&oacute; khăn nhưng họ vẫn lu&ocirc;n vui vẻ, lạc quan v&agrave; y&ecirc;u đời th&igrave; t&ocirc;i chắc rằng bạn vẫn c&ograve;n may mắn ch&aacute;n khi mỗi ng&agrave;y c&oacute; đủ cơm ăn ba bữa.</p>\r\n<p style=\"text-align: justify;\">Đi l&agrave; bạn rời khỏi m&ocirc;i trường sống quen thuộc của m&igrave;nh, kh&ocirc;ng c&ograve;n những mối quan hệ r&agrave;ng buộc, tr&ecirc;n h&agrave;nh tr&igrave;nh phi&ecirc;u lưu ấy bạn phải tập l&agrave;m quen, th&iacute;ch nghi với m&ocirc;i trường văn h&oacute;a mới, với những con người ho&agrave;n to&agrave;n xa lạ. Những điều đ&oacute; c&oacute; gi&uacute;p &iacute;ch được g&igrave; cho t&ocirc;i kh&ocirc;ng? Xin thưa l&agrave; c&oacute;, c&oacute; rất nhiều nữa l&agrave; đằng kh&aacute;c. Những vấn đề t&ocirc;i gặp phải, n&oacute;i chuyện với ai, giải quyết như thế n&agrave;o đều dạy cho t&ocirc;i những kĩ năng m&agrave; ở nh&agrave; t&ocirc;i kh&ocirc;ng bao giờ biết được. Kh&ocirc;ng biết với c&aacute;c bạn th&igrave; sao nhỉ?</p>\r\n<p style=\"text-align: justify;\">Vậy đ&oacute; mỗi ng&agrave;y tr&ocirc;i qua, mỗi chuyến đi đ&atilde; thực hiện xong v&agrave; những dự định trong tương lai, t&ocirc;i vẫn giữ cho m&igrave;nh c&aacute;i nhiệt huyết, đam m&ecirc; của tuổi 20 nhưng c&oacute; sự điềm đạm, ch&iacute;n chắn của người trưởng th&agrave;nh để mỗi chuyến đi l&agrave; một b&agrave;i học lớn trong cuộc đời của m&igrave;nh.</p>\r\n<div class=\"google-auto-placed ap_container\">&nbsp;</div>', '9942531135d091afde84544.80056266.jpg', '2019-06-19', 0),
(56, 29, 'Du lịch có trách nhiệm và Rác – Bạn chọn cái nào?', '<p style=\"text-align: justify;\"><strong>Những ng&agrave;y trước lễ dạo một v&ograve;ng quanh nh&oacute;m, c&aacute;c trang mạng x&atilde; hội lẫn những trang tin tức online, đ&acirc;u đ&acirc;u cũng thấy sự h&aacute;o hức, hồ hởi của c&aacute;c bạn trẻ chuẩn bị cho chuyến du lịch của m&igrave;nh. Tuy nhi&ecirc;n khi g&otilde; những d&ograve;ng n&agrave;y, cảm x&uacute;c của t&ocirc;i c&oacute; phần hụt hẫng v&agrave; kh&oacute; chịu v&igrave; những h&igrave;nh ảnh kh&ocirc;ng đẹp n&oacute; đập thẳng v&agrave;o mắt m&igrave;nh. Đ&oacute; ch&iacute;nh l&agrave; R&Aacute;C, qu&aacute; nhiều r&aacute;c tại những điểm du lịch, đặc biệt l&agrave; những điểm du lịch nổi tiếng. Kh&ocirc;ng biết khi n&agrave;o d&acirc;n Phượt n&oacute;i ri&ecirc;ng v&agrave; du kh&aacute;ch n&oacute;i chung mới c&oacute; &yacute; thức bảo vệ m&ocirc;i trường đ&acirc;y nhỉ?</strong></p>', '<p style=\"text-align: justify;\"><a href=\"http://localhost:8080/newddp/wp-content/uploads/2016/05/image004-1453774618.jpg\"><img class=\"aligncenter size-full wp-image-460\" src=\"http://localhost:8080/newddp/wp-content/uploads/2016/05/image004-1453774618.jpg\" sizes=\"(max-width: 624px) 100vw, 624px\" srcset=\"http://dandiphuot.com/wp-content/uploads/2016/05/image004-1453774618.jpg 624w, http://dandiphuot.com/wp-content/uploads/2016/05/image004-1453774618-150x150.jpg 150w, http://dandiphuot.com/wp-content/uploads/2016/05/image004-1453774618-300x300.jpg 300w, http://dandiphuot.com/wp-content/uploads/2016/05/image004-1453774618-160x160.jpg 160w, http://dandiphuot.com/wp-content/uploads/2016/05/image004-1453774618-320x320.jpg 320w\" alt=\"Bảo vệ m&ocirc;i trường\" width=\"624\" height=\"624\" /></a></p>\r\n<p style=\"text-align: justify;\"><strong>&Yacute; thức của người đi du lịch?</strong></p>\r\n<p style=\"text-align: justify;\">&nbsp;T&ocirc;i kh&ocirc;ng c&oacute; &yacute; muốn đ&aacute;nh đồng tất cả những người đi Phượt đều l&agrave; những người k&eacute;m &yacute; thức, nhưng quả thực nh&igrave;n những h&igrave;nh ảnh r&aacute;c ngập tr&agrave;n c&aacute;c điểm du lịch kh&ocirc;ng khỏi l&agrave;m t&ocirc;i cảm thấy ch&aacute;n ng&aacute;n về &yacute; thức của những du kh&aacute;ch Việt Nam. Phải n&oacute;i l&agrave; qu&aacute; k&eacute;m v&agrave; kh&ocirc;ng thể chấp nhận được. Điểm qua một v&agrave;i địa danh nh&eacute;.</p>\r\n<p style=\"text-align: justify;\">Thứ nhất v&agrave; nặng nề nhất chắc chắn sẽ l&agrave; th&agrave;nh phố sương m&ugrave; Đ&agrave; Lạt, nếu những ai chưa đi Đ&agrave; Lạt m&agrave; xem h&igrave;nh ảnh tr&ecirc;n b&aacute;o ch&iacute; chắc nghĩ rằng Đ&agrave; Lạt chỉ to&agrave;n l&agrave; r&aacute;c với r&aacute;c, nhất l&agrave; ở khu vực chợ đ&ecirc;m Đ&agrave; Lạt v&agrave; xung quanh hồ Xu&acirc;n Hương, c&ograve;n đ&acirc;u h&igrave;nh ảnh một Đ&agrave; Lạt sương m&ugrave; bảng lảng v&agrave; l&atilde;ng mạn l&agrave;m xao xuyến bao người, r&aacute;c nhiều đến nỗi m&agrave; người d&acirc;n bản địa cũng phải ca th&aacute;n v&agrave; than trời l&ecirc;n. Bản th&acirc;n t&ocirc;i l&agrave; người rất y&ecirc;u Đ&agrave; Lạt nhưng cũng tự dặn m&igrave;nh l&agrave; nếu c&oacute; đi th&igrave; chọn ng&agrave;y thường m&agrave; đi chứ đừng l&ecirc;n đ&oacute; v&agrave; dịp lễ.</p>\r\n<p style=\"text-align: justify;\">V&iacute; dụ tiếp theo l&agrave; biển cồn V&agrave;nh ở Th&aacute;i B&igrave;nh, thực sự l&agrave; t&ocirc;i kh&ocirc;ng c&ograve;n tin v&agrave;o mắt m&igrave;nh nữa, một b&atilde;i biển tuy kh&ocirc;ng phải l&agrave; qu&aacute; đẹp nhưng cũng rất sạch sẽ trước đ&oacute;, nhưng chỉ qua một v&agrave;i ng&agrave;y từ ch&iacute;nh x&aacute;c nhất để chỉ điểm du lịch n&agrave;y l&agrave; B&Atilde;I R&Aacute;C chứ kh&ocirc;ng phải l&agrave; b&atilde;i biển nữa. Chỉ mới kể sơ sơ hai điểm du lịch th&ocirc;i m&agrave; đ&atilde; thấy ng&aacute;n ngẩm qu&aacute; rồi, ngo&agrave;i ra c&aacute;c nơi kh&aacute;c như Nha Trang, Sapa, đỉnh Phanxipang cũng ngập tr&agrave;n du kh&aacute;ch v&agrave; đương nhi&ecirc;n l&agrave; cũng ngập r&aacute;c, những điểm du lịch mới nổi như Ph&uacute; Y&ecirc;n, Quy Nhơn những năm trước kh&ocirc;ng nhiều du kh&aacute;ch th&igrave; m&ugrave;a lễ năm nay cũng chật n&iacute;ch du kh&aacute;ch gh&eacute; thăm.</p>\r\n<p style=\"text-align: justify;\"><strong>L&agrave;m sao để n&acirc;ng cao &yacute; thức tr&aacute;ch nhiệm của du kh&aacute;ch khi đi du lịch?</strong></p>\r\n<p style=\"text-align: justify;\">Vấn đề &yacute; thức của du kh&aacute;ch khi đi du lịch l&agrave; c&acirc;u chuyện khổ lắm n&oacute;i m&atilde;i, n&oacute;i từ năm n&agrave;y sang năm kh&aacute;c m&agrave; vẫn chưa c&oacute; biện ph&aacute;p giải quyết rốt r&aacute;o v&agrave; tận gốc. Về chuyện n&agrave;y t&ocirc;i cũng c&oacute; một v&agrave;i &yacute; kiến như sau: thứ nhất &yacute; thức của du kh&aacute;ch phải bắt nguồn từ ch&iacute;nh bản th&acirc;n của mỗi người khi đi du lịch, mỗi du kh&aacute;ch chỉ cần l&agrave;m tốt việc của ch&iacute;nh m&igrave;nh l&agrave; h&atilde;y dọn dẹp sạch r&aacute;c của m&igrave;nh mang theo khi đi du lịch, h&atilde;y nhớ &ldquo;Kh&ocirc;ng để lại g&igrave; ngo&agrave;i những dấu ch&acirc;n v&agrave; kh&ocirc;ng lấy đi g&igrave; ngo&agrave;i những bức ảnh&rdquo;.</p>\r\n<p style=\"text-align: justify;\">Vấn đề tiếp theo phải đến từ ch&iacute;nh quyền của c&aacute;c điểm du lịch, mới gần đ&acirc;y Vũng T&agrave;u ban h&agrave;nh biện ph&aacute;p cấm kinh doanh ăn uống ngay tại b&atilde;i biển, l&uacute;c mới thực hiện th&igrave; cũng gặp nhiều phản ứng tr&aacute;i chiều đến từ những người kinh doanh lẫn du kh&aacute;ch, tuy nhi&ecirc;n hiệu quả đến ngay tức th&igrave; ngay đợt lễ năm nay, b&atilde;i biển Vũng T&agrave;u sạch sẽ, kh&ocirc;ng hề c&oacute; r&aacute;c, thu h&uacute;t đ&ocirc;ng du kh&aacute;ch hơn mọi năm v&agrave; ai cũng thấy h&agrave;i l&ograve;ng. Hay như Đ&agrave; Nẵng ngo&agrave;i việc tuy&ecirc;n truyền tới những hộ kinh doanh tại c&aacute;c b&atilde;i biển th&igrave; đ&atilde; c&oacute; những biện ph&aacute;p như ba h&agrave;nh bộ quy tắc ứng xử văn minh khi đi du lịch th&ocirc;ng qua những h&igrave;nh ảnh sinh động, gần gũi v&agrave; g&acirc;y được tiếng vang rất lớn, chả tr&aacute;ch sao Đ&agrave; Nẵng lu&ocirc;n được du kh&aacute;ch tin y&ecirc;u v&agrave; nhiều năm liền trở th&agrave;nh nơi đ&aacute;ng sống nhất ở nước ta.</p>\r\n<p style=\"text-align: justify;\">Một điều m&agrave; t&ocirc;i thấy n&ecirc;n l&agrave;m nữa l&agrave; người d&acirc;n bản địa ở những điểm du lịch h&atilde;y mạnh dạn n&oacute;i l&ecirc;n quan điểm của m&igrave;nh, phản đối những h&agrave;nh động chưa đẹp của du kh&aacute;ch, kh&ocirc;ng n&ecirc;n v&igrave; c&aacute;i lợi trước mắt m&agrave; dễ d&atilde;i, chiều chuộng một c&aacute;ch th&aacute;i qu&aacute; những y&ecirc;u cầu của du kh&aacute;ch. Du lịch l&agrave; phải bền vững chứ kh&ocirc;ng chỉ ăn xổi ng&agrave;y một ng&agrave;y hai v&agrave;o những đợt cao điểm trong năm, h&atilde;y nghĩ xem nếu qu&ecirc; hương của bạn c&oacute; qu&aacute; nhiều tiếng xấu, chẳng du kh&aacute;ch n&agrave;o d&aacute;m quay trở lại lần hai nữa đ&acirc;u. Vấn đề n&agrave;y đ&uacute;ng khi nh&igrave;n v&agrave;o ng&agrave;nh du lịch Việt Nam những năm gần đ&acirc;y, tuy lượng du kh&aacute;ch quốc tế c&oacute; tăng nhưng gần như l&agrave; kh&aacute;ch đi lần đầu, kh&aacute;ch du lịch quay lại Việt Nam lần 2, lần 3 l&agrave; kh&aacute; hiếm hoi.</p>\r\n<p style=\"text-align: justify;\">Vậy đ&oacute; l&agrave;m du lịch tuy rất kh&oacute; m&agrave; cũng kh&ocirc;ng kh&oacute; nếu mỗi người tự dẹp bớt c&aacute;i t&ocirc;i của m&igrave;nh một ch&uacute;t, nghĩ cho lợi &iacute;ch cộng đồng một ch&uacute;t v&agrave; đương nhi&ecirc;n cần c&oacute; b&agrave;n tay sắt của ch&iacute;nh quyền, nhưng b&agrave;n tay sắt ấy phải bọc nhung, cương quyết nhưng cũng rất mềm dẻo để biến chuyển c&aacute;c biện ph&aacute;p nhằm tăng cường &yacute; thức đi du lịch của du kh&aacute;ch. Mong lắm chuyện n&agrave;y sẽ th&agrave;nh hiện thực trong tương lại gần.</p>', '6014426465d091bb9a543e6.38281502.png', '2019-06-19', 0);
INSERT INTO `tintuc` (`tintuc_id`, `danhmuc_id`, `tintuc_ten`, `tintuc_mota`, `tintuc_noidung`, `tintuc_anh`, `tintuc_ngaytao`, `tintuc_hot`) VALUES
(58, 24, 'Những đặc sản không thể bỏ qua khi đi du lịch Quảng Ninh', '<h5 class=\"article-summary\" style=\"text-align: justify;\">Đến Quảng Ninh, kh&ocirc;ng chỉ đắm m&igrave;nh trong cảnh sắc thi&ecirc;n nhi&ecirc;n của kỳ quan Hạ Long, đất thi&ecirc;ng Y&ecirc;n Tử, bạn c&ograve;n c&oacute; thể thưởng thức v&ocirc; số những đặc sản lừng danh nơi đ&acirc;y.</h5>', '<p style=\"text-align: justify;\">&ugrave;i. M&oacute;n chả mực cũng rất ngon khi&nbsp;d&ugrave;ng với x&ocirc;i trắng, hạt x&ocirc;i kh&ocirc; nhưng mềm, thơm hương nếp mới quyện với m&ugrave;i chả vừa b&eacute;o vừa ngọt. Ngo&agrave;i ra mực c&ograve;n c&oacute; thể ăn k&egrave;m với cơm, với b&aacute;nh cuốn cũng rất hấp dẫn.</p>\r\n<p style=\"text-align: justify;\"><strong>2. Con Ng&aacute;n</strong></p>\r\n<p style=\"text-align: justify;\">Gọi l&agrave; ng&aacute;n nhưng ăn một lần chắc chắn kh&ocirc;ng hề ng&aacute;n, m&agrave; c&ograve;n nghiện. Đ&oacute; ch&iacute;nh l&agrave; điều th&uacute; vị về lo&agrave;i hải sản biển ở Quảng Ninh l&agrave; con ng&aacute;n.</p>\r\n<p style=\"text-align: justify;\"><em><img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-3.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"354\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>M&oacute;n ng&aacute;n nướng thơm lừng.</em></p>\r\n<p style=\"text-align: justify;\">Ng&aacute;n l&agrave; loại nhuyễn thể gần giống như ngao (ngh&ecirc;u), nhưng c&oacute; vị ngọt hấp dẫn đặc biệt. C&aacute;c m&oacute;n từ ng&aacute;n kh&aacute; phổ biến tại c&aacute;c nh&agrave; h&agrave;ng, qu&aacute;n ăn tại Hạ Long. Ng&aacute;n c&oacute; thể chế biến được nhiều m&oacute;n như: Nướng, hấp, nấu ch&aacute;o, x&agrave;o với m&igrave; hay rau cải. N&oacute;i chung gọi l&agrave; ng&aacute;n nhưng ko ng&aacute;n ch&uacute;t n&agrave;o. Với c&aacute;c du kh&aacute;ch nam thường kho&aacute;i khẩu m&oacute;n rượu ng&aacute;n. Rượu ng&aacute;n c&oacute; m&ugrave;i thơm rất ri&ecirc;ng của biển.</p>\r\n<p style=\"text-align: justify;\"><em><img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-4.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"483\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>Gọi l&agrave; Ng&aacute;n nhưng ăn một lần chắc chắn kh&ocirc;ng hề ng&aacute;n, m&agrave; c&ograve;n nghiện.</em></p>\r\n<p style=\"text-align: justify;\">Nếu c&oacute; dịp du lịch tại Hạ Long bạn kh&ocirc;ng n&ecirc;n bỏ qua cơ hội thưởng thức c&aacute;c m&oacute;n ngon từ con ng&aacute;n, một trong những hải sản thơm ngon tại v&ugrave;ng biển Hạ Long.</p>\r\n<p style=\"text-align: justify;\"><strong>3. S&aacute; S&ugrave;ng</strong></p>\r\n<p style=\"text-align: justify;\">S&aacute; s&ugrave;ng, hay c&ograve;n gọi l&agrave; s&aacute;i s&ugrave;ng, l&agrave; đặc sản của v&ugrave;ng biển Minh Ch&acirc;u, Quan Lạn (V&acirc;n Đồn, Quảng Ninh). Nh&igrave;n tho&aacute;ng qua s&aacute; s&ugrave;ng tr&ocirc;ng rất xấu x&iacute;, giống như những con giun, s&acirc;u, c&oacute; m&agrave;u hồng hồng, nhưng đ&acirc;y lại l&agrave; nguy&ecirc;n liệu đắt tiền cho những m&oacute;n ăn ngon miệng v&agrave; bổ dưỡng.</p>\r\n<p style=\"text-align: justify;\"><em><img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-5.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"451\" /></em></p>\r\n<p style=\"text-align: justify;\"><em>S&aacute; s&ugrave;ng tươi x&agrave;o tỏi</em></p>\r\n<p style=\"text-align: justify;\">S&aacute; s&ugrave;ng c&oacute; thể chế biến khi c&ograve;n tươi sống hay phơi kh&ocirc; đều rất hấp dẫn. S&aacute; s&ugrave;ng tươi x&agrave;o với tỏi tươi l&agrave; một m&oacute;n ăn d&acirc;n d&atilde; đặc sắc của người d&acirc;n v&ugrave;ng biển Hạ Long.&nbsp;S&aacute; s&ugrave;ng kh&ocirc; đem rang, khi ch&iacute;n c&oacute; m&agrave;u v&agrave;ng, m&ugrave;i rất thơm, một hương thơm nồng ngậy đậm đ&agrave; của biển. S&aacute; s&ugrave;ng rang chấm với tương ớt, điểm th&ecirc;m rau diếp c&aacute;, rau thơm v&agrave; uống với bia th&igrave; thật l&agrave; tuyệt&hellip;</p>\r\n<p style=\"text-align: justify;\"><img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-6.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"550\" /></p>\r\n<p style=\"text-align: justify;\"><em>S&aacute; s&ugrave;ng kh&ocirc; rang cay.</em></p>\r\n<p style=\"text-align: justify;\"><strong>4. Sam biển</strong></p>\r\n<p style=\"text-align: justify;\">Một lo&agrave;i đặc sản kh&aacute;c v&ocirc; c&ugrave;ng hấp dẫn của biển Hạ Long, đ&oacute; l&agrave; sam &ndash; một lo&agrave;i gi&aacute;p x&aacute;c ch&acirc;n đốt. Từ sam biển người ta c&oacute; thể chế biến ra được rất nhiều m&oacute;n ăn ngon v&agrave; lạ kh&aacute;c nhau như: Tiết canh sam, gỏi sam, ch&acirc;n sam x&agrave;o chua ngọt, sam x&agrave;o xả ớt, trứng sam chi&ecirc;n gi&ograve;n, trứng sam x&agrave;o l&aacute; lốt, sam hấp, sam bao bột r&aacute;n, sụn sam nướng, sam x&agrave;o miến&hellip;</p>\r\n<p style=\"text-align: justify;\"><img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-7.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" /></p>\r\n<p style=\"text-align: justify;\"><em>Sam biển &ndash; Một lo&agrave;i đặc sản kh&aacute;c v&ocirc; c&ugrave;ng hấp dẫn của biển Hạ Long</em></p>\r\n<p style=\"text-align: justify;\">C&aacute;c m&oacute;n ăn từ thịt sam biển rất thơm ngon, du kh&aacute;ch đến Quảng Ninh kh&ocirc;ng n&ecirc;n bỏ lỡ đặc sản n&agrave;y.</p>\r\n<p style=\"text-align: justify;\"><strong>5. Rượu mơ Y&ecirc;n Tử</strong></p>\r\n<p style=\"text-align: justify;\">Tới Y&ecirc;n Tử, sau khi đ&atilde; thưởng ngoạn cảnh đẹp h&ugrave;ng vĩ, n&ecirc;n thơ của chốn non thi&ecirc;ng , bạn c&oacute; thể thưởng thức ch&eacute;n rượu s&oacute;ng s&aacute;nh m&agrave;u v&agrave;ng hổ ph&aacute;ch với hương vị nồng ấm, men say ng&acirc;y ngất. Rượu mơ Y&ecirc;n Tử c&oacute; vị chua, ngọt đậm đ&agrave; khiến du kh&aacute;ch c&oacute; cảm gi&aacute;c l&acirc;ng l&acirc;ng khoan kho&aacute;i.</p>\r\n<p style=\"text-align: justify;\">&nbsp;<img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-8.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"473\" /></p>\r\n<p style=\"text-align: justify;\"><strong>6. B&aacute;nh gật g&ugrave;</strong></p>\r\n<p style=\"text-align: justify;\">Từ Hạ Long, theo Quốc lộ 18 ngược bắc 70 km sẽ gặp thị trấn Ti&ecirc;n Y&ecirc;n với 3 m&oacute;n đặc sản nổi tiếng: b&aacute;nh gật g&ugrave;, g&agrave; đồi v&agrave; c&agrave; s&aacute;y.</p>\r\n<p style=\"text-align: justify;\">C&aacute;ch l&agrave;m b&aacute;nh gật g&ugrave; về h&igrave;nh thức giống như c&aacute;ch l&agrave;m b&aacute;nh cuốn. Cũng l&agrave; bột được xay từ gạo ngon, ng&acirc;m từ trước. B&iacute; quyết với b&aacute;nh gật g&ugrave; l&agrave; khi xay gạo, chủ nh&agrave; sẽ cho th&ecirc;m một &iacute;t cơm nguội để b&aacute;nh ngon hơn. Khi ăn nh&uacute;ng miếng b&aacute;nh v&agrave;o ch&eacute;n nước chấm ngon chưng mỡ g&agrave;, h&agrave;nh phi đặc biệt c&ugrave;ng với một miếng kh&acirc;u nhục (thịt kho t&agrave;u) được tẩm ướp kỹ c&agrave;ng.</p>\r\n<p style=\"text-align: justify;\"><img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-9.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" /></p>\r\n<p style=\"text-align: justify;\">Cảm gi&aacute;c m&aacute;t, mềm của b&aacute;nh, vị ngọt b&ugrave;i, thơm, ngậy của miếng kh&acirc;u nhục c&oacute; thể chiều l&ograve;ng được cả những thực kh&aacute;ch kh&oacute; t&iacute;nh. B&aacute;nh gật g&ugrave; nổi tiếng nhất l&agrave; ở Ti&ecirc;n Y&ecirc;n. Tuy nhi&ecirc;n tr&ecirc;n khắp c&aacute;c v&ugrave;ng của Quảng Ninh đều c&oacute; thể t&igrave;m được m&oacute;n b&aacute;nh gật g&ugrave; vừa ngon vừa độc đ&aacute;o n&agrave;y.</p>\r\n<p style=\"text-align: justify;\">&nbsp;<img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-10.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"449\" /></p>\r\n<p style=\"text-align: justify;\">C&aacute;i t&ecirc;n ngộ nghĩnh của b&aacute;nh được giải th&iacute;ch l&agrave; v&igrave; khi cầm ăn, b&aacute;nh cứ gật l&ecirc;n, gật xuống giống như đang gật đầu, cũng c&oacute; nơi cho rằng v&igrave; sau khi ăn b&aacute;nh th&igrave; ai cũng gật g&ugrave; khen ngon.</p>\r\n<p style=\"text-align: justify;\"><strong>7. G&agrave; đồi, c&agrave; s&aacute;y Ti&ecirc;n Y&ecirc;n</strong></p>\r\n<p style=\"text-align: justify;\">Ti&ecirc;n Y&ecirc;n c&ograve;n nổi danh với m&oacute;n g&agrave; đồi. G&agrave; được nu&ocirc;i thả rong tr&ecirc;n c&aacute;c triền đồi để tự t&uacute;c kiếm ăn từ c&aacute;c loại tr&ugrave;ng, dế, kiến, mối. Nhờ những thứ thực phẩm thi&ecirc;n nhi&ecirc;n độc đ&aacute;o ấy m&agrave; thịt g&agrave; Ti&ecirc;n Y&ecirc;n ngọt thơm một c&aacute;ch đặc biệt, săn chắc m&agrave; vẫn gi&ograve;n, kh&ocirc;ng dai, b&eacute;o m&agrave; kh&ocirc;ng ngậy.</p>\r\n<p style=\"text-align: justify;\">&nbsp;<img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-11.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"420\" /></p>\r\n<p style=\"text-align: justify;\">C&ograve;n c&agrave; s&aacute;y l&agrave; vịt lai ngan, được chế biến theo b&iacute; quyết ri&ecirc;ng n&ecirc;n c&oacute; hương vị ngon tuyệt vời. C&agrave; s&aacute;y l&agrave; vịt lai ngan, &iacute;t đ&acirc;u chế biến c&agrave; s&aacute;y c&oacute; hương vị tuyệt vời như người Ti&ecirc;n Y&ecirc;n. Kh&ocirc;ng r&otilde; họ l&agrave;m kiểu g&igrave; nhưng ăn rồi chỉ c&oacute; tấm tắc khen ngon m&agrave; th&ocirc;i. Từ thịt c&agrave; s&aacute;y cho đến nước chấm đặc biệt đi k&egrave;m đều mang vị thơm ngon kh&oacute; diễn tả. Thịt ngon, thơm, nước chấm vừa c&oacute; c&aacute;i đậm đ&agrave; biển cả, vừa ngọt ng&agrave;o nồng ngậy x&aacute; xị v&agrave; cay cay n&oacute;ng n&oacute;ng của gừng. Du kh&aacute;ch ăn xong m&agrave; c&ograve;n vương vấn m&atilde;i.</p>\r\n<p style=\"text-align: justify;\"><strong>8. Nem chua, nem chạo Quảng Y&ecirc;n</strong></p>\r\n<p style=\"text-align: justify;\">Những h&agrave;ng nem chua, nem chạo l&agrave; niềm tự h&agrave;o của người d&acirc;n thị trấn Quảng Y&ecirc;n, Quảng Ninh. Nguy&ecirc;n liệu l&agrave;m nem đều l&agrave; những thứ b&igrave;nh d&acirc;n: b&igrave; lợn được b&agrave;o nhỏ, trắng như cước, th&iacute;nh l&agrave;m bằng đỗ hay gạo rang v&agrave; lạc rang gi&atilde; dập, tất cả được trộn đều với nhau thật tơi. Chỉ đơn giản thế, nhưng qua b&iacute; quyết chế biến ri&ecirc;ng, h&agrave;ng qu&agrave; qu&ecirc; trở n&ecirc;n lạ miệng, hấp dẫn kh&oacute; qu&ecirc;n.</p>\r\n<p style=\"text-align: justify;\">&nbsp;<img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-12.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"630\" /></p>\r\n<p style=\"text-align: justify;\"><strong>9. Con h&agrave;</strong></p>\r\n<p style=\"text-align: justify;\">Một đặc sản kh&aacute;c m&agrave; người Quảng Y&ecirc;n rất tự h&agrave;o, đ&oacute; l&agrave; con h&agrave;. H&agrave; Quảng Y&ecirc;n, đặc biệt l&agrave; giống h&agrave; cồn sống ở s&ocirc;ng Chanh đem nấu canh chua hay tẩm bột r&aacute;n ăn rất ngon v&agrave; c&oacute; cả bốn m&ugrave;a.&nbsp;Đến Quảng Ninh m&ugrave;a h&egrave; m&agrave; được thưởng thức m&oacute;n m&aacute;t l&agrave;nh v&agrave; thanh đạm n&agrave;y th&igrave; c&ograve;n thoải m&aacute;i hơn nhiều sơn h&agrave;o hải vị kh&aacute;c.</p>\r\n<p style=\"text-align: justify;\">&nbsp;<img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-14.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"365\" /></p>\r\n<p style=\"text-align: justify;\"><strong>10. B&aacute;nh cuốn chả mực</strong></p>\r\n<p style=\"text-align: justify;\">Cũng l&agrave; những chiếc b&aacute;nh cuốn thịt tr&aacute;ng mỏng tang, nh&igrave;n r&otilde; miếng thịt bằm, nấm, mộc nhĩ b&ecirc;n trong, nghi ng&uacute;t kh&oacute;i bay hương thơm của ruốc, h&agrave;nh phi. Người ăn gắp một miếng b&aacute;nh cuốn, k&egrave;m ch&uacute;t rau m&ugrave;i, một miếng chả mực vừa chi&ecirc;n cũng đang n&oacute;ng hổi v&agrave;ng ruộm, tất cả nh&uacute;ng v&agrave;o b&aacute;t nước chấm m&agrave;u hổ ph&aacute;ch s&oacute;ng s&aacute;nh khoanh ớt đỏ. Sự kết hợp tuyệt vời của b&aacute;nh cuốn đ&atilde; quen thuộc với chả mực Hạ Long l&agrave;m n&ecirc;n một m&oacute;n ăn hương vị biển rất hấp dẫn.</p>\r\n<p style=\"text-align: justify;\"><img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-15.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"465\" /></p>\r\n<p style=\"text-align: justify;\">Du kh&aacute;ch c&oacute; thể nếm b&aacute;nh cuốn chả mực tại qu&aacute;n cạnh rạp Bạch Đằng, th&agrave;nh phố Hạ Long, Quảng Ninh, giờ phục vụ từ s&aacute;ng đến trưa; hay b&aacute;nh cuốn chả mực b&agrave; Ng&acirc;n phố C&acirc;y Th&aacute;p, Hồng Gai, th&agrave;nh phố Hạ Long.</p>\r\n<p style=\"text-align: justify;\"><strong>11. Con ruốc</strong></p>\r\n<p style=\"text-align: justify;\">&nbsp;<img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-16.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"460\" /></p>\r\n<p style=\"text-align: justify;\">B&ecirc;n cạnh chả mực Hạ Long, ng&aacute;n biển&hellip;du kh&aacute;ch tới đ&acirc;y cũng kh&ocirc;ng mấy người bỏ qua ruốc lỗ. D&acirc;n s&agrave;nh ăn thường nhậu ruốc c&ugrave;ng rượu ng&aacute;n.</p>\r\n<p style=\"text-align: justify;\"><strong>12.&nbsp;B&aacute;nh t&agrave;i lồng ệp</strong></p>\r\n<p style=\"text-align: justify;\">&nbsp;<img src=\"http://kenhdulich.org/container/2015/content/articles/t-11/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh/nhung-dac-san-khong-the-bo-qua-khi-du-lich-quang-ninh-17.jpg\" alt=\"Những đặc sản kh&ocirc;ng thể bỏ qua khi du lịch Quảng Ninh\" width=\"630\" height=\"378\" /></p>\r\n<p style=\"text-align: justify;\">B&aacute;nh t&agrave;i lồng ệp vừa l&agrave; thức qu&agrave; ăn chơi vừa được d&ugrave;ng để l&agrave;m m&oacute;n đồ c&uacute;ng ng&agrave;y lễ tết. Đ&acirc;y l&agrave; một m&oacute;n ăn được xem như đặc sản của người S&aacute;n D&igrave;u ở Quảng Ninh.</p>', '21234789295d091c7ce66b83.69768002.jpg', '2019-06-19', 0);

